import React from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { 
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Typography,
  Grid,
  IconButton,
  CircularProgress,
  Switch,
  FormControlLabel,
  Paper,
  Autocomplete,
  Box
} from '@mui/material';
import { Add, Remove } from '@mui/icons-material';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import api from '../lib/api';
import { getProducts } from '../services/masterService';
interface BOMComponent {
  component_item_id: number;
  quantity_required: number;
  unit: string;
  unit_cost: number;
  wastage_percentage: number;
  is_optional: boolean;
  sequence: number;
  notes?: string;
}
interface BOM {
  id?: number;
  bom_name: string;
  output_item_id: number;
  output_quantity: number;
  version: string;
  validity_start?: string;
  validity_end?: string;
  description?: string;
  notes?: string;
  material_cost: number;
  labor_cost: number;
  overhead_cost: number;
  is_active: boolean;
  components: BOMComponent[];
}
const defaultBOM: BOM = {
  bom_name: '',
  output_item_id: 0,
  output_quantity: 1.0,
  version: '1.0',
  description: '',
  notes: '',
  material_cost: 0.0,
  labor_cost: 0.0,
  overhead_cost: 0.0,
  is_active: true,
  components: [
    {
      component_item_id: 0,
      quantity_required: 1.0,
      unit: 'PCS',
      unit_cost: 0.0,
      wastage_percentage: 0.0,
      is_optional: false,
      sequence: 1,
      notes: ''
    }
  ]
};
interface AddBOMModalProps {
  open: boolean;
  onClose: () => void;
  onAdd: (data: BOM) => void;
  initialData?: BOM;
  mode: 'create' | 'edit';
}
const AddBOMModal: React.FC<AddBOMModalProps> = ({ open, onClose, onAdd, initialData, mode }) => {
  const queryClient = useQueryClient();
  const { control, handleSubmit, reset, setValue, watch, formState: { errors } } = useForm<BOM>({
    defaultValues: initialData || defaultBOM
  });
  const { fields, append, remove } = useFieldArray({
    control,
    name: 'components'
  });
  // Fetch products
  const { data: productList, isLoading: isLoadingProducts } = useQuery({
    queryKey: ['products'],
    queryFn: getProducts
  });
  const productOptions = productList || [];
  // Mutation for create/edit
  const mutation = useMutation({
    mutationFn: (bomData: BOM) => {
      // Clean the data
      const cleanData = {
        ...bomData,
        output_item_id: bomData.output_item_id || null,
        output_quantity: Number(bomData.output_quantity) || 1.0,
        labor_cost: Number(bomData.labor_cost) || 0.0,
        overhead_cost: Number(bomData.overhead_cost) || 0.0,
        material_cost: Number(bomData.material_cost) || 0.0,
        components: bomData.components.map(comp => ({
          ...comp,
          component_item_id: Number(comp.component_item_id) || null,
          quantity_required: Number(comp.quantity_required) || 1.0,
          unit_cost: Number(comp.unit_cost) || 0.0,
          wastage_percentage: Number(comp.wastage_percentage) || 0.0,
          sequence: Number(comp.sequence) || 0,
          is_optional: Boolean(comp.is_optional)
        })).filter(comp => comp.component_item_id)
      };
      if (mode === 'create') {
        return api.post('/bom', cleanData).then(res => res.data);
      } else {
        return api.put(`/bom/${initialData?.id}`, cleanData).then(res => res.data);
      }
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['boms'] });
      onAdd(data);
      onClose();
      reset(defaultBOM);
    },
    onError: (error: any) => {
      console.error(`Error ${mode === 'create' ? 'creating' : 'updating'} BOM:`, error);
    }
  });
  const onSubmit = (data: BOM) => {
    // Validations
    if (!data.bom_name?.trim()) {return;}
    if (!data.output_item_id || data.output_item_id === 0) {return;}
    if (!data.components || data.components.length === 0) {return;}
    const invalidComponents = data.components.filter(comp => 
      !comp.component_item_id || comp.quantity_required <= 0
    );
    if (invalidComponents.length > 0) {return;}
    mutation.mutate(data);
  };
  const addComponent = () => {
    append({
      component_item_id: 0,
      quantity_required: 1.0,
      unit: 'PCS',
      unit_cost: 0.0,
      wastage_percentage: 0.0,
      is_optional: false,
      sequence: fields.length + 1,
      notes: ''
    });
  };
  const removeComponent = (index: number) => {
    if (fields.length > 1) {
      remove(index);
    }
  };
  const calculateTotalCost = () => {
    const components = watch('components') || [];
    const materialCost = components.reduce((sum, comp) => {
      const totalQty = comp.quantity_required * (1 + comp.wastage_percentage / 100);
      return sum + (totalQty * comp.unit_cost);
    }, 0);
    const laborCost = watch('labor_cost') || 0;
    const overheadCost = watch('overhead_cost') || 0;
    return materialCost + laborCost + overheadCost;
  };
  if (isLoadingProducts) {
    return <CircularProgress />;
  }
  return (
    <Dialog open={open} onClose={onClose} maxWidth="lg" fullWidth>
      <DialogTitle>{mode === 'create' ? 'Create BOM' : 'Edit BOM'}</DialogTitle>
      <form onSubmit={handleSubmit(onSubmit)}>
        <DialogContent>
          <Grid container spacing={3}>
            {/* Basic Information */}
            <Grid size={12}>
              <Typography variant="h6" gutterBottom>Basic Information</Typography>
            </Grid>
            <Grid size={6}>
              <TextField
                {...control.register('bom_name', { required: 'BOM name is required' })}
                label="BOM Name"
                fullWidth
                error={!!errors.bom_name}
                helperText={errors.bom_name?.message}
              />
            </Grid>
            <Grid size={3}>
              <TextField
                {...control.register('version', { required: 'Version is required' })}
                label="Version"
                fullWidth
                error={!!errors.version}
                helperText={errors.version?.message}
              />
            </Grid>
            <Grid size={3}>
              <TextField
                {...control.register('output_quantity', { required: 'Output quantity is required', min: 0.01 })}
                label="Output Quantity"
                type="number"
                fullWidth
                error={!!errors.output_quantity}
                helperText={errors.output_quantity?.message}
                InputProps={{ inputProps: { step: 0.01 } }}
              />
            </Grid>
            <Grid size={6}>
              <Autocomplete
                options={productOptions}
                getOptionLabel={(option) => option.product_name || ''}
                value={productOptions.find((p: any) => p.id === watch('output_item_id')) || null}
                onChange={(_, newValue) => setValue('output_item_id', newValue?.id || 0)}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Output Item *"
                    error={!!errors.output_item_id}
                    helperText={errors.output_item_id?.message || 'Select the product that will be manufactured'}
                    required
                  />
                )}
              />
            </Grid>
            <Grid size={6}>
              <FormControlLabel
                control={
                  <Switch
                    checked={watch('is_active')}
                    onChange={(e) => setValue('is_active', e.target.checked)}
                  />
                }
                label="Active"
              />
            </Grid>
            <Grid size={6}>
              <TextField
                {...control.register('validity_start')}
                label="Validity Start"
                type="date"
                fullWidth
                InputLabelProps={{ shrink: true }}
              />
            </Grid>
            <Grid size={6}>
              <TextField
                {...control.register('validity_end')}
                label="Validity End"
                type="date"
                fullWidth
                InputLabelProps={{ shrink: true }}
              />
            </Grid>
            <Grid size={12}>
              <TextField
                {...control.register('description')}
                label="Description"
                fullWidth
                multiline
                rows={2}
              />
            </Grid>
            <Grid size={12}>
              <TextField
                {...control.register('notes')}
                label="Notes"
                fullWidth
                multiline
                rows={2}
              />
            </Grid>
            {/* Components */}
            <Grid size={12}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: 3, mb: 2 }}>
                <Typography variant="h6">Components</Typography>
                <Button
                  variant="outlined"
                  startIcon={<Add />}
                  onClick={addComponent}
                >
                  Add Component
                </Button>
              </Box>
            </Grid>
            {fields.map((field, index) => (
              <Grid size={12} key={field.id}>
                <Paper sx={{ p: 2, mb: 2 }}>
                  <Grid container spacing={2}>
                    <Grid size={4}>
                      <Autocomplete
                        options={productOptions}
                        getOptionLabel={(option) => option.product_name || ''}
                        value={productOptions.find((p: any) => p.id === watch(`components.${index}.component_item_id`)) || null}
                        onChange={(_, newValue) => setValue(`components.${index}.component_item_id`, newValue?.id || 0)}
                        renderInput={(params) => (
                          <TextField
                            {...params}
                            label="Component Item *"
                            size="small"
                            required
                            error={!watch(`components.${index}.component_item_id`)}
                            helperText={!watch(`components.${index}.component_item_id`) ? 'Component item is required' : ''}
                          />
                        )}
                      />
                    </Grid>
                    <Grid size={2}>
                      <TextField
                        {...control.register(`components.${index}.quantity_required` as const, { 
                          required: 'Quantity is required', 
                          min: { value: 0.01, message: 'Quantity must be greater than 0' }
                        })}
                        label="Quantity *"
                        type="number"
                        fullWidth
                        size="small"
                        error={!!errors.components?.[index]?.quantity_required}
                        helperText={errors.components?.[index]?.quantity_required?.message}
                        InputProps={{ inputProps: { step: 0.01, min: 0.01 } }}
                      />
                    </Grid>
                    <Grid size={1}>
                      <TextField
                        {...control.register(`components.${index}.unit` as const)}
                        label="Unit"
                        fullWidth
                        size="small"
                      />
                    </Grid>
                    <Grid size={2}>
                      <TextField
                        {...control.register(`components.${index}.unit_cost` as const, { min: 0 })}
                        label="Unit Cost"
                        type="number"
                        fullWidth
                        size="small"
                        InputProps={{ inputProps: { step: 0.01 } }}
                      />
                    </Grid>
                    <Grid size={2}>
                      <TextField
                        {...control.register(`components.${index}.wastage_percentage` as const, { min: 0, max: 100 })}
                        label="Wastage %"
                        type="number"
                        fullWidth
                        size="small"
                        InputProps={{ inputProps: { step: 0.1 } }}
                      />
                    </Grid>
                    <Grid size={1}>
                      <IconButton
                        onClick={() => removeComponent(index)}
                        color="error"
                        disabled={fields.length === 1}
                      >
                        <Remove />
                      </IconButton>
                    </Grid>
                    <Grid size={12}>
                      <TextField
                        {...control.register(`components.${index}.notes` as const)}
                        label="Component Notes"
                        fullWidth
                        size="small"
                      />
                    </Grid>
                  </Grid>
                </Paper>
              </Grid>
            ))}
            {/* Costing */}
            <Grid size={12}>
              <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>Costing</Typography>
            </Grid>
            <Grid size={4}>
              <TextField
                {...control.register('labor_cost', { min: 0 })}
                label="Labor Cost"
                type="number"
                fullWidth
                InputProps={{ inputProps: { step: 0.01 } }}
              />
            </Grid>
            <Grid size={4}>
              <TextField
                {...control.register('overhead_cost', { min: 0 })}
                label="Overhead Cost"
                type="number"
                fullWidth
                InputProps={{ inputProps: { step: 0.01 } }}
              />
            </Grid>
            <Grid size={4}>
              <TextField
                label="Total Cost"
                value={calculateTotalCost().toFixed(2)}
                fullWidth
                disabled
                InputProps={{
                  readOnly: true,
                }}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose} disabled={mutation.isPending}>
            Cancel
          </Button>
          <Button 
            type="submit" 
            variant="contained" 
            disabled={mutation.isPending}
          >
            {mutation.isPending ? <CircularProgress size={20} /> : (mode === 'create' ? 'Create' : 'Update')}
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
};
export default AddBOMModal;

import React from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Typography,
  CircularProgress,
  Box,
  Grid,
  InputAdornment
} from '@mui/material';
import { useForm, Controller } from 'react-hook-form';
interface AddCommissionModalProps {
  open: boolean;
  onClose: () => void;
  onAdd: (data: any) => Promise<void>;
  loading?: boolean;
}
interface CommissionFormData {
  sales_person_id: number;
  opportunity_id?: number;
  lead_id?: number;
  commission_type: string;
  commission_rate?: number;
  commission_amount?: number;
  base_amount: number;
  commission_date: string;
  payment_status: string;
  notes?: string;
}
const commissionTypes = [
  'percentage',
  'fixed_amount',
  'tiered',
  'bonus'
];
const paymentStatuses = [
  'pending',
  'paid',
  'approved',
  'rejected',
  'on_hold'
];
const AddCommissionModal: React.FC<AddCommissionModalProps> = ({
  open,
  onClose,
  onAdd,
  loading = false
}) => {
  const { register, handleSubmit, reset, control, watch, formState: { errors } } = useForm<CommissionFormData>({
    defaultValues: {
      sales_person_id: 0,
      opportunity_id: undefined,
      lead_id: undefined,
      commission_type: 'percentage',
      commission_rate: 0,
      commission_amount: 0,
      base_amount: 0,
      commission_date: new Date().toISOString().split('T')[0],
      payment_status: 'pending',
      notes: '',
    }
  });
  const commissionType = watch('commission_type');
  const baseAmount = watch('base_amount');
  const commissionRate = watch('commission_rate');
  React.useEffect(() => {
    if (open) {
      reset({
        sales_person_id: 0,
        opportunity_id: undefined,
        lead_id: undefined,
        commission_type: 'percentage',
        commission_rate: 0,
        commission_amount: 0,
        base_amount: 0,
        commission_date: new Date().toISOString().split('T')[0],
        payment_status: 'pending',
        notes: ''
      });
    }
  }, [open, reset]);
  // Calculate commission amount automatically for percentage type
  React.useEffect(() => {
    if (commissionType === 'percentage' && baseAmount && commissionRate) {
      const calculated = (baseAmount * commissionRate) / 100;
      console.log(calculated); // Use the expression to fix no-unused-expressions
    }
  }, [commissionType, baseAmount, commissionRate]);
  const onSubmit = async (commissionData: CommissionFormData) => {
    try {
      // Clean and prepare data
      const cleanData = {
        ...commissionData,
        // Ensure numeric fields are properly typed
        sales_person_id: Number(commissionData.sales_person_id),
        opportunity_id: commissionData.opportunity_id ? Number(commissionData.opportunity_id) : null,
        lead_id: commissionData.lead_id ? Number(commissionData.lead_id) : null,
        commission_rate: commissionData.commission_rate ? Number(commissionData.commission_rate) : null,
        commission_amount: commissionData.commission_amount ? Number(commissionData.commission_amount) : null,
        base_amount: Number(commissionData.base_amount)
      };
      // Remove undefined/empty fields
      Object.keys(cleanData).forEach(key => {
        if (cleanData[key as keyof typeof cleanData] === undefined || cleanData[key as keyof typeof cleanData] === '') {
          delete cleanData[key as keyof typeof cleanData];
        }
      });
      await onAdd(cleanData);
      reset();
      onClose();
    } catch (error) {
      console.error('Error adding commission:', error);
    }
  };
  const handleClose = () => {
    if (!loading) {
      reset();
      onClose();
    }
  };
  return (
    <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
      <DialogTitle>
        <Typography variant="h6" component="div">
          Add New Commission Record
        </Typography>
      </DialogTitle>
      <form onSubmit={handleSubmit(onSubmit)}>
        <DialogContent>
          <Box sx={{ mt: 1 }}>
            <Grid container spacing={3}>
              {/* Basic Information */}
              <Grid size={{ xs: 12 }}>
                <Typography variant="h6" color="primary" sx={{ mb: 2 }}>
                  Commission Details
                </Typography>
              </Grid>
              <Grid size={{ xs: 12, sm: 6 }}>
                <TextField
                  {...register('sales_person_id', { 
                    required: 'Sales person ID is required',
                    min: { value: 1, message: 'Please enter a valid sales person ID' }
                  })}
                  label="Sales Person ID"
                  type="number"
                  fullWidth
                  error={!!errors.sales_person_id}
                  helperText={errors.sales_person_id?.message}
                  disabled={loading}
                  inputProps={{ min: 1 }}
                />
              </Grid>
              <Grid size={{ xs: 12, sm: 6 }}>
                <FormControl fullWidth disabled={loading}>
                  <InputLabel>Commission Type</InputLabel>
                  <Controller
                    name="commission_type"
                    control={control}
                    rules={{ required: 'Commission type is required' }}
                    render={({ field }) => (
                      <Select
                        {...field}
                        label="Commission Type"
                        error={!!errors.commission_type}
                      >
                        {commissionTypes.map((type) => (
                          <MenuItem key={type} value={type}>
                            {type.replace('_', ' ').toUpperCase()}
                          </MenuItem>
                        ))}
                      </Select>
                    )}
                  />
                  {errors.commission_type && (
                    <Typography variant="caption" color="error" sx={{ mt: 0.5, ml: 2 }}>
                      {errors.commission_type.message}
                    </Typography>
                  )}
                </FormControl>
              </Grid>
              <Grid size={{ xs: 12, sm: 6 }}>
                <TextField
                  {...register('base_amount', { 
                    required: 'Base amount is required',
                    min: { value: 0, message: 'Base amount must be positive' }
                  })}
                  label="Base Amount"
                  type="number"
                  fullWidth
                  error={!!errors.base_amount}
                  helperText={errors.base_amount?.message}
                  disabled={loading}
                  InputProps={{
                    startAdornment: <InputAdornment position="start">$</InputAdornment>,
                  }}
                  inputProps={{ min: 0, step: 0.01 }}
                />
              </Grid>
              {commissionType === 'percentage' && (
                <Grid size={{ xs: 12, sm: 6 }}>
                  <TextField
                    {...register('commission_rate', {
                      min: { value: 0, message: 'Rate must be positive' },
                      max: { value: 100, message: 'Rate cannot exceed 100%' }
                    })}
                    label="Commission Rate"
                    type="number"
                    fullWidth
                    error={!!errors.commission_rate}
                    helperText={errors.commission_rate?.message}
                    disabled={loading}
                    InputProps={{
                      endAdornment: <InputAdornment position="end">%</InputAdornment>,
                    }}
                    inputProps={{ min: 0, max: 100, step: 0.1 }}
                  />
                </Grid>
              )}
              {(commissionType === 'fixed_amount' || commissionType === 'bonus') && (
                <Grid size={{ xs: 12, sm: 6 }}>
                  <TextField
                    {...register('commission_amount', {
                      min: { value: 0, message: 'Amount must be positive' }
                    })}
                    label="Commission Amount"
                    type="number"
                    fullWidth
                    error={!!errors.commission_amount}
                    helperText={errors.commission_amount?.message}
                    disabled={loading}
                    InputProps={{
                      startAdornment: <InputAdornment position="start">$</InputAdornment>,
                    }}
                    inputProps={{ min: 0, step: 0.01 }}
                  />
                </Grid>
              )}
              <Grid size={{ xs: 12, sm: 6 }}>
                <TextField
                  {...register('commission_date', { 
                    required: 'Commission date is required' 
                  })}
                  label="Commission Date"
                  type="date"
                  fullWidth
                  error={!!errors.commission_date}
                  helperText={errors.commission_date?.message}
                  disabled={loading}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </Grid>
              <Grid size={{ xs: 12, sm: 6 }}>
                <FormControl fullWidth disabled={loading}>
                  <InputLabel>Payment Status</InputLabel>
                  <Controller
                    name="payment_status"
                    control={control}
                    rules={{ required: 'Payment status is required' }}
                    render={({ field }) => (
                      <Select
                        {...field}
                        label="Payment Status"
                        error={!!errors.payment_status}
                      >
                        {paymentStatuses.map((status) => (
                          <MenuItem key={status} value={status}>
                            {status.replace('_', ' ').toUpperCase()}
                          </MenuItem>
                        ))}
                      </Select>
                    )}
                  />
                  {errors.payment_status && (
                    <Typography variant="caption" color="error" sx={{ mt: 0.5, ml: 2 }}>
                      {errors.payment_status.message}
                    </Typography>
                  )}
                </FormControl>
              </Grid>
              {/* Reference Fields */}
              <Grid size={{ xs: 12 }}>
                <Typography variant="h6" color="primary" sx={{ mb: 2, mt: 2 }}>
                  Reference Information (Optional)
                </Typography>
              </Grid>
              <Grid size={{ xs: 12, sm: 6 }}>
                <TextField
                  {...register('opportunity_id')}
                  label="Opportunity ID"
                  type="number"
                  fullWidth
                  disabled={loading}
                  inputProps={{ min: 1 }}
                />
              </Grid>
              <Grid size={{ xs: 12, sm: 6 }}>
                <TextField
                  {...register('lead_id')}
                  label="Lead ID"
                  type="number"
                  fullWidth
                  disabled={loading}
                  inputProps={{ min: 1 }}
                />
              </Grid>
              <Grid size={{ xs: 12 }}>
                <TextField
                  {...register('notes')}
                  label="Notes"
                  multiline
                  rows={3}
                  fullWidth
                  disabled={loading}
                  placeholder="Add any additional notes about this commission..."
                />
              </Grid>
              {/* Calculation Display */}
              {commissionType === 'percentage' && baseAmount && commissionRate && (
                <Grid size={{ xs: 12 }}>
                  <Box sx={{ p: 2, bgcolor: 'grey.50', borderRadius: 1 }}>
                    <Typography variant="subtitle2" gutterBottom>
                      Calculated Commission Amount
                    </Typography>
                    <Typography variant="h6" color="primary">
                      ${((baseAmount * commissionRate) / 100).toFixed(2)}
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                      {commissionRate}% of ${baseAmount}
                    </Typography>
                  </Box>
                </Grid>
              )}
            </Grid>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button 
            onClick={handleClose} 
            disabled={loading}
            color="inherit"
          >
            Cancel
          </Button>
          <Button 
            type="submit" 
            variant="contained" 
            disabled={loading}
            startIcon={loading ? <CircularProgress size={20} /> : null}
          >
            {loading ? 'Adding...' : 'Add Commission'}
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
};
export default AddCommissionModal;

import React, { useEffect, useState, useRef } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Typography,
  CircularProgress,
  Grid,
  Alert,
  InputAdornment,
  Box,
  Paper,
  Chip,
  Tooltip,
  LinearProgress,
  IconButton,
} from '@mui/material';
import { CloudUpload, Description, CheckCircle, Search } from '@mui/icons-material';
import { useForm } from 'react-hook-form';
import { usePincodeLookup } from '../hooks/usePincodeLookup';
import api from '../lib/api';
interface AddCustomerModalProps {
  open: boolean;
  onClose: () => void;
  onAdd?: (data: any) => Promise<void>;
  loading?: boolean;
  initialName?: string;
}
interface CustomerFormData {
  name: string;
  contact_number: string;
  email: string;
  address1: string;
  address2: string;
  city: string;
  state: string;
  pin_code: string;
  gst_number: string;
  pan_number: string;
  state_code: string;
}
const AddCustomerModal: React.FC<AddCustomerModalProps> = ({
  open,
  onClose,
  onAdd,
  loading = false,
  initialName = ''
}) => {
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [gstFile, setGstFile] = useState<File | null>(null);
  const [gstUploadLoading, setGstUploadLoading] = useState(false);
  const [gstExtractedData, setGstExtractedData] = useState<any>(null);
  const [gstUploadError, setGstUploadError] = useState<string | null>(null);
  const [gstSearchLoading, setGstSearchLoading] = useState(false);
  const { register, handleSubmit, reset, formState: { errors }, setValue, watch } = useForm<CustomerFormData>({
    defaultValues: {
      name: initialName,
      contact_number: '',
      email: '',
      address1: '',
      address2: '',
      city: '',
      state: '',
      pin_code: '',
      gst_number: '',
      pan_number: '',
      state_code: '',
    }
  });
  const { lookupPincode, pincodeData, loading: pincodeLoading, error: pincodeError, clearData } = usePincodeLookup();
  const watchedPincode = watch('pin_code');
  const watchedGstNumber = watch('gst_number');
  // Auto-populate form fields when pincode data is available
  useEffect(() => {
    if (pincodeData) {
      setValue('city', pincodeData.city);
      setValue('state', pincodeData.state);
      setValue('state_code', pincodeData.state_code);
    }
  }, [pincodeData, setValue]);
  // Handle pincode change with debouncing
  useEffect(() => {
    if (watchedPincode && /^\d{6}$/.test(watchedPincode)) {
      const timeoutId = setTimeout(() => {
        lookupPincode(watchedPincode);
      }, 500); // 500ms debounce
      return () => clearTimeout(timeoutId);
    } else {
      clearData();
    }
  }, [watchedPincode, lookupPincode, clearData]);
  // Handle GST certificate upload with actual API call
  const handleGstFileUpload = async (file: File) => {
    setGstUploadLoading(true);
    setGstUploadError(null);
    try {
      // Create FormData for file upload
      const formData = new FormData();
      formData.append('file', file);
      // Call backend PDF extraction API
      const response = await api.post('/pdf-extraction/extract/customer', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      if (response.data.success) {
        const extractedData = response.data.extracted_data;
        // Auto-populate form fields with processed extracted data
        Object.entries(extractedData).forEach(([key, value]) => {
          if (value) {
            setValue(key as keyof CustomerFormData, value as string);
          }
        });
        setGstExtractedData(extractedData);
        setGstFile(file);
      } else {
        const errorMessage = (response.data as any)?.detail || 'Extraction failed';
        throw new globalThis.Error(errorMessage);
      }
    } catch (error: any) {
      console.error('Error processing GST certificate:', error);
      setGstUploadError(error.response?.data?.detail || 'Failed to process GST certificate. Please try again.');
    } finally {
      setGstUploadLoading(false);
    }
  };
  const handleFileInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (file.type !== 'application/pdf') {
        setGstUploadError('Please upload a PDF file');
        return;
      }
      if (file.size > 10 * 1024 * 1024) { // 10MB limit
        setGstUploadError('File size should be less than 10MB');
        return;
      }
      handleGstFileUpload(file);
    }
  };
  const triggerFileUpload = () => {
    fileInputRef.current?.click();
  };
  const removeGstFile = () => {
    setGstFile(null);
    setGstExtractedData(null);
    setGstUploadError(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };
  const handleGstSearch = async () => {
    if (!watchedGstNumber || !/^[0-9]{2}[A-Z]{5}[0-9]{4}[A-Z]{1}[1-9A-Z]{1}Z[0-9A-Z]{1}$/.test(watchedGstNumber)) {
      setGstUploadError('Please enter a valid GSTIN');
      return;
    }
    setGstSearchLoading(true);
    setGstUploadError(null);
    try {
      const response = await api.get(`/gst/search/${watchedGstNumber}`);
      const data = response.data;
      // Auto-populate fields from API response
      Object.entries(data).forEach(([key, value]) => {
        if (value) {
          setValue(key as keyof CustomerFormData, value as string);
        }
      });
    } catch (error: any) {
      setGstUploadError(error.response?.data?.detail || 'Failed to fetch GST details. Please check GSTIN.');
    } finally {
      setGstSearchLoading(false);
    }
  };
  const onSubmit = async (customerData: CustomerFormData) => {
    try {
      // Remove empty fields to match backend schema
      const allowedFields = ['name', 'contact_number', 'email', 'address1', 'address2', 'city', 'state', 'pin_code', 'gst_number', 'pan_number', 'state_code'];
      const cleanData = Object.fromEntries(
        Object.entries(customerData).filter(([key, value]) => allowedFields.includes(key) && value !== null && String(value).trim() !== '')
      );
      // Direct API call to save customer
      const response = await api.post('/customers', cleanData);
      console.log('Customer added successfully:', response.data);
      // Call onAdd if provided and is a function
      if (typeof onAdd === 'function') {
        await onAdd(response.data);
      }
      reset();
      onClose();  // Close modal on success
    } catch (error: any) {
      console.error('Error adding customer:', error);
      // Set more specific error message
      const errorMessage = error.response?.data?.detail || 'Failed to add customer. Please try again.';
      setGstUploadError(errorMessage);
    }
  };
  const handleClose = () => {
    reset();
    clearData();
    removeGstFile();
    onClose();
  };
  return (
    <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
      <DialogTitle>
        <Typography variant="h6">Add New Customer</Typography>
      </DialogTitle>
      <form onSubmit={handleSubmit(onSubmit)}>
        <DialogContent>
          <Grid container spacing={2}>
            <Grid size={{ xs: 12, md: 6 }}>
              <TextField
                fullWidth
                label="Customer Name *"
                {...register('name', { required: 'Customer name is required' })}
                error={!!errors.name}
                helperText={errors.name?.message}
                margin="normal"
                InputLabelProps={{
                  shrink: !!watch('name') || !!gstExtractedData?.name,
                }}
              />
            </Grid>
            <Grid size={{ xs: 12, md: 6 }}>
              <TextField
                fullWidth
                label="Contact Number"
                {...register('contact_number')}
                margin="normal"
                InputLabelProps={{
                  shrink: !!watch('contact_number') || !!gstExtractedData?.phone,
                }}
              />
            </Grid>
            <Grid size={{ xs: 12, md: 6 }}>
              <TextField
                fullWidth
                label="Email"
                type="email"
                {...register('email', {
                  pattern: {
                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    message: 'Invalid email address'
                  }
                })}
                error={!!errors.email}
                helperText={errors.email?.message}
                margin="normal"
                InputLabelProps={{
                  shrink: !!watch('email') || !!gstExtractedData?.email,
                }}
              />
            </Grid>
            <Grid size={{ xs: 12, md: 6 }}>
              <TextField
                fullWidth
                label="GST Number"
                {...register('gst_number')}
                margin="normal"
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      {gstSearchLoading ? (
                        <CircularProgress size={16} />
                      ) : (
                        <IconButton
                          onClick={handleGstSearch}
                          disabled={!watchedGstNumber || gstSearchLoading}
                          aria-label="Search GST"
                        >
                          <Search />
                        </IconButton>
                      )}
                    </InputAdornment>
                  ),
                }}
                InputLabelProps={{
                  shrink: !!watch('gst_number') || !!gstExtractedData?.gst_number,
                }}
              />
            </Grid>
            {/* GST Certificate Upload Section */}
            <Grid size={12}>
              <Paper sx={{ p: 2, bgcolor: 'grey.50', border: '1px dashed', borderColor: 'grey.300' }}>
                <Box display="flex" alignItems="center" justifyContent="space-between" mb={1}>
                  <Typography variant="subtitle2" color="textSecondary">
                    GST Certificate Upload (Optional)
                  </Typography>
                  <Tooltip title="Upload GST certificate PDF to auto-fill customer details">
                    <Typography variant="caption" color="textSecondary">
                      PDF Auto-Extract
                    </Typography>
                  </Tooltip>
                </Box>
                {!gstFile && !gstUploadLoading && (
                  <Box
                    sx={{
                      border: '2px dashed',
                      borderColor: 'grey.300',
                      borderRadius: 1,
                      p: 3,
                      textAlign: 'center',
                      cursor: 'pointer',
                      '&:hover': {
                        borderColor: 'primary.main',
                        bgcolor: 'action.hover'
                      }
                    }}
                    onClick={triggerFileUpload}
                  >
                    <CloudUpload sx={{ fontSize: 48, color: 'grey.400', mb: 1 }} />
                    <Typography variant="body2" color="textSecondary" gutterBottom>
                      Click to upload GST certificate (PDF only)
                    </Typography>
                    <Typography variant="caption" color="textSecondary">
                      Maximum file size: 10MB
                    </Typography>
                  </Box>
                )}
                {gstUploadLoading && (
                  <Box sx={{ p: 3, textAlign: 'center' }}>
                    <CircularProgress size={40} sx={{ mb: 2 }} />
                    <Typography variant="body2" color="textSecondary" gutterBottom>
                      Processing GST certificate...
                    </Typography>
                    <LinearProgress sx={{ mt: 1 }} />
                  </Box>
                )}
                {gstFile && !gstUploadLoading && (
                  <Box sx={{ p: 2, bgcolor: 'success.light', borderRadius: 1 }}>
                    <Box display="flex" alignItems="center" justifyContent="space-between">
                      <Box display="flex" alignItems="center" gap={1}>
                        <Description color="primary" />
                        <Typography variant="body2" fontWeight="medium">
                          {gstFile.name}
                        </Typography>
                        <Chip
                          icon={<CheckCircle />}
                          label="Processed"
                          size="small"
                          color="success"
                          variant="outlined"
                        />
                      </Box>
                      <Button
                        size="small"
                        onClick={removeGstFile}
                        color="error"
                        variant="outlined"
                      >
                        Remove
                      </Button>
                    </Box>
                    {gstExtractedData && (
                      <Alert severity="success" sx={{ mt: 1 }}>
                        <Typography variant="caption">
                          Auto-populated: {Object.keys(gstExtractedData).join(', ')}
                        </Typography>
                      </Alert>
                    )}
                  </Box>
                )}
                {gstUploadError && (
                  <Alert severity="error" sx={{ mt: 1 }} onClose={() => setGstUploadError(null)}>
                    {gstUploadError}
                  </Alert>
                )}
                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".pdf"
                  style={{ display: 'none' }}
                  onChange={handleFileInputChange}
                />
              </Paper>
            </Grid>
            <Grid size={{ xs: 12, md: 6 }}>
              <TextField
                fullWidth
                label="PAN Number"
                {...register('pan_number')}
                margin="normal"
                InputLabelProps={{
                  shrink: !!watch('pan_number') || !!gstExtractedData?.pan_number,
                }}
              />
            </Grid>
            <Grid size={12}>
              <TextField
                fullWidth
                label="Address Line 1"
                {...register('address1')}
                margin="normal"
                InputLabelProps={{
                  shrink: !!watch('address1') || !!gstExtractedData?.address1,
                }}
              />
            </Grid>
            <Grid size={12}>
              <TextField
                fullWidth
                label="Address Line 2"
                {...register('address2')}
                margin="normal"
                InputLabelProps={{
                  shrink: !!watch('address2') || !!gstExtractedData?.address2,
                }}
              />
            </Grid>
            {/* PIN Code moved to be first after address lines */}
            <Grid size={{ xs: 12, md: 3 }}>
              <TextField
                fullWidth
                label="PIN Code *"
                {...register('pin_code', { 
                  required: 'PIN code is required',
                  pattern: {
                    value: /^\d{6}$/,
                    message: 'Please enter a valid 6-digit PIN code'
                  }
                })}
                error={!!errors.pin_code}
                helperText={errors.pin_code?.message || (pincodeError && pincodeError)}
                margin="normal"
                InputProps={{
                  endAdornment: pincodeLoading ? (
                    <InputAdornment position="end">
                      <CircularProgress size={16} />
                    </InputAdornment>
                  ) : null,
                }}
                InputLabelProps={{
                  shrink: !!watch('pin_code') || !!gstExtractedData?.pin_code,
                }}
              />
            </Grid>
            <Grid size={{ xs: 12, md: 3 }}>
              <TextField
                fullWidth
                label="City"
                {...register('city', { required: 'City is required' })}
                error={!!errors.city}
                helperText={errors.city?.message}
                margin="normal"
                InputProps={{
                  readOnly: !!pincodeData,
                }}
                InputLabelProps={{
                  shrink: !!watch('city') || !!pincodeData || !!gstExtractedData?.city,
                }}
              />
            </Grid>
            <Grid size={{ xs: 12, md: 3 }}>
              <TextField
                fullWidth
                label="State"
                {...register('state', { required: 'State is required' })}
                error={!!errors.state}
                helperText={errors.state?.message}
                margin="normal"
                InputProps={{
                  readOnly: !!pincodeData,
                }}
                InputLabelProps={{
                  shrink: !!watch('state') || !!pincodeData || !!gstExtractedData?.state,
                }}
              />
            </Grid>
            <Grid size={{ xs: 12, md: 3 }}>
              <TextField
                fullWidth
                label="State Code"
                {...register('state_code', { required: 'State code is required' })}
                error={!!errors.state_code}
                helperText={errors.state_code?.message}
                margin="normal"
                InputProps={{
                  readOnly: !!pincodeData,
                }}
                InputLabelProps={{
                  shrink: !!watch('state_code') || !!pincodeData || !!gstExtractedData?.state_code,
                }}
              />
            </Grid>
            {pincodeError && (
              <Grid size={12}>
                <Alert severity="warning" sx={{ mt: 1 }}>
                  {pincodeError}
                </Alert>
              </Grid>
            )}
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} disabled={loading}>
            Cancel
          </Button>
          <Button
            type="submit"
            variant="contained"
            disabled={loading}
            startIcon={loading ? <CircularProgress size={20} /> : null}
          >
            {loading ? 'Adding...' : 'Add Customer'}
          </Button>
        </DialogActions>
      </form>
    </Dialog>
  );
};
export default AddCustomerModal;

import React, { useState, useRef, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Typography,
  CircularProgress,
  Alert,
  Box,
  Paper,
  LinearProgress,
  IconButton,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Tabs,
  Tab,
} from '@mui/material';
import Grid from '@mui/material/Grid';
import { CloudUpload, Description, Delete as DeleteIcon } from '@mui/icons-material';
import { useForm, Controller } from 'react-hook-form';
import { usePincodeLookup } from '../hooks/usePincodeLookup';
import api from '../lib/api';
interface AddEmployeeModalProps {
  open: boolean;
  onClose: () => void;
  onAdd: (data: any) => Promise<void>;
  loading?: boolean;
  initialData?: any;
  mode: 'create' | 'edit';
}
interface EmployeeFormData {
  full_name: string;
  email: string;
  phone: string;
  date_of_birth?: string;
  gender?: string;
  employee_code: string;
  employee_type: string;
  hire_date?: string;
  job_title?: string;
  department?: string;
  work_location?: string;
  reporting_manager_id?: number;
  pan_number?: string;
  aadhaar_number?: string;
  passport_number?: string;
  driving_license?: string;
  bank_account_number?: string;
  bank_name?: string;
  ifsc_code?: string;
  bank_branch?: string;
  address_line1?: string;
  address_line2?: string;
  city?: string;
  state?: string;
  pin_code?: string;
  country?: string;
  emergency_contact_name?: string;
  emergency_contact_phone?: string;
  emergency_contact_relation?: string;
}
const AddEmployeeModal: React.FC<AddEmployeeModalProps> = ({
  open,
  onClose,
  onAdd,
  loading = false,
  initialData,
  mode,
}) => {
  const fileInputRefs = [
    useRef<HTMLInputElement>(null),
    useRef<HTMLInputElement>(null),
    useRef<HTMLInputElement>(null),
    useRef<HTMLInputElement>(null),
    useRef<HTMLInputElement>(null),
  ];
  const [documents, setDocuments] = useState<
    Array<{ file: File | null; type: string; extractedData?: any; loading: boolean; error?: string }>
  >(
    Array.from({ length: 5 }, () => ({
      file: null,
      type: '',
      extractedData: null,
      loading: false,
      error: undefined,
    }))
  );
  const [tabValue, setTabValue] = useState(0);
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
    setValue,
    watch,
    control,
  } = useForm<EmployeeFormData>({
    defaultValues: initialData || {
      full_name: '',
      email: '',
      phone: '',
      employee_code: '',
      employee_type: 'permanent',
      gender: '',
      country: 'India',
    },
  });
  const { lookupPincode, pincodeData, loading: pincodeLoading, error: pincodeError, clearData } = usePincodeLookup();
  const watchedPincode = watch('pin_code');
  useEffect(() => {
    if (pincodeData) {
      setValue('city', pincodeData.city);
      setValue('state', pincodeData.state);
    }
  }, [pincodeData, setValue]);
  useEffect(() => {
    if (watchedPincode && /^\d{6}$/.test(watchedPincode)) {
      const timeoutId = setTimeout(() => {
        lookupPincode(watchedPincode);
      }, 500);
      return () => clearTimeout(timeoutId);
    } else {
      clearData();
    }
  }, [watchedPincode, lookupPincode, clearData]);
  const handleDocumentUpload = async (index: number, file: File) => {
    const updatedDocs = [...documents];
    updatedDocs[index] = { ...updatedDocs[index], loading: true, error: undefined };
    setDocuments(updatedDocs);
    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('document_type', updatedDocs[index].type || 'general');
      const response: { data: { success: boolean; extracted_data?: any; detail?: string } } = await api.post(
        '/pdf-extraction/extract/employee',
        formData,
        {
          headers: { 'Content-Type': 'multipart/form-data' },
        }
      );
      if (response.data.success) {
        const extractedData = response.data.extracted_data;
        Object.entries(extractedData).forEach(([key, value]) => {
          if (value) {setValue(key as keyof EmployeeFormData, value as string);}
        });
        updatedDocs[index] = { ...updatedDocs[index], file, extractedData, loading: false };
      } else {
        throw new globalThis.Error(response.data.detail || 'Extraction failed');
      }
    } catch (error: any) {
      updatedDocs[index] = {
        ...updatedDocs[index],
        loading: false,
        error: error.message || 'Failed to process document',
      };
    }
    setDocuments(updatedDocs);
  };
  const handleFileChange = (index: number, event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (file.type !== 'application/pdf') {return alert('Please upload a PDF file');}
      if (file.size > 10 * 1024 * 1024) {return alert('File size should be less than 10MB');}
      handleDocumentUpload(index, file);
    }
  };
  const triggerUpload = (index: number) => {
    fileInputRefs[index].current?.click();
  };
  const removeDocument = (index: number) => {
    const updatedDocs = [...documents];
    updatedDocs[index] = { file: null, type: '', extractedData: null, loading: false, error: undefined };
    setDocuments(updatedDocs);
  };
  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setTabValue(newValue);
  };
  const onSubmit = async (employeeData: EmployeeFormData) => {
    try {
      const formData = new FormData();
      Object.entries(employeeData).forEach(([key, value]) => {
        formData.append(key, value as string);
      });
      documents.forEach((doc, index) => {
        if (doc.file) {
          formData.append(`documents_${index}`, doc.file);
          formData.append(`document_types_${index}`, doc.type);
        }
      });
      const endpoint = mode === 'create' ? '/hr/employees' : `/hr/employees/${initialData?.id}`;
      const method = mode === 'create' ? 'post' : 'put';
      const response = await api[method](endpoint, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      await onAdd(response.data);
      reset();
      onClose();
    } catch (error: any) {
      console.error('Error saving employee:', error);
      alert(error.response?.data?.detail || 'Failed to save employee');
    }
  };
  const handleClose = () => {
    reset();
    clearData();
    setDocuments(
      Array.from({ length: 5 }, () => ({
        file: null,
        type: '',
        extractedData: null,
        loading: false,
        error: undefined,
      }))
    );
    onClose();
  };
  return (
    <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
      <DialogTitle>
        <Typography variant="h6">{mode === 'create' ? 'Add New Employee' : 'Edit Employee'}</Typography>
      </DialogTitle>
      <form onSubmit={handleSubmit(onSubmit)}>
        <DialogContent>
          <Tabs value={tabValue} onChange={handleTabChange}>
            <Tab label="Personal Details" />
            <Tab label="Employment Details" />
            <Tab label="KYC & Bank" />
            <Tab label="Address & Emergency" />
          </Tabs>
          {tabValue === 0 && (
            <Grid container spacing={2} sx={{ mt: 2 }}>
              <Grid size={{ xs: 12, md: 6 }}>
                <TextField
                  fullWidth
                  label="Full Name *"
                  {...register('full_name', { required: 'Full name is required' })}
                  error={!!errors.full_name}
                  helperText={errors.full_name?.message}
                  margin="normal"
                />
              </Grid>
              <Grid size={{ xs: 12, md: 6 }}>
                <TextField
                  fullWidth
                  label="Email *"
                  type="email"
                  {...register('email', { required: 'Email is required' })}
                  error={!!errors.email}
                  helperText={errors.email?.message}
                  margin="normal"
                />
              </Grid>
              <Grid size={{ xs: 12, md: 6 }}>
                <TextField
                  fullWidth
                  label="Phone Number"
                  {...register('phone')}
                  margin="normal"
                />
              </Grid>
              <Grid size={{ xs: 12, md: 6 }}>
                <TextField
                  fullWidth
                  label="Date of Birth"
                  type="date"
                  {...register('date_of_birth')}
                  InputLabelProps={{ shrink: true }}
                  margin="normal"
                />
              </Grid>
              <Grid size={{ xs: 12, md: 6 }}>
                <FormControl fullWidth margin="normal">
                  <InputLabel>Gender</InputLabel>
                  <Controller
                    name="gender"
                    control={control}
                    render={({ field }) => (
                      <Select {...field} label="Gender">
                        <MenuItem value="male">Male</MenuItem>
                        <MenuItem value="female">Female</MenuItem>
                        <MenuItem value="other">Other</MenuItem>
                        <MenuItem value="prefer_not_to_say">Prefer not to say</MenuItem>
                      </Select>
                    )}
                  />
                </FormControl>
              </Grid>
            </Grid>
          )}
          {tabValue === 1 && (
            <Grid container spacing={2} sx={{ mt: 2 }}>
              <Grid size={{ xs: 12, md: 6 }}>
                <TextField
                  fullWidth
                  label="Employee Code *"
                  {...register('employee_code', { required: 'Employee code is required' })}
                  error={!!errors.employee_code}
                  helperText={errors.employee_code?.message}
                  margin="normal"
                />
              </Grid>
              <Grid size={{ xs: 12, md: 6 }}>
                <FormControl fullWidth margin="normal">
                  <InputLabel>Employee Type</InputLabel>
                  <Controller
                    name="employee_type"
                    control={control}
                    render={({ field }) => (
                      <Select {...field} label="Employee Type">
                        <MenuItem value="permanent">Permanent</MenuItem>
