// frontend/src/components/AuthProvider.tsx
import React, {ReactNode, createContext, useEffect, useState} from 'react';
import { useRouter } from 'next/router';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';
declare function fetchUser(...args: any[]): any;
declare function refreshToken(...args: any[]): any;
declare function logout(...args: any[]): any;
interface User {
  id: number;
  email: string;
}
interface JwtPayload {
  exp: number;
}
interface AuthContextType {
  user: User | null;
  loading: boolean;
  login: (email: string, password: string) => Promise<void>;
  logout: () => void;
  refreshToken: () => Promise<void>;
}
const AuthContext = createContext<AuthContextType | undefined>(undefined);
export const AuthProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const router = useRouter();
  const api = axios.create({
    baseURL: '/api/v1',
  });
  api.interceptors.request.use(
    (config) => {
      const token = localStorage.getItem('token');
      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
      }
      return config;
    },
    (error) => Promise.reject(error)
  );
  api.interceptors.response.use(
    (response) => response,
    async (error) => {
      const originalRequest = error.config;
      if (error.response.status === 401 && !originalRequest._retry) {
        originalRequest._retry = true;
        await refreshToken();
        return api(originalRequest);
      }
      return Promise.reject(error);
    }
  );
  const refreshToken = async () => {
    const token = localStorage.getItem('token');
    if (!token) {return logout();}
    try {
      const response = await api.post('/auth/refresh-token');
      localStorage.setItem('token', response.data.access_token);
      await fetchUser();
    } catch (refreshError) {
      console.error('Token refresh failed', refreshError);
      logout();
    }
  };
  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
    router.push('/login');
  };
  const fetchUser = async () => {
    try {
      const response = await api.get('/users/me');
      setUser(response.data);
    } catch (fetchError) {
      console.error('Fetch user error', fetchError);
      throw fetchError;
    }
  };
  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          const decoded: JwtPayload = jwtDecode(token);
          if (decoded.exp * 1000 < Date.now()) {
            console.log('Token expired on load, logging out');
            logout();
            return;
          }
          await fetchUser();
        } catch (authError) {
          console.error('Auth check failed', authError);
          logout();
        }
      } else {
        logout();
      }
      setLoading(false);
    };
    checkAuth();
  }, []);
  const login = async (email: string, password: string) => {
    try {
      const response = await api.post('/auth/login/email', { email, password });
      localStorage.setItem('token', response.data.access_token);
      setUser(response.data.user);
      router.push('/dashboard');
    } catch (loginError) {
      console.error('Login error', loginError);
      throw loginError;
    }
  };
  return (
    <AuthContext.Provider value={{ user, loading, login, logout, refreshToken }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
export const useAuth = (): AuthContextType => {
  const context = React.useContext(AuthContext);
  if (undefined === context) {throw new Error('useAuth must be used within AuthProvider');}
  return context;
};

import React, { useState, useRef } from 'react';
import {
  Box,
  Button,
  Typography,
  IconButton,
  Paper,
  CircularProgress,
  Alert,
  Avatar,
  Tooltip,
  Stack
} from '@mui/material';
import {
  CloudUpload,
  Delete,
  PhotoCamera,
  Business
} from '@mui/icons-material';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { companyService } from '../services/authService';
interface CompanyLogoUploadProps {
  companyId: number;
  currentLogoPath?: string | null;
  disabled?: boolean;
  onLogoChange?: (logoPath: string | null) => void;
}
const CompanyLogoUpload: React.FC<CompanyLogoUploadProps> = ({
  companyId,
  currentLogoPath,
  disabled = false,
  onLogoChange
}) => {
  const [isDragOver, setIsDragOver] = useState(false);
  const [uploadError, setUploadError] = useState<string | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(currentLogoPath || null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const queryClient = useQueryClient();
  // Upload mutation
  const uploadMutation = useMutation({
    mutationFn: (file: File) => companyService.uploadLogo(companyId, file),
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ['company'] });
      setPreviewUrl(companyService.getLogoUrl(companyId));
      setUploadError(null);
      if (onLogoChange) {
        onLogoChange(data.logo_path);
      }
    },
    onError: (error: any) => {
      setUploadError(error.message || 'Failed to upload logo');
    },
  });
  // Delete mutation
  const deleteMutation = useMutation({
    mutationFn: () => companyService.deleteLogo(companyId),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['company'] });
      setPreviewUrl(null);
      setUploadError(null);
      if (onLogoChange) {
        onLogoChange(null);
      }
    },
    onError: (error: any) => {
      setUploadError(error.message || 'Failed to delete logo');
    },
  });
  const validateFile = (file: File): string | null => {
    // Check file type
    if (!file.type.startsWith('image/')) {
      return 'Please select an image file (PNG, JPG, JPEG, GIF, etc.)';
    }
    // Check file size (5MB limit)
    if (file.size > 5 * 1024 * 1024) {
      return 'Logo file size must be less than 5MB';
    }
    return null;
  };
  const handleFileSelect = (files: FileList | null) => {
    if (!files || files.length === 0) {return;}
    const file = files[0];
    const validationError = validateFile(file);
    if (validationError) {
      setUploadError(validationError);
      return;
    }
    // Create preview URL
    const objectUrl = URL.createObjectURL(file);
    setPreviewUrl(objectUrl);
    // Upload file
    uploadMutation.mutate(file);
  };
  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    if (!disabled) {
      handleFileSelect(e.dataTransfer.files);
    }
  };
  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    if (!disabled) {
      setIsDragOver(true);
    }
  };
  const handleDragLeave = () => {
    setIsDragOver(false);
  };
  const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    handleFileSelect(e.target.files);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };
  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };
  const handleDeleteLogo = () => {
    deleteMutation.mutate();
  };
  const isLoading = uploadMutation.isPending || deleteMutation.isPending;
  return (
    <Box>
      <Typography variant="subtitle2" gutterBottom>
        Company Logo
      </Typography>
      {uploadError && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {uploadError}
        </Alert>
      )}
      <Stack direction="row" spacing={2} alignItems="center">
        {/* Logo Preview */}
        <Avatar
          src={previewUrl || undefined}
          sx={{
            width: 80,
            height: 80,
            bgcolor: 'grey.200',
            border: '2px solid',
            borderColor: 'grey.300'
          }}
        >
          {!previewUrl && <Business sx={{ fontSize: 40, color: 'grey.500' }} />}
        </Avatar>
        {/* Upload Area */}
        <Box sx={{ flex: 1 }}>
          {!previewUrl ? (
            <Paper
              sx={{
                p: 2,
                border: '2px dashed',
                borderColor: isDragOver ? 'primary.main' : 'grey.300',
                bgcolor: isDragOver ? 'action.hover' : 'background.paper',
                cursor: disabled ? 'default' : 'pointer',
                textAlign: 'center',
                transition: 'all 0.2s ease-in-out',
                minHeight: 80,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
              onDrop={handleDrop}
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onClick={disabled ? undefined : handleUploadClick}
            >
              {isLoading ? (
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <CircularProgress size={20} />
                  <Typography variant="body2">
                    {uploadMutation.isPending ? 'Uploading...' : 'Processing...'}
                  </Typography>
                </Box>
              ) : (
                <Box>
                  <CloudUpload sx={{ fontSize: 32, color: 'grey.500', mb: 1 }} />
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    Drag & drop logo here or click to upload
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    PNG, JPG, JPEG, GIF up to 5MB
                  </Typography>
                </Box>
              )}
            </Paper>
          ) : (
            <Box sx={{ display: 'flex', gap: 1 }}>
              <Tooltip title="Change Logo">
                <Button
                  variant="outlined"
                  startIcon={<PhotoCamera />}
                  onClick={handleUploadClick}
                  disabled={disabled || isLoading}
                  size="small"
                >
                  Change
                </Button>
              </Tooltip>
              <Tooltip title="Remove Logo">
                <IconButton
                  color="error"
                  onClick={handleDeleteLogo}
                  disabled={disabled || isLoading}
                  size="small"
                >
                  <Delete />
                </IconButton>
              </Tooltip>
            </Box>
          )}
        </Box>
      </Stack>
      {/* Hidden file input */}
      <input
        type="file"
        ref={fileInputRef}
        onChange={handleFileInputChange}
        accept="image/*"
        style={{ display: 'none' }}
        disabled={disabled}
      />
    </Box>
  );
};
export default CompanyLogoUpload;

