      await loadJobDetails();
    } catch (err) {
      setError('Failed to update task status');
      console.error('Error updating task status:', err);
    }
  };
  const handleAssignTask = async (taskId: number, technicianId: number) => {
    try {
      await dispatchService.updateInstallationTask(taskId, { assigned_technician_id: technicianId });
      await loadJobDetails();
    } catch (err) {
      setError('Failed to assign task');
      console.error('Error assigning task:', err);
    }
  };
  const handleCompleteJob = async () => {
    if (!job) {return;}
    try {
      setLoading(true);
      await dispatchService.completeInstallationJob(job.id, {
        installation_job_id: job.id,
        completion_date: new Date().toISOString(),
        completion_status: COMPLETION_STATUSES.COMPLETED,
        ...completionForm,
        actual_start_time: completionForm.actual_start_time.toISOString(),
        actual_end_time: completionForm.actual_end_time.toISOString()
      });
      await loadJobDetails();
      if (onJobUpdated) {onJobUpdated();}
      setCurrentTab(0); // Switch back to overview tab
    } catch (err) {
      setError('Failed to complete job');
      console.error('Error completing job:', err);
    } finally {
      setLoading(false);
    }
  };
  const canAssignTasks = user?.role === 'admin' || user?.role === 'manager';
  const canCompleteJob = job?.assigned_technician_id === user?.id || user?.role === 'admin';
  const isJobCompleted = job?.status === INSTALLATION_JOB_STATUSES.COMPLETED;
  if (!job && !loading) {return null;}
  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Dialog
        open={open}
        onClose={onClose}
        maxWidth="lg"
        fullWidth
        PaperProps={{
          sx: { minHeight: '80vh' }
        }}
      >
        <DialogTitle>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            <Typography variant="h6">
              Installation Job Details - {job?.job_number}
            </Typography>
            <IconButton onClick={onClose}>
              <CloseIcon />
            </IconButton>
          </Box>
        </DialogTitle>
        <DialogContent>
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          {loading ? (
            <Box display="flex" justifyContent="center" p={4}>
              <Typography>Loading...</Typography>
            </Box>
          ) : job ? (
            <>
              <Tabs value={currentTab} onChange={(_, newValue) => setCurrentTab(newValue)}>
                <Tab label="Overview" />
                <Tab label="Tasks" />
                {!isJobCompleted && canCompleteJob && <Tab label="Complete Job" />}
                {isJobCompleted && <Tab label="Completion Details" />}
              </Tabs>
              <TabPanel value={currentTab} index={0}>
                {/* Overview Tab */}
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" gutterBottom>Job Information</Typography>
                    <Box display="flex" flexDirection="column" gap={2}>
                      <Box>
                        <Typography variant="body2" color="textSecondary">Status</Typography>
                        <Chip 
                          label={job.status}
                          color={job.status === 'completed' ? 'success' : job.status === 'in_progress' ? 'warning' : 'default'}
                        />
                      </Box>
                      <Box>
                        <Typography variant="body2" color="textSecondary">Priority</Typography>
                        <Chip 
                          label={job.priority}
                          color={job.priority === 'urgent' ? 'error' : job.priority === 'high' ? 'warning' : 'default'}
                        />
                      </Box>
                      <Box>
                        <Typography variant="body2" color="textSecondary">Scheduled Date</Typography>
                        <Typography>{job.scheduled_date ? new Date(job.scheduled_date).toLocaleDateString() : 'Not scheduled'}</Typography>
                      </Box>
                      <Box>
                        <Typography variant="body2" color="textSecondary">Installation Address</Typography>
                        <Typography>{job.installation_address}</Typography>
                      </Box>
                    </Box>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <Typography variant="h6" gutterBottom>Progress Summary</Typography>
                    <Box display="flex" flexDirection="column" gap={2}>
                      <Box>
                        <Typography variant="body2" color="textSecondary">Total Tasks</Typography>
                        <Typography>{job.tasks?.length || 0}</Typography>
                      </Box>
                      <Box>
                        <Typography variant="body2" color="textSecondary">Completed Tasks</Typography>
                        <Typography>
                          {job.tasks?.filter(task => task.status === 'completed').length || 0}
                        </Typography>
                      </Box>
                      <Box>
                        <Typography variant="body2" color="textSecondary">Estimated Duration</Typography>
                        <Typography>
                          {job.tasks?.reduce((total, task) => total + (task.estimated_duration_minutes || 0), 0) || 0} minutes
                        </Typography>
                      </Box>
                    </Box>
                  </Grid>
                </Grid>
              </TabPanel>
              <TabPanel value={currentTab} index={1}>
                {/* Tasks Tab */}
                <Box>
                  <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                    <Typography variant="h6">Installation Tasks</Typography>
                    {canAssignTasks && !isJobCompleted && (
                      <Button
                        startIcon={<AddIcon />}
                        variant="contained"
                        onClick={() => {/* Open task creation form */}}
                        size="small"
                      >
                        Add Task
                      </Button>
                    )}
                  </Box>
                  {/* New Task Form */}
                  {canAssignTasks && !isJobCompleted && (
                    <Card sx={{ mb: 3 }}>
                      <CardContent>
                        <Typography variant="subtitle2" gutterBottom>Create New Task</Typography>
                        <Grid container spacing={2}>
                          <Grid item xs={12} md={6}>
                            <TextField
                              fullWidth
                              label="Task Title"
                              value={newTask.title}
                              onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                              size="small"
                            />
                          </Grid>
                          <Grid item xs={12} md={3}>
                            <FormControl fullWidth size="small">
                              <InputLabel>Priority</InputLabel>
                              <Select
                                value={newTask.priority}
                                onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
                              >
                                {Object.values(INSTALLATION_TASK_PRIORITIES).map(priority => (
                                  <MenuItem key={priority} value={priority}>{priority}</MenuItem>
                                ))}
                              </Select>
                            </FormControl>
                          </Grid>
                          <Grid item xs={12} md={3}>
                            <TextField
                              fullWidth
                              type="number"
                              label="Est. Duration (min)"
                              value={newTask.estimated_duration_minutes}
                              onChange={(e) => setNewTask({ ...newTask, estimated_duration_minutes: parseInt(e.target.value) })}
                              size="small"
                            />
                          </Grid>
                          <Grid item xs={12}>
                            <TextField
                              fullWidth
                              multiline
                              rows={2}
                              label="Description"
                              value={newTask.description}
                              onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                              size="small"
                            />
                          </Grid>
                          <Grid item xs={12}>
                            <Button 
                              variant="contained" 
                              onClick={handleCreateTask}
                              disabled={!newTask.title.trim()}
                              size="small"
                            >
                              Create Task
                            </Button>
                          </Grid>
                        </Grid>
                      </CardContent>
                    </Card>
                  )}
                  {/* Task List */}
                  <Box display="flex" flexDirection="column" gap={2}>
                    {job.tasks?.map((task) => (
                      <Card key={task.id}>
                        <CardContent>
                          <Box display="flex" justifyContent="space-between" alignItems="start">
                            <Box flex={1}>
                              <Typography variant="subtitle1">{task.title}</Typography>
                              <Typography variant="body2" color="textSecondary" gutterBottom>
                                {task.description}
                              </Typography>
                              <Box display="flex" gap={1} mt={1}>
                                <Chip 
                                  label={task.status} 
                                  size="small"
                                  color={task.status === 'completed' ? 'success' : task.status === 'in_progress' ? 'warning' : 'default'}
                                />
                                <Chip label={task.priority} size="small" />
                                <Chip label={`${task.estimated_duration_minutes || 0} min`} size="small" />
                              </Box>
                            </Box>
                            <Box display="flex" gap={1}>
                              {task.status === 'pending' && (
                                <Tooltip title="Start Task">
                                  <IconButton
                                    size="small"
                                    onClick={() => handleUpdateTaskStatus(task.id, 'in_progress')}
                                  >
                                    <StartIcon />
                                  </IconButton>
                                </Tooltip>
                              )}
                              {task.status === 'in_progress' && (
                                <Tooltip title="Complete Task">
                                  <IconButton
                                    size="small"
                                    onClick={() => handleUpdateTaskStatus(task.id, 'completed')}
                                  >
                                    <CompleteIcon />
                                  </IconButton>
                                </Tooltip>
                              )}
                            </Box>
                          </Box>
                        </CardContent>
                      </Card>
                    ))}
                    {(!job.tasks || job.tasks.length === 0) && (
                      <Typography variant="body2" color="textSecondary" textAlign="center" py={4}>
                        No tasks created yet
                      </Typography>
                    )}
                  </Box>
                </Box>
              </TabPanel>
              {!isJobCompleted && canCompleteJob && (
                <TabPanel value={currentTab} index={2}>
                  {/* Complete Job Tab */}
                  <Typography variant="h6" gutterBottom>Complete Installation Job</Typography>
                  <Grid container spacing={3}>
                    <Grid item xs={12}>
                      <TextField
                        fullWidth
                        multiline
                        rows={4}
                        label="Work Performed *"
                        value={completionForm.work_performed}
                        onChange={(e) => setCompletionForm({ ...completionForm, work_performed: e.target.value })}
                        required
                      />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField
                        fullWidth
                        multiline
                        rows={3}
                        label="Issues Encountered"
                        value={completionForm.issues_encountered}
                        onChange={(e) => setCompletionForm({ ...completionForm, issues_encountered: e.target.value })}
                      />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField
                        fullWidth
                        multiline
                        rows={3}
                        label="Resolution Notes"
                        value={completionForm.resolution_notes}
                        onChange={(e) => setCompletionForm({ ...completionForm, resolution_notes: e.target.value })}
                      />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <DateTimePicker
                        label="Actual Start Time"
                        value={completionForm.actual_start_time}
                        onChange={(date) => setCompletionForm({ ...completionForm, actual_start_time: date || new Date() })}
                        slotProps={{ textField: { fullWidth: true } }}
                      />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <DateTimePicker
                        label="Actual End Time"
                        value={completionForm.actual_end_time}
                        onChange={(date) => setCompletionForm({ ...completionForm, actual_end_time: date || new Date() })}
                        slotProps={{ textField: { fullWidth: true } }}
                      />
                    </Grid>
                    <Grid item xs={12}>
                      <Box display="flex" flexDirection="column" gap={2}>
                        <FormControlLabel
                          control={
                            <Checkbox
                              checked={completionForm.quality_check_passed}
                              onChange={(e) => setCompletionForm({ ...completionForm, quality_check_passed: e.target.checked })}
                            />
                          }
                          label="Quality Check Passed"
                        />
                        <FormControlLabel
                          control={
                            <Checkbox
                              checked={completionForm.customer_present}
                              onChange={(e) => setCompletionForm({ ...completionForm, customer_present: e.target.checked })}
                            />
                          }
                          label="Customer Present"
                        />
                        <FormControlLabel
                          control={
                            <Checkbox
                              checked={completionForm.customer_signature_received}
                              onChange={(e) => setCompletionForm({ ...completionForm, customer_signature_received: e.target.checked })}
                            />
                          }
                          label="Customer Signature Received"
                        />
                      </Box>
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField
                        fullWidth
                        multiline
                        rows={3}
                        label="Customer Feedback"
                        value={completionForm.customer_feedback_notes}
                        onChange={(e) => setCompletionForm({ ...completionForm, customer_feedback_notes: e.target.value })}
                      />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <Box>
                        <Typography variant="body2" gutterBottom>Customer Rating</Typography>
                        <Rating
                          value={completionForm.customer_rating}
                          onChange={(_, value) => setCompletionForm({ ...completionForm, customer_rating: value || 5 })}
                        />
                      </Box>
                    </Grid>
                  </Grid>
                </TabPanel>
              )}
              {isJobCompleted && job.completion_record && (
                <TabPanel value={currentTab} index={2}>
                  {/* Completion Details Tab */}
                  <Typography variant="h6" gutterBottom>Completion Details</Typography>
                  <Grid container spacing={3}>
                    <Grid item xs={12} md={6}>
                      <Typography variant="subtitle2" gutterBottom>Work Performed</Typography>
                      <Typography variant="body2" paragraph>
                        {job.completion_record.work_performed}
                      </Typography>
                      {job.completion_record.issues_encountered && (
                        <>
                          <Typography variant="subtitle2" gutterBottom>Issues Encountered</Typography>
                          <Typography variant="body2" paragraph>
                            {job.completion_record.issues_encountered}
                          </Typography>
                        </>
                      )}
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <Typography variant="subtitle2" gutterBottom>Quality & Customer</Typography>
                      <Box display="flex" flexDirection="column" gap={1}>
                        <Chip 
                          label={job.completion_record.quality_check_passed ? "Quality Check Passed" : "Quality Check Failed"}
                          color={job.completion_record.quality_check_passed ? "success" : "error"}
                          size="small"
                        />
                        <Chip 
                          label={job.completion_record.customer_present ? "Customer Present" : "Customer Not Present"}
                          color={job.completion_record.customer_present ? "success" : "default"}
                          size="small"
                        />
                        {job.completion_record.customer_rating && (
                          <Box>
                            <Typography variant="body2">Customer Rating:</Typography>
                            <Rating value={job.completion_record.customer_rating} readOnly />
                          </Box>
                        )}
                      </Box>
                    </Grid>
                  </Grid>
                </TabPanel>
              )}
            </>
          ) : (
            <Box display="flex" justifyContent="center" p={4}>
              <Typography>No job data available</Typography>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose}>Close</Button>
          {!isJobCompleted && canCompleteJob && currentTab === 2 && (
            <Button
              variant="contained"
              color="primary"
              onClick={handleCompleteJob}
              disabled={!completionForm.work_performed.trim() || loading}
            >
              Complete Job
            </Button>
          )}
        </DialogActions>
      </Dialog>
    </LocalizationProvider>
  );
};
export default InstallationJobDialog;

// src/components/DispatchManagement/InstallationSchedulePromptModal.tsx
'use client';
import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  Box,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
  TextField,
  Alert,
  Grid,
  InputLabel,
  Select,
  MenuItem,
  Divider
} from '@mui/material';
import {
  Build as InstallationIcon,
  Schedule as ScheduleIcon,
  Person as PersonIcon
} from '@mui/icons-material';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { InstallationJobCreate } from '../../services/dispatchService';
import { INSTALLATION_JOB_PRIORITIES, InstallationJobPriority } from '../../types/dispatch.types';
interface InstallationSchedulePromptModalProps {
  open: boolean;
  onClose: () => void;
  onCreateInstallation: (installationData: InstallationJobCreate) => Promise<void>;
  dispatchOrderId?: number;
  customerId?: number;
  customerName?: string;
  deliveryAddress?: string;
}
const InstallationSchedulePromptModal: React.FC<InstallationSchedulePromptModalProps> = ({
  open,
  onClose,
  onCreateInstallation,
  dispatchOrderId,
  customerId,
  customerName,
  deliveryAddress
}) => {
  const [createSchedule, setCreateSchedule] = useState<string>('yes');
  const [scheduledDate, setScheduledDate] = useState<Date | null>(null);
  const [priority, setPriority] = useState<InstallationJobPriority>('medium');
  const [estimatedDuration, setEstimatedDuration] = useState<number>(2);
  const [installationAddress, setInstallationAddress] = useState<string>(deliveryAddress || '');
  const [contactPerson, setContactPerson] = useState<string>('');
  const [contactNumber, setContactNumber] = useState<string>('');
  const [installationNotes, setInstallationNotes] = useState<string>('');
  const [assignedTechnician, setAssignedTechnician] = useState<string>('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const handleSubmit = async () => {
    if (createSchedule === 'no') {
      onClose();
      return;
    }
    if (!dispatchOrderId || !customerId) {
      setError('Missing required dispatch order or customer information');
      return;
    }
    if (!installationAddress.trim()) {
      setError('Installation address is required');
      return;
    }
    if (!scheduledDate) {
      setError('Scheduled date and time are required');
      return;
    }
    if (estimatedDuration <= 0) {
      setError('Estimated duration must be greater than 0');
      return;
    }
    try {
      setLoading(true);
      setError(null);
      const installationJobData: InstallationJobCreate = {
        dispatch_order_id: dispatchOrderId,
        customer_id: customerId,
        status: 'scheduled',
        priority,
        scheduled_date: scheduledDate.toISOString(),
        estimated_duration_hours: estimatedDuration,
        installation_address: installationAddress.trim(),
        contact_person: contactPerson.trim() || null,
        contact_number: contactNumber.trim() || null,
        installation_notes: installationNotes.trim() || null,
        assigned_technician_id: assignedTechnician ? parseInt(assignedTechnician) : null
      };
      await onCreateInstallation(installationJobData);
      onClose();
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to create installation schedule');
      console.error('Error creating installation schedule:', err);
    } finally {
      setLoading(false);
    }
  };
  const handleCancel = () => {
    onClose();
  };
  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
        <DialogTitle>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <InstallationIcon color="primary" />
            <Typography variant="h6">
              Create Installation Schedule
            </Typography>
          </Box>
        </DialogTitle>
        <DialogContent dividers>
          <Box sx={{ mb: 3 }}>
            <Alert severity="info" sx={{ mb: 2 }}>
              A delivery challan or service voucher has been created for {customerName || 'Unknown Customer'}. 
              Would you like to schedule an installation for the delivered items?
            </Alert>
            <FormControl component="fieldset" sx={{ mb: 3 }}>
              <Typography variant="subtitle1" gutterBottom>
                Do you want to create an installation schedule for this customer?
              </Typography>
              <RadioGroup
                value={createSchedule}
                onChange={(e) => setCreateSchedule(e.target.value)}
                row
              >
                <FormControlLabel
                  value="yes"
                  control={<Radio />}
                  label="Yes, schedule installation"
                />
                <FormControlLabel
                  value="no"
                  control={<Radio />}
                  label="No, skip installation scheduling"
                />
              </RadioGroup>
            </FormControl>
            {createSchedule === 'yes' && (
              <>
                <Divider sx={{ mb: 3 }} />
                <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <ScheduleIcon />
                  Installation Details
                </Typography>
                {error && (
                  <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                  </Alert>
                )}
                <Grid container spacing={3}>
                  {/* @ts-expect-error Suppress Grid item prop type mismatch due to MUI type resolution */}
                  <Grid item xs={12} md={6}>
                    <FormControl fullWidth>
                      <InputLabel>Priority</InputLabel>
                      <Select
                        value={priority}
                        onChange={(e) => setPriority(e.target.value as InstallationJobPriority)}
                        label="Priority"
                      >
                        {Object.values(INSTALLATION_JOB_PRIORITIES).map(value => (
                          <MenuItem key={value} value={value}>
                            {value.charAt(0).toUpperCase() + value.slice(1)}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  </Grid>
                  {/* @ts-expect-error Suppress Grid item prop type mismatch due to MUI type resolution */}
                  <Grid item xs={12} md={6}>
                    <DateTimePicker
                      label="Scheduled Date & Time"
                      value={scheduledDate}
                      onChange={(newValue) => setScheduledDate(newValue)}
                      slotProps={{
                        textField: {
                          fullWidth: true,
                          helperText: 'Select the preferred installation date and time'
                        }
                      }}
                    />
                  </Grid>
                  {/* @ts-expect-error Suppress Grid item prop type mismatch due to MUI type resolution */}
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      type="number"
                      label="Estimated Duration (hours)"
                      value={estimatedDuration}
                      onChange={(e) => setEstimatedDuration(Math.max(0.5, parseFloat(e.target.value) || 0))}
                      inputProps={{ min: 0.5, step: 0.5 }}
                      helperText="Expected time to complete installation"
                    />
                  </Grid>
                  {/* @ts-expect-error Suppress Grid item prop type mismatch due to MUI type resolution */}
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      label="Assigned Technician ID"
                      value={assignedTechnician}
                      onChange={(e) => setAssignedTechnician(e.target.value)}
                      helperText="Optional: Enter technician ID if known"
                      type="number"
                    />
                  </Grid>
                  {/* @ts-expect-error Suppress Grid item prop type mismatch due to MUI type resolution */}
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      multiline
                      rows={3}
                      label="Installation Address"
                      value={installationAddress}
                      onChange={(e) => setInstallationAddress(e.target.value)}
                      required
                      helperText="Where the installation will take place"
                    />
                  </Grid>
                  {/* @ts-expect-error Suppress Grid item prop type mismatch due to MUI type resolution */}
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      label="Contact Person"
                      value={contactPerson}
                      onChange={(e) => setContactPerson(e.target.value)}
                      helperText="Primary contact for installation"
                    />
                  </Grid>
                  {/* @ts-expect-error Suppress Grid item prop type mismatch due to MUI type resolution */}
                  <Grid item xs={12} md={6}>
                    <TextField
                      fullWidth
                      label="Contact Number"
                      value={contactNumber}
                      onChange={(e) => setContactNumber(e.target.value)}
                      helperText="Phone number for coordination"
                    />
                  </Grid>
                  {/* @ts-expect-error Suppress Grid item prop type mismatch due to MUI type resolution */}
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      multiline
                      rows={2}
                      label="Installation Notes"
                      value={installationNotes}
                      onChange={(e) => setInstallationNotes(e.target.value)}
                      helperText="Any special instructions or requirements"
                    />
                  </Grid>
                </Grid>
              </>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button 
            onClick={handleCancel}
            disabled={loading}
          >
            {createSchedule === 'yes' ? 'Cancel' : 'Skip'}
          </Button>
          <Button
            onClick={handleSubmit}
            variant="contained"
            disabled={loading || (createSchedule === 'yes' && (!installationAddress.trim() || !scheduledDate))}
            startIcon={createSchedule === 'yes' ? <InstallationIcon /> : undefined}
          >
            {loading ? 'Creating...' : createSchedule === 'yes' ? 'Create Installation Schedule' : 'Continue'}
          </Button>
        </DialogActions>
      </Dialog>
    </LocalizationProvider>
  );
};
export default InstallationSchedulePromptModal;

