'use client';
import React from 'react';
import { Button, Box } from '@mui/material';
import { exportToExcel, importFromExcel } from '../lib/excelUtils';
interface ExcelImportExportProps {
  data: any[];
  entity: string;
  onImport: (importedData: any[]) => void;
}
const ExcelImportExport: React.FC<ExcelImportExportProps> = ({ data, entity, onImport }) => {
  const handleExport = () => {
    exportToExcel(data, entity);
  };
  const handleImport = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const importedData = await importFromExcel(file);
      onImport(importedData);
    }
  };
  return (
    <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
      <Button variant="outlined" onClick={handleExport}>
        Export {entity} to Excel
      </Button>
      <Button variant="outlined" component="label">
        Import {entity} from Excel
        <input type="file" hidden accept=".xlsx" onChange={handleImport} />
      </Button>
    </Box>
  );
};
export default ExcelImportExport;

'use client';
import React, { useState } from 'react';
import { Button, Typography, CircularProgress, Alert, Box } from '@mui/material';
import axios from 'axios';
const ExcelUploadComponent = ({ endpoint = '/api/v1/stock/import/excel' }): void => {  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [loading, setLoading] = useState(false);
  const [response, setResponse] = useState<{ message: string; total_processed: number; errors: any[] } | null>(null);
  const [error, setError] = useState<string | null>(null);
  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSelectedFile(event.target.files?.[0] || null);
    setResponse(null);
    setError(null);
  };
  const handleUpload = async () => {
    if (!selectedFile) {
      setError('Please select an Excel file first');
      return;
    }
    setLoading(true);
    const formData = new FormData();
    formData.append('file', selectedFile);
    try {
      const res = await axios.post(endpoint, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`,  // Assume token-based auth
        },
      });
      setResponse(res.data);
      setError(null);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Upload failed');
      setResponse(null);
    } finally {
      setLoading(false);
    }
  };
  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h6">Upload Excel File</Typography>
      <input type="file" accept=".xlsx,.xls" onChange={handleFileChange} />
      <Button variant="contained" onClick={handleUpload} disabled={loading} sx={{ mt: 2 }}>
        {loading ? <CircularProgress size={24} /> : 'Upload'}
      </Button>
      {response && (
        <Alert severity="success" sx={{ mt: 2 }}>
          {response.message} (Processed: {response.total_processed}, Errors: {response.errors.length})
        </Alert>
      )}
      {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
    </Box>
  );
};
export default ExcelUploadComponent;

