const SATISFACTION_LEVELS = [
  { value: 'very_satisfied', label: 'Very Satisfied', color: '#4caf50' },
  { value: 'satisfied', label: 'Satisfied', color: '#8bc34a' },
  { value: 'neutral', label: 'Neutral', color: '#ff9800' },
