    mutationFn: ({ id, testData }: { id: number; testData: any }) =>
      testNotificationTemplate(id, testData),
    onSuccess: (data) => {
      toast.success('Template test completed successfully');
      console.log('Test result:', data);
    },
    onError: (error: any) => {
      toast.error(error.response?.data?.detail || 'Failed to test template');
    }
  });
  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      template_type: 'appointment_reminder',
      channel: 'email',
      subject: '',
      body: '',
      html_body: '',
      trigger_event: '',
      variables: [],
      is_active: true
    });
  };
  const handleEdit = (template: NotificationTemplate) => {
    setEditingTemplate(template);
    setFormData({
      name: template.name,
      description: template.description || '',
      template_type: template.template_type,
      channel: template.channel,
      subject: template.subject || '',
      body: template.body,
      html_body: template.html_body || '',
      trigger_event: template.trigger_event || '',
      variables: template.variables || [],
      is_active: template.is_active
    });
    setIsCreateModalOpen(true);
  };
  const handleSubmit = () => {
    if (editingTemplate) {
      updateMutation.mutate({ id: editingTemplate.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };
  const handleDelete = (template: NotificationTemplate) => {
    setTemplateToDelete(template);
    setIsDeleteModalOpen(true);
  };
  const confirmDelete = () => {
    if (templateToDelete) {
      deleteMutation.mutate(templateToDelete.id);
    }
  };
  const handleTest = (template: NotificationTemplate) => {
    testMutation.mutate({
      id: template.id,
      testData: {
        variables: {
          customer_name: 'John Doe',
          appointment_date: '2024-01-15 10:00 AM',
          service_type: 'AC Repair'
        }
      }
    });
  };
  const getChannelIcon = (channel: string) => {
    switch (channel) {
      case 'email':
        return <Email />;
      case 'sms':
        return <Sms />;
      case 'push':
        return <NotificationImportant />;
      case 'in_app':
        return <Notifications />;
      default:
        return <Assignment />;
    }
  };
  // Filter templates by channel for tabs
  const emailTemplates = templates.filter(t => t.channel === 'email');
  const smsTemplates = templates.filter(t => t.channel === 'sms');
  const pushTemplates = templates.filter(t => t.channel === 'push');
  const inAppTemplates = templates.filter(t => t.channel === 'in_app');
  const TemplateTable: React.FC<{ templates: NotificationTemplate[] }> = ({ templates }) => (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Name</TableCell>
            <TableCell>Type</TableCell>
            <TableCell>Channel</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Variables</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {templates.map((template) => (
            <TableRow key={template.id}>
              <TableCell>
                <Box>
                  <Typography variant="subtitle2">{template.name}</Typography>
                  {template.description && (
                    <Typography variant="caption" color="text.secondary">
                      {template.description}
                    </Typography>
                  )}
                </Box>
              </TableCell>
              <TableCell>
                <Chip 
                  label={getTemplateTypeDisplayName(template.template_type as any)} 
                  size="small"
                  variant="outlined"
                />
              </TableCell>
              <TableCell>
                <Box display="flex" alignItems="center" gap={1}>
                  {getChannelIcon(template.channel)}
                  {getChannelDisplayName(template.channel as any)}
                </Box>
              </TableCell>
              <TableCell>
                <Chip 
                  label={template.is_active ? 'Active' : 'Inactive'}
                  color={template.is_active ? 'success' : 'default'}
                  size="small"
                />
              </TableCell>
              <TableCell>
                {template.variables && template.variables.length > 0 && (
                  <Tooltip title={template.variables.join(', ')}>
                    <Chip 
                      label={`${template.variables.length} variables`} 
                      size="small"
                      variant="outlined"
                    />
                  </Tooltip>
                )}
              </TableCell>
              <TableCell>
                <Box display="flex" gap={1}>
                  <Tooltip title="Test Template">
                    <IconButton 
                      size="small" 
                      onClick={() => handleTest(template)}
                      disabled={testMutation.isPending}
                    >
                      <PlayArrow />
                    </IconButton>
                  </Tooltip>
                  <Tooltip title="Edit Template">
                    <IconButton size="small" onClick={() => handleEdit(template)}>
                      <Edit />
                    </IconButton>
                  </Tooltip>
                  <Tooltip title="Delete Template">
                    <IconButton 
                      size="small" 
                      onClick={() => handleDelete(template)}
                      color="error"
                    >
                      <Delete />
                    </IconButton>
                  </Tooltip>
                </Box>
              </TableCell>
            </TableRow>
          ))}
          {templates.length === 0 && (
            <TableRow>
              <TableCell colSpan={6} align="center" sx={{ py: 4 }}>
                <Typography color="text.secondary">
                  No templates found for this channel
                </Typography>
              </TableCell>
            </TableRow>
          )}
        </TableBody>
      </Table>
    </TableContainer>
  );
  if (error) {
    return (
      <Alert severity="error">
        Failed to load notification templates. Please try again.
      </Alert>
    );
  }
  return (
    <Box>
      <Card>
        <CardContent>
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
            <Typography variant="h5" component="h2">
              Notification Templates
            </Typography>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => setIsCreateModalOpen(true)}
            >
              Create Template
            </Button>
          </Box>
          {isLoading ? (
            <Box display="flex" justifyContent="center" py={4}>
              <CircularProgress />
            </Box>
          ) : (
            <Box>
              <Tabs 
                value={selectedTab} 
                onChange={(_, newValue) => setSelectedTab(newValue)}
                sx={{ borderBottom: 1, borderColor: 'divider' }}
              >
                <Tab 
                  label={`Email (${emailTemplates.length})`} 
                  icon={<Email />}
                  iconPosition="start"
                />
                <Tab 
                  label={`SMS (${smsTemplates.length})`} 
                  icon={<Sms />}
                  iconPosition="start"
                />
                <Tab 
                  label={`Push (${pushTemplates.length})`} 
                  icon={<NotificationImportant />}
                  iconPosition="start"
                />
                <Tab 
                  label={`In-App (${inAppTemplates.length})`} 
                  icon={<Notifications />}
                  iconPosition="start"
                />
              </Tabs>
              <TabPanel value={selectedTab} index={0}>
                <TemplateTable templates={emailTemplates} />
              </TabPanel>
              <TabPanel value={selectedTab} index={1}>
                <TemplateTable templates={smsTemplates} />
              </TabPanel>
              <TabPanel value={selectedTab} index={2}>
                <TemplateTable templates={pushTemplates} />
              </TabPanel>
              <TabPanel value={selectedTab} index={3}>
                <TemplateTable templates={inAppTemplates} />
              </TabPanel>
            </Box>
          )}
        </CardContent>
      </Card>
      {/* Create/Edit Template Modal */}
      <Dialog 
        open={isCreateModalOpen} 
        onClose={() => setIsCreateModalOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          {editingTemplate ? 'Edit Template' : 'Create Template'}
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Template Name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                required
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>Template Type</InputLabel>
                <Select
                  value={formData.template_type}
                  label="Template Type"
                  onChange={(e) => setFormData({ ...formData, template_type: e.target.value })}
                >
                  {TEMPLATE_TYPES.map(type => (
                    <MenuItem key={type} value={type}>
                      {getTemplateTypeDisplayName(type)}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth>
                <InputLabel>Channel</InputLabel>
                <Select
                  value={formData.channel}
                  label="Channel"
                  onChange={(e) => setFormData({ ...formData, channel: e.target.value })}
                >
                  {NOTIFICATION_CHANNELS.map(channel => (
                    <MenuItem key={channel} value={channel}>
                      {getChannelDisplayName(channel)}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControlLabel
                control={
                  <Switch
                    checked={formData.is_active}
                    onChange={(e) => setFormData({ ...formData, is_active: e.target.checked })}
                  />
                }
                label="Active"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                multiline
                rows={2}
              />
            </Grid>
            {formData.channel === 'email' && (
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Subject"
                  value={formData.subject}
                  onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                />
              </Grid>
            )}
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Message Body"
                value={formData.body}
                onChange={(e) => setFormData({ ...formData, body: e.target.value })}
                multiline
                rows={4}
                required
                helperText="Use {variable_name} for dynamic content (e.g., {customer_name}, {appointment_date})"
              />
            </Grid>
            {formData.channel === 'email' && (
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="HTML Body (Optional)"
                  value={formData.html_body}
                  onChange={(e) => setFormData({ ...formData, html_body: e.target.value })}
                  multiline
                  rows={4}
                  helperText="HTML version of the email for rich formatting"
                />
              </Grid>
            )}
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Trigger Event (Optional)"
                value={formData.trigger_event}
                onChange={(e) => setFormData({ ...formData, trigger_event: e.target.value })}
                helperText="Event that automatically triggers this notification (e.g., customer_interaction, appointment_scheduled)"
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setIsCreateModalOpen(false)}>Cancel</Button>
          <Button 
            onClick={handleSubmit}
            variant="contained"
            disabled={createMutation.isPending || updateMutation.isPending}
          >
            {editingTemplate ? 'Update' : 'Create'}
          </Button>
        </DialogActions>
      </Dialog>
      {/* Delete Confirmation Modal */}
      <Dialog open={isDeleteModalOpen} onClose={() => setIsDeleteModalOpen(false)}>
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete the template "{templateToDelete?.name}"?
            This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setIsDeleteModalOpen(false)}>Cancel</Button>
          <Button 
            onClick={confirmDelete}
            color="error"
            variant="contained"
            disabled={deleteMutation.isPending}
          >
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};
export default NotificationTemplates;

import React, { useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  TextField,
  Button,
  Typography,
  Alert,
  CircularProgress,
  Stepper,
  Step,
  StepLabel,
  Container
} from '@mui/material';
import {useForm} from 'react-hook-form';
import { useRouter } from 'next/navigation';
import { authService } from '../services/authService';
interface OTPLoginProps {
  onLogin: (token: string, loginResponse?: any) => void;
}
interface EmailFormData {
  email: string;
}
interface OTPFormData {
  otp: string;
}
const OTPLogin: React.FC<OTPLoginProps> = ({ onLogin }) => {
  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const emailForm = useForm<EmailFormData>();
  const otpForm = useForm<OTPFormData>();
  const router = useRouter(); // Removed underscore since it's used
  const steps = ['Enter Email', 'Verify OTP'];
  const handleEmailSubmit = async (data: EmailFormData) => {
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      setUserEmail(data.email);
      setSuccess(`OTP sent to ${data.email}. Please check your email.`);
      setActiveStep(1);
    } catch (error: any) {
      setError(error.response?.data?.detail || 'Failed to send OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  const handleOTPSubmit = async (data: OTPFormData) => {
    setLoading(true);
    setError('');
    try {
      const response = await authService.verifyOTP(userEmail, data.otp);
      setSuccess('Login successful!');
      // Login successful!
      localStorage.setItem('token', response.access_token);
      localStorage.setItem('user_role', response.user_role);
      // Organization context is managed by backend session only
      // Call parent callback with token and response
      onLogin(response.access_token, response);
      // Redirect to dashboard
      router.push('/dashboard');
    } catch (error: any) {
      setError(error.response?.data?.detail || 'Invalid OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  const handleResendOTP = async () => {
    setLoading(true);
    setError('');
    try {
      await authService.requestOTP(userEmail);
      setSuccess('OTP resent successfully!');
    } catch (error: any) {
      setError(error.response?.data?.detail || 'Failed to resend OTP.');
    } finally {
      setLoading(false);
    }
  };
  const handleBack = () => {
    setActiveStep(0);
    setError('');
    setSuccess('');
    emailForm.reset();
    otpForm.reset();
  };
  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 8, mb: 4 }}>
        <Card>
          <CardContent sx={{ p: 4 }}>
            <Typography variant="h4" component="h1" gutterBottom align="center">
              TRITIQ BOS
            </Typography>
            <Typography variant="h6" component="h2" gutterBottom align="center" color="textSecondary">
              OTP Authentication
            </Typography>
            <Stepper activeStep={activeStep} sx={{ mt: 3, mb: 4 }}>
              {steps.map((label) => (
                <Step key={label}>
                  <StepLabel>{label}</StepLabel>
                </Step>
              ))}
            </Stepper>
            {error && (
              <Alert severity="error" sx={{ mb: 2 }}>
                {error}
              </Alert>
            )}
            {success && (
              <Alert severity="success" sx={{ mb: 2 }}>
                {success}
              </Alert>
            )}
            {activeStep === 0 && (
              <Box component="form" onSubmit={emailForm.handleSubmit(handleEmailSubmit)}>
                <TextField
                  fullWidth
                  label="Email Address"
                  type="email"
                  {...emailForm.register('email', {
                    required: 'Email is required',
                    pattern: {
                      value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                      message: 'Invalid email address'
                    }
                  })}
                  error={!!emailForm.formState.errors.email}
                  helperText={emailForm.formState.errors.email?.message}
                  margin="normal"
                  autoFocus
                />
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  sx={{ mt: 3, mb: 2 }}
                  disabled={loading}
                >
                  {loading ? <CircularProgress size={24} /> : 'Send OTP'}
                </Button>
                <Typography variant="body2" color="textSecondary" align="center">
                  Enter your email address to receive an OTP for secure login.
                </Typography>
              </Box>
            )}
            {activeStep === 1 && (
              <Box component="form" onSubmit={otpForm.handleSubmit(handleOTPSubmit)}>
                <Typography variant="body1" sx={{ mb: 2 }}>
                  Enter the 6-digit OTP sent to: <strong>{userEmail}</strong>
                </Typography>
                <TextField
                  fullWidth
                  label="OTP Code"
                  type="text"
                  inputProps={{ maxLength: 6, pattern: '[0-9]*' }}
                  {...otpForm.register('otp', {
                    required: 'OTP is required',
                    pattern: {
                      value: /^\d{6}$/,
                      message: 'OTP must be 6 digits'
                    }
                  })}
                  error={!!otpForm.formState.errors.otp}
                  helperText={otpForm.formState.errors.otp?.message}
                  margin="normal"
                  autoFocus
                />
                <Box sx={{ mt: 3, mb: 2, display: 'flex', gap: 2 }}>
                  <Button
                    variant="outlined"
                    onClick={handleBack}
                    disabled={loading}
                    sx={{ flex: 1 }}
                  >
                    Back
                  </Button>
                  <Button
                    type="submit"
                    variant="contained"
                    disabled={loading}
                    sx={{ flex: 1 }}
                  >
                    {loading ? <CircularProgress size={24} /> : 'Verify & Login'}
                  </Button>
                </Box>
                <Button
                  variant="text"
                  onClick={handleResendOTP}
                  disabled={loading}
                  fullWidth
                  sx={{ mt: 1 }}
                >
                  Resend OTP
                </Button>
                <Typography variant="body2" color="textSecondary" align="center" sx={{ mt: 2 }}>
                  OTP is valid for 10 minutes.
                </Typography>
              </Box>
            )}
          </CardContent>
        </Card>
      </Box>
    </Container>
  );
};
export default OTPLogin;

'use client';
import React, { useState } from 'react';
import { TextField, Button, CircularProgress, Grid, FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import axios from 'axios';
// State to GST state code map (mirrored from backend for autofill)
const STATE_CODE_MAP: { [key: string]: string } = {
