import React, { useState, useEffect } from 'react';
import {
declare function fetchUserOrganizations(...args: any[]): any;
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  Typography,
  Alert,
  CircularProgress,
  SelectChangeEvent
} from '@mui/material';
import { organizationService } from '../services/organizationService';
interface Organization {
  id: number;
  name: string;
  subdomain: string;
  role: string;
  is_current: boolean;
}
const OrganizationSwitcher: React.FC = () => {
  const [organizations, setOrganizations] = useState<Organization[]>([]);
  const [currentOrganization, setCurrentOrganization] = useState<number | ''>('');
  const [loading, setLoading] = useState(true);
  const [switching, setSwitching] = useState(false);
  const [err, setErr] = useState<string | null>(null);
  useEffect(() => {
    fetchUserOrganizations();
  }, []);
  const fetchUserOrganizations = async () => {
    try {
      setLoading(true);
      const orgs = await organizationService.getUserOrganizations();
      setOrganizations(orgs);
      // Find current organization
      const current = orgs.find((org: Organization) => org.is_current);
      if (current) {
        setCurrentOrganization(current.id);
      }
    } catch (error: any) {
      setErr(error.message);
    } finally {
      setLoading(false);
    }
  };
  const handleOrganizationSwitch = async (event: SelectChangeEvent<number | ''>) => {
    const organizationId = event.target.value as number;
    if (!organizationId || organizationId === currentOrganization) {
      return;
    }
    try {
      setSwitching(true);
      setErr(null);
      await organizationService.switchOrganization(organizationId);
      // Update current organization state
      setCurrentOrganization(organizationId);
      setOrganizations(prev => 
        prev.map(org => ({
          ...org,
          is_current: org.id === organizationId
        }))
      );
      // Call the page to update all components with new organization context
      window.location.reload();
    } catch (error: any) {
      setErr(error.message);
    } finally {
      setSwitching(false);
    }
  };
  if (loading) {
    return (
      <Box display="flex" alignItems="center" gap={1}>
        <CircularProgress size={20} />
        <Typography variant="caption">Loading organizations...</Typography>
      </Box>
    );
  }
  if (organizations.length === 0) {
    return (
      <Typography variant="caption" color="text.secondary">
        No organizations available
      </Typography>
    );
  }
  if (organizations.length === 1) {
    const org = organizations[0];
    return (
      <Box>
        <Typography variant="body2" fontWeight="medium">
          {org.name}
        </Typography>
        <Typography variant="caption" color="text.secondary">
          Role: {org.role}
        </Typography>
      </Box>
    );
  }
  return (
    <Box sx={{ minWidth: 200 }}>
      {err && (
        <Alert severity="error" sx={{ mb: 1, fontSize: '0.8rem' }}>
          {err}
        </Alert>
      )}
      <FormControl fullWidth size="small">
        <InputLabel id="organization-select-label">Organization</InputLabel>
        <Select
          labelId="organization-select-label"
          value={currentOrganization}
          label="Organization"
          onChange={handleOrganizationSwitch}
          disabled={switching}
        >
          {organizations.map((org) => (
            <MenuItem key={org.id} value={org.id}>
              <Box>
                <Typography variant="body2" fontWeight="medium">
                  {org.name}
                </Typography>
                <Typography variant="caption" color="text.secondary">
                  Role: {org.role} | {org.subdomain}
                </Typography>
              </Box>
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      {switching && (
        <Box display="flex" alignItems="center" gap={1} mt={1}>
          <CircularProgress size={16} />
          <Typography variant="caption">Switching organization...</Typography>
        </Box>
      )}
    </Box>
  );
};
export default OrganizationSwitcher;

'use client';
import React, { useState, useRef } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  Box,
  Typography,
  Alert,
  CircularProgress
} from '@mui/material';
import { useForm } from 'react-hook-form';
import { getFeatureFlag } from '../utils/config';
interface PasswordChangeModalProps {
  open: boolean;
  onClose: () => void;
  onSuccess?: () => void;
  isRequired?: boolean; // For mandatory password changes
}
interface PasswordFormData {
  current_password?: string;
  new_password: string;
  confirm_password: string;
}
const PasswordChangeModal: React.FC<PasswordChangeModalProps> = ({
  open,
  onClose,
  onSuccess,
  isRequired = false
}) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);
  // Check if password change functionality is enabled
  const passwordChangeEnabled = getFeatureFlag('passwordChange');
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    watch,
    getValues
  } = useForm<PasswordFormData>();
  const new_password = watch('new_password');
  const handleClose = () => {
    if (!isRequired || success) {
      reset();
      setError(null);
      setSuccess(false);
      onClose();
    }
  };
  const onSubmit = async (data: PasswordFormData) => {
    // Validation checks
    if (data.new_password !== data.confirm_password) {
      setError('New passwords do not match');
      return;
    }
    if (!isRequired && !data.current_password) {
      setError('Current password is required');
      return;
    }
    setLoading(true);
    setError(null);
    try {
      // Call API for password change (mandatory or regular)
      const endpoint = isRequired ? '/api/v1/password/change-mandatory' : '/api/v1/password/change';
      const payload = isRequired ? { new_password: data.new_password } : data;
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      if (!response.ok) {throw new Error('Failed to change password');}
      setSuccess(true);
      if (onSuccess) {
        onSuccess();
      }
      if (!isRequired) {
        setTimeout(() => {
          handleClose();
        }, 2000);
      }
    } catch (err: any) {
      // Try to extract from various error structures
      const detail = err.response?.data?.detail;
      const message = err.response?.data?.message;
      let errorMessage = 'Failed to change password';
      if (typeof detail === 'string' && detail) {
        errorMessage = detail;
      } else if (typeof message === 'string' && message) {
        errorMessage = message;
      } else if (Array.isArray(detail) && detail.length > 0) {
        // Handle Pydantic validation errors
        const messages = detail.map(e => e.msg || `${e.loc?.join(' -> ')}: ${e.type}`).filter(Boolean);
        errorMessage = messages.length > 0 ? messages.join(', ') : 'Validation error';
      } else if (detail && typeof detail === 'object') {
        // Handle object error details
        errorMessage = detail.error || detail.message || 'Invalid request format';
      } else if (typeof err.message === 'string' && err.message) {
        errorMessage = err.message;
      } else if (err.status === 422) {
        errorMessage = 'Invalid request. Please check your input fields.';
      }
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };
  return (
    <Dialog 
      open={open} 
      onClose={handleClose} 
      maxWidth="sm" 
      fullWidth
      disableEscapeKeyDown={isRequired && !success}
    >
      <DialogTitle>
        {isRequired ? 'Change Your Password' : 'Change Password'}
      </DialogTitle>
      <DialogContent>
        <Box sx={{ pt: 2 }}>
          {isRequired && !success && (
            <Alert severity="warning" sx={{ mb: 2 }}>
              You are required to change your password before continuing.
            </Alert>
          )}
          {!passwordChangeEnabled && (
            <Alert severity="info" sx={{ mb: 2 }}>
              Password change functionality is temporarily disabled. Please contact your administrator.
            </Alert>
          )}
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}
          {success && (
            <Alert severity="success" sx={{ mb: 2 }}>
              Password changed successfully!
              {isRequired && (
                <Typography variant="body2" sx={{ mt: 1 }}>
                  You can now continue using the application.
                </Typography>
              )}
            </Alert>
          )}
          {!success && passwordChangeEnabled && (
            <form onSubmit={handleSubmit(onSubmit)}>
              {!isRequired && (
                <TextField
                  fullWidth
                  label="Current Password"
                  type="password"
                  margin="normal"
                  {...register('current_password', {
                    required: 'Current password is required'
                  })}
                  error={!!errors.current_password}
                  helperText={errors.current_password?.message}
                  disabled={loading}
                />
              )}
              <TextField
                fullWidth
                label="New Password"
                type="password"
                margin="normal"
                {...register('new_password', {
                  required: 'New password is required',
                  minLength: {
                    value: 8,
                    message: 'Password must be at least 8 characters long'
                  },
                  pattern: {
                    value: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&].+$/,
                    message: 'Password must contain at least one uppercase letter, one lowercase letter, one number, and one special character'
                  }
                })}
                error={!!errors.new_password}
                helperText={errors.new_password?.message}
                disabled={loading}
              />
              <TextField
                fullWidth
                label="Confirm New Password"
                type="password"
                margin="normal"
                {...register('confirm_password', {
                  required: 'Please confirm your new password',
                  validate: (value) => value === new_password || 'Passwords do not match'
                })}
                error={!!errors.confirm_password}
                helperText={errors.confirm_password?.message}
                disabled={loading}
              />
              <Box sx={{ mt: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Password must be at least 8 characters long and contain at least one uppercase letter, 
                  one lowercase letter, one number, and one special character (@$!%*?&).
                </Typography>
              </Box>
            </form>
          )}
        </Box>
      </DialogContent>
      <DialogActions>
        {!isRequired && (
          <Button onClick={handleClose} disabled={loading}>
            Cancel
          </Button>
        )}
        {success && isRequired && (
          <Button onClick={handleClose} variant="contained">
            Continue
          </Button>
        )}
        {!success && passwordChangeEnabled && (
          <Button
            onClick={handleSubmit(onSubmit)}
            variant="contained"
            disabled={loading}
            startIcon={loading ? <CircularProgress size={20} /> : null}
          >
            Change Password
          </Button>
        )}
        {!passwordChangeEnabled && (isRequired ? (
          <Button onClick={handleClose} variant="contained">
            Continue Without Changing Password
          </Button>
        ) : (
          <Button onClick={handleClose}>
            Close
          </Button>
        ))}
      </DialogActions>
    </Dialog>
  );
};
export default PasswordChangeModal;

