  // Preview mutation
  const previewMutation = useMutation({
    mutationFn: ({ id, testData }: { id: number; testData: any }) =>
      testNotificationTemplate(id, testData),
    onSuccess: (data) => {
      setPreviewContent(data);
      setIsPreviewOpen(true);
    },
    onError: (error: any) => {
      toast.error('Failed to generate preview');
    }
  });
  const resetSingleForm = () => {
    setSingleForm({
      template_id: '',
      recipient_type: 'customer',
      recipient_id: '',
      channel: 'email',
      variables: {},
      override_subject: '',
      override_content: ''
    });
  };
  const resetBulkForm = () => {
    setBulkForm({
      template_id: '',
      subject: '',
      content: '',
      channel: 'email',
      recipient_type: 'customers',
      recipient_ids: [],
      segment_name: '',
      variables: {}
    });
  };
  const handleSendSingle = () => {
    if (!singleForm.recipient_id) {
      toast.error('Please select a recipient');
      return;
    }
    if (!singleForm.template_id && !singleForm.override_content) {
      toast.error('Please select a template or provide custom content');
      return;
    }
    const request: NotificationSendRequest = {
      template_id: singleForm.template_id ? parseInt(singleForm.template_id) : undefined,
      recipient_type: singleForm.recipient_type,
      recipient_id: parseInt(singleForm.recipient_id),
      channel: singleForm.channel,
      variables: Object.keys(singleForm.variables).length > 0 ? singleForm.variables : undefined,
      override_subject: singleForm.override_subject || undefined,
      override_content: singleForm.override_content || undefined
    };
    sendSingleMutation.mutate(request);
  };
  const handleSendBulk = () => {
    if (bulkForm.recipient_type === 'customers' && bulkForm.recipient_ids.length === 0) {
      toast.error('Please select customers');
      return;
    }
    if (bulkForm.recipient_type === 'segment' && !bulkForm.segment_name) {
      toast.error('Please enter segment name');
      return;
    }
    if (!bulkForm.template_id && !bulkForm.content) {
      toast.error('Please select a template or provide custom content');
      return;
    }
    const request: BulkNotificationRequest = {
      template_id: bulkForm.template_id ? parseInt(bulkForm.template_id) : undefined,
      subject: bulkForm.subject || undefined,
      content: bulkForm.content,
      channel: bulkForm.channel,
      recipient_type: bulkForm.recipient_type,
      recipient_ids: bulkForm.recipient_ids.length > 0 ? bulkForm.recipient_ids : undefined,
      segment_name: bulkForm.segment_name || undefined,
      variables: Object.keys(bulkForm.variables).length > 0 ? bulkForm.variables : undefined
    };
    sendBulkMutation.mutate(request);
  };
  const handlePreview = (isForBulk: boolean = false) => {
    const templateId = isForBulk ? bulkForm.template_id : singleForm.template_id;
    const variables = isForBulk ? bulkForm.variables : singleForm.variables;
    if (!templateId) {
      toast.error('Please select a template to preview');
      return;
    }
    previewMutation.mutate({
      id: parseInt(templateId),
      testData: { variables }
    });
  };
  const selectedTemplate = templates.find(t => 
    t.id === parseInt(selectedTab === 0 ? singleForm.template_id : bulkForm.template_id)
  );
  // Filter entities based on recipient type
  const customers = entities.filter(e => e.type === 'Customer');
  const users = entities.filter(e => e.type === 'User');
  return (
    <Box>
      <Card>
        <CardContent>
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
            <Typography variant="h5" component="h2">
              Send Notifications
            </Typography>
          </Box>
          <Tabs 
            value={selectedTab} 
            onChange={(_, newValue) => setSelectedTab(newValue)}
            sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}
          >
            <Tab 
              label="Single Notification" 
              icon={<Person />}
              iconPosition="start"
            />
            <Tab 
              label="Bulk Notification" 
              icon={<Group />}
              iconPosition="start"
            />
          </Tabs>
          {/* Single Notification Tab */}
          <TabPanel value={selectedTab} index={0}>
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <FormControl fullWidth margin="normal">
                  <InputLabel>Template (Optional)</InputLabel>
                  <Select
                    value={singleForm.template_id}
                    label="Template (Optional)"
                    onChange={(e) => setSingleForm({ ...singleForm, template_id: e.target.value })}
                  >
                    <MenuItem value="">Custom Message</MenuItem>
                    {templates.map(template => (
                      <MenuItem key={template.id} value={template.id.toString()}>
                        {template.name} ({getChannelDisplayName(template.channel as any)})
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={6}>
                <FormControl fullWidth margin="normal">
                  <InputLabel>Channel</InputLabel>
                  <Select
                    value={singleForm.channel}
                    label="Channel"
                    onChange={(e) => setSingleForm({ ...singleForm, channel: e.target.value })}
                  >
                    {NOTIFICATION_CHANNELS.map(channel => (
                      <MenuItem key={channel} value={channel}>
                        <Box display="flex" alignItems="center" gap={1}>
                          {getChannelIcon(channel)}
                          {getChannelDisplayName(channel)}
                        </Box>
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={6}>
                <FormControl fullWidth margin="normal">
                  <InputLabel>Recipient Type</InputLabel>
                  <Select
                    value={singleForm.recipient_type}
                    label="Recipient Type"
                    onChange={(e) => setSingleForm({ 
                      ...singleForm, 
                      recipient_type: e.target.value as any,
                      recipient_id: '' 
                    })}
                  >
                    <MenuItem value="customer">Customer</MenuItem>
                    <MenuItem value="user">User</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={6}>
                <Autocomplete
                  options={singleForm.recipient_type === 'customer' ? customers : users}
                  getOptionLabel={(option) => option.name}
                  value={entities.find(e => e.id.toString() === singleForm.recipient_id) || null}
                  onChange={(_, value) => setSingleForm({ 
                    ...singleForm, 
                    recipient_id: value?.id.toString() || '' 
                  })}
                  renderInput={(params) => (
                    <TextField 
                      {...params} 
                      label={`Select ${singleForm.recipient_type}`}
                      margin="normal"
                      fullWidth
                    />
                  )}
                />
              </Grid>
              {!singleForm.template_id && (
                <>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Subject"
                      value={singleForm.override_subject}
                      onChange={(e) => setSingleForm({ ...singleForm, override_subject: e.target.value })}
                      margin="normal"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Message Content"
                      value={singleForm.override_content}
                      onChange={(e) => setSingleForm({ ...singleForm, override_content: e.target.value })}
                      multiline
                      rows={4}
                      margin="normal"
                      required
                    />
                  </Grid>
                </>
              )}
              {selectedTemplate && selectedTemplate.variables && (
                <Grid item xs={12}>
                  <Typography variant="h6" gutterBottom>
                    Template Variables
                  </Typography>
                  <Grid container spacing={2}>
                    {selectedTemplate.variables.map(variable => (
                      <Grid item xs={12} sm={6} key={variable}>
                        <TextField
                          fullWidth
                          label={variable}
                          value={singleForm.variables[variable] || ''}
                          onChange={(e) => setSingleForm({
                            ...singleForm,
                            variables: {
                              ...singleForm.variables,
                              [variable]: e.target.value
                            }
                          })}
                        />
                      </Grid>
                    ))}
                  </Grid>
                </Grid>
              )}
              <Grid item xs={12}>
                <Box display="flex" gap={2}>
                  <Button
                    variant="contained"
                    startIcon={<Send />}
                    onClick={handleSendSingle}
                    disabled={sendSingleMutation.isPending}
                  >
                    Send Notification
                  </Button>
                  {singleForm.template_id && (
                    <Button
                      variant="outlined"
                      startIcon={<Preview />}
                      onClick={() => handlePreview(false)}
                      disabled={previewMutation.isPending}
                    >
                      Preview
                    </Button>
                  )}
                </Box>
              </Grid>
            </Grid>
          </TabPanel>
          {/* Bulk Notification Tab */}
          <TabPanel value={selectedTab} index={1}>
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <FormControl fullWidth margin="normal">
                  <InputLabel>Template (Optional)</InputLabel>
                  <Select
                    value={bulkForm.template_id}
                    label="Template (Optional)"
                    onChange={(e) => setBulkForm({ ...bulkForm, template_id: e.target.value })}
                  >
                    <MenuItem value="">Custom Message</MenuItem>
                    {templates.map(template => (
                      <MenuItem key={template.id} value={template.id.toString()}>
                        {template.name} ({getChannelDisplayName(template.channel as any)})
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} md={6}>
                <FormControl fullWidth margin="normal">
                  <InputLabel>Channel</InputLabel>
                  <Select
                    value={bulkForm.channel}
                    label="Channel"
                    onChange={(e) => setBulkForm({ ...bulkForm, channel: e.target.value })}
                  >
                    {NOTIFICATION_CHANNELS.map(channel => (
                      <MenuItem key={channel} value={channel}>
                        <Box display="flex" alignItems="center" gap={1}>
                          {getChannelIcon(channel)}
                          {getChannelDisplayName(channel)}
                        </Box>
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <FormControl fullWidth margin="normal">
                  <InputLabel>Recipient Type</InputLabel>
                  <Select
                    value={bulkForm.recipient_type}
                    label="Recipient Type"
                    onChange={(e) => setBulkForm({ 
                      ...bulkForm, 
                      recipient_type: e.target.value as any,
                      recipient_ids: [],
                      segment_name: ''
                    })}
                  >
                    <MenuItem value="customers">Select Customers</MenuItem>
                    <MenuItem value="segment">Customer Segment</MenuItem>
                    <MenuItem value="users">Select Users</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              {bulkForm.recipient_type === 'customers' && (
                <Grid item xs={12}>
                  <Autocomplete
                    multiple
                    options={customers}
                    getOptionLabel={(option) => option.name}
                    value={customers.filter(c => bulkForm.recipient_ids.includes(c.id))}
                    onChange={(_, value) => setBulkForm({ 
                      ...bulkForm, 
                      recipient_ids: value.map(v => v.id) 
                    })}
                    renderInput={(params) => (
                      <TextField 
                        {...params} 
                        label="Select Customers"
                        margin="normal"
                        fullWidth
                      />
                    )}
                    renderTags={(value, getTagProps) =>
                      value.map((option, index) => (
                        <Chip
                          variant="outlined"
                          label={option.name}
                          {...getTagProps({ index })}
                          key={option.id}
                        />
                      ))
                    }
                  />
                </Grid>
              )}
              {bulkForm.recipient_type === 'segment' && (
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Segment Name"
                    value={bulkForm.segment_name}
                    onChange={(e) => setBulkForm({ ...bulkForm, segment_name: e.target.value })}
                    margin="normal"
                    helperText="e.g., 'vip', 'premium', 'new_customers'"
                  />
                </Grid>
              )}
              {bulkForm.recipient_type === 'users' && (
                <Grid item xs={12}>
                  <Autocomplete
                    multiple
                    options={users}
                    getOptionLabel={(option) => option.name}
                    value={users.filter(u => bulkForm.recipient_ids.includes(u.id))}
                    onChange={(_, value) => setBulkForm({ 
                      ...bulkForm, 
                      recipient_ids: value.map(v => v.id) 
                    })}
                    renderInput={(params) => (
                      <TextField 
                        {...params} 
                        label="Select Users"
                        margin="normal"
                        fullWidth
                      />
                    )}
                    renderTags={(value, getTagProps) =>
                      value.map((option, index) => (
                        <Chip
                          variant="outlined"
                          label={option.name}
                          {...getTagProps({ index })}
                          key={option.id}
                        />
                      ))
                    }
                  />
                </Grid>
              )}
              {!bulkForm.template_id && (
                <>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Subject"
                      value={bulkForm.subject}
                      onChange={(e) => setBulkForm({ ...bulkForm, subject: e.target.value })}
                      margin="normal"
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Message Content"
                      value={bulkForm.content}
                      onChange={(e) => setBulkForm({ ...bulkForm, content: e.target.value })}
                      multiline
                      rows={4}
                      margin="normal"
                      required
                    />
                  </Grid>
                </>
              )}
              <Grid item xs={12}>
                <Box display="flex" gap={2}>
                  <Button
                    variant="contained"
                    startIcon={<Send />}
                    onClick={handleSendBulk}
                    disabled={sendBulkMutation.isPending}
                  >
                    Send Bulk Notification
                  </Button>
                  {bulkForm.template_id && (
                    <Button
                      variant="outlined"
                      startIcon={<Preview />}
                      onClick={() => handlePreview(true)}
                      disabled={previewMutation.isPending}
                    >
                      Preview
                    </Button>
                  )}
                </Box>
              </Grid>
            </Grid>
          </TabPanel>
        </CardContent>
      </Card>
      {/* Preview Modal */}
      <Dialog open={isPreviewOpen} onClose={() => setIsPreviewOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>Template Preview</DialogTitle>
        <DialogContent>
          {previewContent && (
            <Box>
              <Typography variant="h6" gutterBottom>
                {previewContent.template_name} ({getChannelDisplayName(previewContent.channel)})
              </Typography>
              {previewContent.test_subject && (
                <Box mb={2}>
                  <Typography variant="subtitle2" color="text.secondary">Subject:</Typography>
                  <Typography>{previewContent.test_subject}</Typography>
                </Box>
              )}
              <Box mb={2}>
                <Typography variant="subtitle2" color="text.secondary">Content:</Typography>
                <Paper sx={{ p: 2, bgcolor: 'grey.50' }}>
                  <Typography style={{ whiteSpace: 'pre-wrap' }}>
                    {previewContent.test_content}
                  </Typography>
                </Paper>
              </Box>
              <Box>
                <Typography variant="subtitle2" color="text.secondary">Variables used:</Typography>
                <Box display="flex" gap={1} flexWrap="wrap" mt={1}>
                  {Object.entries(previewContent.variables_used).map(([key, value]) => (
                    <Chip 
                      key={key} 
                      label={`${key}: ${value}`} 
                      size="small" 
                      variant="outlined" 
                    />
                  ))}
                </Box>
              </Box>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setIsPreviewOpen(false)}>Close</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};
export default SendNotification;

// frontend/src/components/ServiceAnalytics/AnalyticsFilters.tsx
import React, { useState } from 'react';
import {
  Box,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  TextField,
  Typography,
  Autocomplete
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import {
  ReportPeriod,
  AnalyticsRequest,
  TechnicianOption,
  CustomerOption
} from '../../services/serviceAnalyticsService';
interface AnalyticsFiltersProps {
  filters: AnalyticsRequest;
  onFiltersChange: (filters: AnalyticsRequest) => void;
  technicians: TechnicianOption[];
  customers: CustomerOption[];
}
const AnalyticsFilters: React.FC<AnalyticsFiltersProps> = ({
  filters,
  onFiltersChange,
  technicians,
  customers
}) => {
  const [localFilters, setLocalFilters] = useState<AnalyticsRequest>(filters);
  const handleApplyFilters = () => {
    onFiltersChange(localFilters);
  };
  const handleResetFilters = () => {
    const resetFilters: AnalyticsRequest = {
      period: ReportPeriod.MONTH
    };
    setLocalFilters(resetFilters);
    onFiltersChange(resetFilters);
  };
  const handlePeriodChange = (period: ReportPeriod) => {
    const updatedFilters = {
      ...localFilters,
      period,
      // Clear custom dates when selecting predefined period
      start_date: period === ReportPeriod.CUSTOM ? localFilters.start_date : undefined,
      end_date: period === ReportPeriod.CUSTOM ? localFilters.end_date : undefined
    };
    setLocalFilters(updatedFilters);
  };
  const handleTechnicianChange = (technicianId: number | null) => {
    setLocalFilters({
      ...localFilters,
      technician_id: technicianId || undefined
    });
  };
  const handleCustomerChange = (customerId: number | null) => {
    setLocalFilters({
      ...localFilters,
      customer_id: customerId || undefined
    });
  };
  const selectedTechnician = technicians.find(t => t.id === localFilters.technician_id) || null;
  const selectedCustomer = customers.find(c => c.id === localFilters.customer_id) || null;
  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Box>
        <Typography variant="h6" gutterBottom>
          Analytics Filters
        </Typography>
        <Grid container spacing={3}>
          {/* Period Selection */}
          <Grid item xs={12} sm={6} md={3}>
            <FormControl fullWidth>
              <InputLabel>Period</InputLabel>
              <Select
                value={localFilters.period || ReportPeriod.MONTH}
                label="Period"
                onChange={(e) => handlePeriodChange(e.target.value as ReportPeriod)}
              >
                <MenuItem value={ReportPeriod.TODAY}>Today</MenuItem>
                <MenuItem value={ReportPeriod.WEEK}>This Week</MenuItem>
                <MenuItem value={ReportPeriod.MONTH}>This Month</MenuItem>
                <MenuItem value={ReportPeriod.QUARTER}>This Quarter</MenuItem>
                <MenuItem value={ReportPeriod.YEAR}>This Year</MenuItem>
                <MenuItem value={ReportPeriod.CUSTOM}>Custom Range</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          {/* Custom Date Range - Only show when Custom is selected */}
          {localFilters.period === ReportPeriod.CUSTOM && (
            <>
              <Grid item xs={12} sm={6} md={3}>
                <DatePicker
                  label="Start Date"
                  value={localFilters.start_date ? new Date(localFilters.start_date) : null}
                  onChange={(date) => {
                    setLocalFilters({
                      ...localFilters,
                      start_date: date ? date.toISOString().split('T')[0] : undefined
                    });
                  }}
                  renderInput={(params) => <TextField {...params} fullWidth />}
                />
              </Grid>
              <Grid item xs={12} sm={6} md={3}>
                <DatePicker
                  label="End Date"
                  value={localFilters.end_date ? new Date(localFilters.end_date) : null}
                  onChange={(date) => {
                    setLocalFilters({
                      ...localFilters,
                      end_date: date ? date.toISOString().split('T')[0] : undefined
                    });
                  }}
                  minDate={localFilters.start_date ? new Date(localFilters.start_date) : undefined}
                  renderInput={(params) => <TextField {...params} fullWidth />}
                />
              </Grid>
            </>
          )}
          {/* Technician Filter */}
          <Grid item xs={12} sm={6} md={3}>
            <Autocomplete
              options={technicians}
              getOptionLabel={(option) => option.name}
              value={selectedTechnician}
              onChange={(_, newValue) => handleTechnicianChange(newValue?.id || null)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Technician"
                  placeholder="All Technicians"
                />
              )}
              renderOption={(props, option) => (
                <li {...props}>
                  <Box>
                    <Typography variant="body2">{option.name}</Typography>
                    <Typography variant="caption" color="text.secondary">
                      {option.email}
                    </Typography>
                  </Box>
                </li>
              )}
            />
          </Grid>
          {/* Customer Filter */}
          <Grid item xs={12} sm={6} md={3}>
            <Autocomplete
              options={customers}
              getOptionLabel={(option) => option.name}
              value={selectedCustomer}
              onChange={(_, newValue) => handleCustomerChange(newValue?.id || null)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Customer"
                  placeholder="All Customers"
                />
              )}
              renderOption={(props, option) => (
                <li {...props}>
                  <Box>
                    <Typography variant="body2">{option.name}</Typography>
                    <Typography variant="caption" color="text.secondary">
                      {option.email}
                    </Typography>
                  </Box>
                </li>
              )}
            />
          </Grid>
          {/* Action Buttons */}
          <Grid item xs={12}>
            <Box display="flex" gap={2} justifyContent="flex-end">
              <Button 
                variant="outlined" 
                onClick={handleResetFilters}
              >
                Reset
              </Button>
              <Button 
                variant="contained" 
                onClick={handleApplyFilters}
              >
                Apply Filters
              </Button>
            </Box>
          </Grid>
        </Grid>
        {/* Active Filters Summary */}
        {(localFilters.technician_id || localFilters.customer_id || localFilters.period === ReportPeriod.CUSTOM) && (
          <Box mt={2}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Active Filters:
            </Typography>
            <Box display="flex" flexWrap="wrap" gap={1}>
              {selectedTechnician && (
                <Box component="span" px={1} py={0.5} bgcolor="primary.light" borderRadius={1}>
                  <Typography variant="caption" color="primary.contrastText">
                    Technician: {selectedTechnician.name}
                  </Typography>
                </Box>
              )}
              {selectedCustomer && (
                <Box component="span" px={1} py={0.5} bgcolor="secondary.light" borderRadius={1}>
                  <Typography variant="caption" color="secondary.contrastText">
                    Customer: {selectedCustomer.name}
                  </Typography>
                </Box>
              )}
              {localFilters.period === ReportPeriod.CUSTOM && localFilters.start_date && localFilters.end_date && (
                <Box component="span" px={1} py={0.5} bgcolor="info.light" borderRadius={1}>
                  <Typography variant="caption" color="info.contrastText">
                    Custom Range: {localFilters.start_date} to {localFilters.end_date}
                  </Typography>
                </Box>
              )}
            </Box>
          </Box>
        )}
      </Box>
    </LocalizationProvider>
  );
};
export default AnalyticsFilters;

// frontend/src/components/ServiceAnalytics/CustomerSatisfactionChart.tsx
import React from 'react';
import {
  Card,
  CardContent,
  CardHeader,
  Box,
  Typography,
  Grid,
  LinearProgress,
  Chip,
  Paper,
  Rating
} from '@mui/material';
import {
  SentimentVerySatisfied as VeryHappyIcon,
  SentimentSatisfied as HappyIcon,
  SentimentNeutral as NeutralIcon,
  SentimentDissatisfied as UnhappyIcon,
  ThumbUp as RecommendIcon,
  TrendingUp as TrendIcon
} from '@mui/icons-material';
import { CustomerSatisfactionMetrics } from '../../services/serviceAnalyticsService';
interface CustomerSatisfactionChartProps {
  data: CustomerSatisfactionMetrics;
}
const CustomerSatisfactionChart: React.FC<CustomerSatisfactionChartProps> = ({ data }) => {
  const getSatisfactionColor = (level: string) => {
    switch (level.toLowerCase()) {
      case 'satisfied':
        return 'success';
      case 'neutral':
        return 'warning';
      case 'dissatisfied':
        return 'error';
      default:
        return 'info';
    }
  };
  const getSatisfactionIcon = (level: string) => {
    switch (level.toLowerCase()) {
      case 'satisfied':
        return <VeryHappyIcon />;
      case 'neutral':
        return <NeutralIcon />;
      case 'dissatisfied':
        return <UnhappyIcon />;
      default:
        return <HappyIcon />;
    }
  };
  const getRatingColor = (rating: number) => {
    if (rating >= 4) {return 'success.main';}
    if (rating >= 3) {return 'warning.main';}
    return 'error.main';
  };
  const formatRating = (rating?: number) => {
    return rating ? rating.toFixed(1) : 'N/A';
  };
  return (
    <Card>
      <CardHeader 
        title="Customer Satisfaction" 
        subheader={`${data.total_feedback_received} feedback responses analyzed`}
      />
      <CardContent>
        {/* Overall Rating */}
        <Box sx={{ mb: 3, textAlign: 'center' }}>
          <Typography variant="h3" sx={{ color: getRatingColor(data.average_overall_rating) }}>
            {formatRating(data.average_overall_rating)}
          </Typography>
          <Rating 
            value={data.average_overall_rating} 
            readOnly 
            precision={0.1}
