// frontend/src/components/ServiceAnalytics/SLAComplianceChart.tsx
import React from 'react';
import {
  Card,
  CardContent,
  CardHeader,
  Box,
  Typography,
  Grid,
  LinearProgress,
  Chip,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from '@mui/material';
import {
  CheckCircle as ComplianceIcon,
  Warning as BreachIcon,
  Speed as PerformanceIcon,
  Error as ErrorIcon,
  Schedule as TimeIcon
} from '@mui/icons-material';
import { SLAComplianceMetrics } from '../../services/serviceAnalyticsService';
interface SLAComplianceChartProps {
  data: SLAComplianceMetrics;
}
const SLAComplianceChart: React.FC<SLAComplianceChartProps> = ({ data }) => {
  const getComplianceColor = (rate: number) => {
    if (rate >= 95) {return 'success';}
    if (rate >= 85) {return 'info';}
    if (rate >= 70) {return 'warning';}
    return 'error';
  };
  const getPriorityColor = (priority: string) => {
    switch (priority.toLowerCase()) {
      case 'urgent':
        return 'error';
      case 'high':
        return 'warning';
      case 'medium':
        return 'info';
      case 'low':
        return 'success';
      default:
        return 'default';
    }
  };
  const formatTime = (hours?: number) => {
    if (!hours) {return 'N/A';}
    if (hours < 1) {return `${(hours * 60).toFixed(0)}m`;}
    if (hours < 24) {return `${hours.toFixed(1)}h`;}
    return `${(hours / 24).toFixed(1)}d`;
  };
  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric' 
    });
  };
  return (
    <Card>
      <CardHeader 
        title="SLA Compliance" 
        subheader={`${data.total_jobs_with_sla} jobs with SLA requirements analyzed`}
      />
      <CardContent>
        {data.total_jobs_with_sla === 0 ? (
          <Box textAlign="center" py={4}>
            <Typography variant="body1" color="text.secondary">
              No SLA compliance data available for the selected period.
            </Typography>
            <Typography variant="body2" color="text.secondary">
              SLA metrics will appear here once SLA policies are configured and jobs are tracked.
            </Typography>
          </Box>
        ) : (
          <>
            {/* Overall Compliance Rate */}
            <Box sx={{ mb: 3, textAlign: 'center' }}>
              <Typography variant="h3" sx={{ color: `${getComplianceColor(data.overall_compliance_rate)}.main` }}>
                {data.overall_compliance_rate.toFixed(1)}%
              </Typography>
              <Typography variant="h6" color="text.secondary" gutterBottom>
                Overall SLA Compliance Rate
              </Typography>
              <LinearProgress
                variant="determinate"
                value={data.overall_compliance_rate}
                sx={{ height: 12, borderRadius: 6, maxWidth: 300, mx: 'auto' }}
                color={getComplianceColor(data.overall_compliance_rate)}
              />
            </Box>
            {/* Key Metrics Summary */}
            <Grid container spacing={2} sx={{ mb: 3 }}>
              <Grid item xs={6} sm={3}>
                <Paper elevation={1} sx={{ p: 2, textAlign: 'center', bgcolor: 'background.default' }}>
                  <Typography variant="h5" color="success.main">
                    {data.sla_met_count}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    SLA Met
                  </Typography>
                </Paper>
              </Grid>
              <Grid item xs={6} sm={3}>
                <Paper elevation={1} sx={{ p: 2, textAlign: 'center', bgcolor: 'background.default' }}>
                  <Typography variant="h5" color="error.main">
                    {data.sla_breached_count}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    SLA Breached
                  </Typography>
                </Paper>
              </Grid>
              <Grid item xs={6} sm={3}>
                <Paper elevation={1} sx={{ p: 2, textAlign: 'center', bgcolor: 'background.default' }}>
                  <Typography variant="h5" color="primary.main">
                    {data.total_jobs_with_sla}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    Total with SLA
                  </Typography>
                </Paper>
              </Grid>
              <Grid item xs={6} sm={3}>
                <Paper elevation={1} sx={{ p: 2, textAlign: 'center', bgcolor: 'background.default' }}>
                  <Typography variant="h5" color="info.main">
                    {formatTime(data.average_resolution_time_hours)}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    Avg Resolution
                  </Typography>
                </Paper>
              </Grid>
            </Grid>
            {/* Compliance by Priority */}
            {Object.keys(data.compliance_by_priority).length > 0 && (
              <Box sx={{ mb: 3 }}>
                <Typography variant="h6" gutterBottom>
                  Compliance by Priority
                </Typography>
                <Grid container spacing={1}>
                  {Object.entries(data.compliance_by_priority).map(([priority, rate]) => (
                    <Grid item xs={12} sm={6} md={3} key={priority}>
                      <Paper 
                        elevation={1} 
                        sx={{ 
                          p: 2, 
                          bgcolor: 'background.default'
                        }}
                      >
                        <Box display="flex" justifyContent="space-between" alignItems="center" mb={1}>
                          <Typography variant="body2" sx={{ textTransform: 'capitalize' }}>
                            {priority}
                          </Typography>
                          <Chip
                            size="small"
                            label={`${rate.toFixed(1)}%`}
                            color={getComplianceColor(rate)}
                          />
                        </Box>
                        <LinearProgress
                          variant="determinate"
                          value={rate}
                          sx={{ height: 6, borderRadius: 3 }}
                          color={getComplianceColor(rate)}
                        />
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            )}
            {/* SLA Breach Reasons */}
            {Object.keys(data.breach_reasons).length > 0 && (
              <Box sx={{ mb: 3 }}>
                <Typography variant="h6" gutterBottom>
                  Common Breach Reasons
                </Typography>
                <TableContainer component={Paper} elevation={1}>
                  <Table size="small">
                    <TableHead>
                      <TableRow>
                        <TableCell>Reason</TableCell>
                        <TableCell align="center">Occurrences</TableCell>
                        <TableCell align="center">% of Breaches</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {Object.entries(data.breach_reasons)
                        .sort(([, a], [, b]) => b - a)
                        .map(([reason, count]) => (
                          <TableRow key={reason}>
                            <TableCell>
                              <Box display="flex" alignItems="center" gap={1}>
                                <ErrorIcon fontSize="small" color="error" />
                                <Typography variant="body2" sx={{ textTransform: 'capitalize' }}>
                                  {reason.replace('_', ' ')}
                                </Typography>
                              </Box>
                            </TableCell>
                            <TableCell align="center">
                              <Chip 
                                label={count}
                                size="small"
                                color="error"
                                variant="outlined"
                              />
                            </TableCell>
                            <TableCell align="center">
                              <Typography variant="body2" color="text.secondary">
                                {data.sla_breached_count > 0 
                                  ? ((count / data.sla_breached_count) * 100).toFixed(1)
                                  : 0}%
                              </Typography>
                            </TableCell>
                          </TableRow>
                        ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </Box>
            )}
            {/* Compliance Trend Preview */}
            {data.compliance_trend.length > 0 && (
              <Box sx={{ mb: 3 }}>
                <Typography variant="h6" gutterBottom>
                  Recent Compliance Trend
                </Typography>
                <Paper elevation={1} sx={{ p: 2, bgcolor: 'background.default' }}>
                  <Typography variant="body2" color="text.secondary" gutterBottom>
                    Daily SLA compliance rates
                  </Typography>
                  <Box display="flex" gap={1} flexWrap="wrap">
                    {data.compliance_trend.slice(-7).map((point, index) => (
                      <Chip
                        key={index}
                        label={`${formatDate(point.date)}: ${point.value.toFixed(1)}%`}
                        size="small"
                        variant="outlined"
                        color={getComplianceColor(point.value)}
                        icon={point.value >= 85 ? <ComplianceIcon /> : <BreachIcon />}
                      />
                    ))}
                  </Box>
                  {/* Trend Analysis */}
                  <Box mt={2}>
                    <Typography variant="body2" color="text.secondary">
                      Trend Analysis:
                    </Typography>
                    {(() => {
                      const recentTrend = data.compliance_trend.slice(-7);
                      const avgRecent = recentTrend.reduce((sum, point) => sum + point.value, 0) / recentTrend.length;
                      const trendDirection = avgRecent > data.overall_compliance_rate ? 'improving' : 'declining';
                      const trendColor = avgRecent > data.overall_compliance_rate ? 'success.main' : 'warning.main';
                      return (
                        <Typography variant="body2" sx={{ color: trendColor }}>
                          Recent 7-day average ({avgRecent.toFixed(1)}%) shows {trendDirection} trend
                        </Typography>
                      );
                    })()}
                  </Box>
                </Paper>
              </Box>
            )}
            {/* Performance Indicators */}
            <Box>
              <Typography variant="h6" gutterBottom>
                Performance Indicators
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <Paper elevation={1} sx={{ p: 2, bgcolor: 'background.default' }}>
                    <Box display="flex" alignItems="center" gap={2}>
                      <PerformanceIcon color={getComplianceColor(data.overall_compliance_rate)} />
                      <Box>
                        <Typography variant="body2" color="text.secondary">
                          Compliance Status
                        </Typography>
                        <Typography variant="body1" fontWeight="medium">
                          {data.overall_compliance_rate >= 95 ? 'Excellent' :
                           data.overall_compliance_rate >= 85 ? 'Good' :
                           data.overall_compliance_rate >= 70 ? 'Needs Improvement' : 'Critical'}
                        </Typography>
                      </Box>
                    </Box>
                  </Paper>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <Paper elevation={1} sx={{ p: 2, bgcolor: 'background.default' }}>
                    <Box display="flex" alignItems="center" gap={2}>
                      <TimeIcon color="action" />
                      <Box>
                        <Typography variant="body2" color="text.secondary">
                          Resolution Performance
                        </Typography>
                        <Typography variant="body1" fontWeight="medium">
                          {data.average_resolution_time_hours 
                            ? `${formatTime(data.average_resolution_time_hours)} average`
                            : 'No data available'}
                        </Typography>
                      </Box>
                    </Box>
                  </Paper>
                </Grid>
              </Grid>
            </Box>
          </>
        )}
      </CardContent>
    </Card>
  );
};
export default SLAComplianceChart;

// frontend/src/components/ServiceAnalytics/ServiceAnalyticsDashboard.tsx
import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  CardHeader,
  Grid,
  Typography,
  CircularProgress,
  Alert,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Button,
  TextField,
  IconButton,
  Tooltip,
  Chip
} from '@mui/material';
import {
  Refresh as RefreshIcon,
  FilterList as FilterIcon,
  Download as DownloadIcon,
  DateRange as DateRangeIcon
} from '@mui/icons-material';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import { 
  serviceAnalyticsService, 
  ReportPeriod, 
  AnalyticsDashboard,
  AnalyticsRequest,
  TechnicianOption,
  CustomerOption
} from '../../services/serviceAnalyticsService';
import { useAuth } from '../../hooks/useAuth';
import JobCompletionChart from './JobCompletionChart';
import TechnicianPerformanceChart from './TechnicianPerformanceChart';
import CustomerSatisfactionChart from './CustomerSatisfactionChart';
import JobVolumeChart from './JobVolumeChart';
import SLAComplianceChart from './SLAComplianceChart';
import AnalyticsFilters from './AnalyticsFilters';
interface ServiceAnalyticsDashboardProps {
  organizationId?: number;
}
const ServiceAnalyticsDashboard: React.FC<ServiceAnalyticsDashboardProps> = ({
  organizationId: propOrganizationId
}) => {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  // Use the organization ID from props or fall back to the user's organization
  const organizationId = propOrganizationId || user?.organization_id;
  // State for filters
  const [filters, setFilters] = useState<AnalyticsRequest>({
    period: ReportPeriod.MONTH
  });
  const [showFilters, setShowFilters] = useState(false);
  // Query for dashboard data
  const {
    data: dashboardData,
    isLoading,
    error,
    refetch
  } = useQuery({
    queryKey: ['service-analytics-dashboard', organizationId, filters],
    queryFn: () => serviceAnalyticsService.getAnalyticsDashboard(organizationId!, filters),
    enabled: !!organizationId,
    refetchInterval: 5 * 60 * 1000, // Refetch every 5 minutes
    staleTime: 2 * 60 * 1000 // Consider data stale after 2 minutes
  });
  // Query for filter options
  const { data: technicians } = useQuery({
    queryKey: ['analytics-technicians', organizationId],
    queryFn: () => serviceAnalyticsService.getAvailableTechnicians(organizationId!),
    enabled: !!organizationId
  });
  const { data: customers } = useQuery({
    queryKey: ['analytics-customers', organizationId],
    queryFn: () => serviceAnalyticsService.getAvailableCustomers(organizationId!),
    enabled: !!organizationId
  });
  const handleFilterChange = (newFilters: AnalyticsRequest) => {
    setFilters(newFilters);
  };
  const handleRefresh = () => {
    queryClient.invalidateQueries({ queryKey: ['service-analytics-dashboard'] });
    refetch();
  };
  const handleExport = async () => {
    if (!organizationId) {return;}
    try {
      const blob = await serviceAnalyticsService.exportAnalyticsData(organizationId, {
        format: 'csv',
        metric_types: ['job_completion', 'technician_performance', 'customer_satisfaction', 'job_volume'],
        filters: filters,
        include_raw_data: false
      });
      // Create download link
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `service-analytics-${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Failed to export analytics data:', error);
    }
  };
  if (!organizationId) {
    return (
      <Alert severity="error">
        No organization ID available. Please ensure you are logged in.
      </Alert>
    );
  }
  if (isLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress size={60} />
        <Typography variant="h6" sx={{ ml: 2 }}>
          Loading analytics dashboard...
        </Typography>
      </Box>
    );
  }
  if (error) {
    return (
      <Alert severity="error" action={
        <Button color="inherit" size="small" onClick={handleRefresh}>
          Retry
        </Button>
      }>
        Failed to load analytics dashboard: {(error as Error).message}
      </Alert>
    );
  }
  if (!dashboardData) {
    return (
      <Alert severity="info">
        No analytics data available for the selected period.
      </Alert>
    );
  }
  return (
    <Box>
      {/* Header */}
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          Service Analytics Dashboard
        </Typography>
        <Box display="flex" gap={1}>
          <Tooltip title="Toggle Filters">
            <IconButton 
              onClick={() => setShowFilters(!showFilters)}
              color={showFilters ? 'primary' : 'default'}
            >
              <FilterIcon />
            </IconButton>
          </Tooltip>
          <Tooltip title="Export Data">
            <IconButton onClick={handleExport}>
              <DownloadIcon />
            </IconButton>
          </Tooltip>
          <Tooltip title="Refresh Data">
            <IconButton onClick={handleRefresh}>
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        </Box>
      </Box>
      {/* Filters */}
      {showFilters && (
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <AnalyticsFilters
              filters={filters}
              onFiltersChange={handleFilterChange}
              technicians={technicians || []}
              customers={customers || []}
            />
          </CardContent>
        </Card>
      )}
      {/* Dashboard Info */}
      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Box display="flex" flexWrap="wrap" gap={2} alignItems="center">
            <Chip 
              icon={<DateRangeIcon />}
              label={`Period: ${dashboardData.report_period.toUpperCase()}`}
              color="primary"
            />
            <Chip 
              label={`${dashboardData.start_date} to ${dashboardData.end_date}`}
              variant="outlined"
            />
            <Chip 
              label={`Generated: ${new Date(dashboardData.generated_at).toLocaleString()}`}
              variant="outlined"
              size="small"
            />
          </Box>
        </CardContent>
      </Card>
      {/* Analytics Charts Grid */}
      <Grid container spacing={3}>
        {/* Job Completion Metrics */}
        <Grid item xs={12} md={6}>
          <JobCompletionChart data={dashboardData.job_completion} />
        </Grid>
        {/* Customer Satisfaction Metrics */}
        <Grid item xs={12} md={6}>
          <CustomerSatisfactionChart data={dashboardData.customer_satisfaction} />
        </Grid>
        {/* Job Volume Chart */}
        <Grid item xs={12} md={6}>
          <JobVolumeChart data={dashboardData.job_volume} />
        </Grid>
        {/* SLA Compliance Chart */}
        <Grid item xs={12} md={6}>
          <SLAComplianceChart data={dashboardData.sla_compliance} />
        </Grid>
        {/* Technician Performance - Full width if data available */}
        {dashboardData.technician_performance.length > 0 && (
          <Grid item xs={12}>
            <TechnicianPerformanceChart data={dashboardData.technician_performance} />
          </Grid>
        )}
      </Grid>
      {/* Summary Statistics */}
      <Card sx={{ mt: 3 }}>
        <CardHeader title="Summary Statistics" />
        <CardContent>
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6} md={3}>
              <Box textAlign="center">
                <Typography variant="h4" color="primary">
                  {dashboardData.job_completion.total_jobs}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Total Jobs
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <Box textAlign="center">
