// SortableTable component for requirement #3 - Table Sorting on Header Click
import React, { useState, useMemo } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TableSortLabel,
  Paper,
  Box,
  Typography
} from '@mui/material';
import { visuallyHidden } from '@mui/utils';
export type Order = 'asc' | 'desc';
export interface HeadCell<T> {
  id: keyof T;
  label: string;
  numeric: boolean;
  disablePadding?: boolean;
  sortable?: boolean;
  width?: string | number;
  align?: 'left' | 'right' | 'center';
  render?: (value: any, row: T) => React.ReactNode;
}
interface SortableTableProps<T> {
  data: T[];
  headCells: HeadCell<T>[];
  title?: string;
  defaultOrderBy?: keyof T;
  defaultOrder?: Order;
  onRowClick?: (row: T) => void;
  dense?: boolean;
  stickyHeader?: boolean;
  maxHeight?: string | number;
  emptyMessage?: string;
  loading?: boolean;
  actions?: (row: T) => React.ReactNode;
}
function descendingComparator<T>(a: T, b: T, orderBy: keyof T) {
  const aVal = a[orderBy];
  const bVal = b[orderBy];
  // Handle null/undefined values
  if (bVal == null && aVal == null) {return 0;}
  if (bVal == null) {return -1;}
  if (aVal == null) {return 1;}
  // Handle different types
  if (typeof aVal === 'number' && typeof bVal === 'number') {
    return bVal - aVal;
  }
  if (typeof aVal === 'string' && typeof bVal === 'string') {
    return bVal.localeCompare(aVal, undefined, { numeric: true, sensitivity: 'base' });
  }
  // Handle dates
  if (aVal instanceof Date && bVal instanceof Date) {
    return bVal.getTime() - aVal.getTime();
  }
  // Handle date strings
  if (typeof aVal === 'string' && typeof bVal === 'string') {
    const aDate = new Date(aVal);
    const bDate = new Date(bVal);
    if (!isNaN(aDate.getTime()) && !isNaN(bDate.getTime())) {
      return bDate.getTime() - aDate.getTime();
    }
  }
  // Default string comparison
  return String(bVal).localeCompare(String(aVal), undefined, { numeric: true, sensitivity: 'base' });
}
function getComparator<T>(order: Order, orderBy: keyof T): (a: T, b: T) => number {
  return order === 'desc'
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}
function stableSort<T>(array: readonly T[], comparator: (a: T, b: T) => number) {
  const stabilizedThis = array.map((el, index) => [el, index] as [T, number]);
  stabilizedThis.sort((a, b) => {
    const order = comparator(a[0], b[0]);
    if (order !== 0) {
      return order;
    }
    return a[1] - b[1];
  });
  return stabilizedThis.map((el) => el[0]);
}
interface SortableTableHeadProps<T> {
  headCells: HeadCell<T>[];
  order: Order;
  orderBy: keyof T;
  onRequestSort: (property: keyof T) => void;
  hasActions: boolean;
}
function SortableTableHead<T>(props: SortableTableHeadProps<T>) {
  const { headCells, order, orderBy, onRequestSort, hasActions } = props;
  const createSortHandler = (property: keyof T) => () => {
    onRequestSort(property);
  };
  return (
    <TableHead>
      <TableRow>
        {headCells.map((headCell) => (
          <TableCell
            key={String(headCell.id)}
            align={headCell.align || (headCell.numeric ? 'right' : 'left')}
            padding={headCell.disablePadding ? 'none' : 'normal'}
            sortDirection={orderBy === headCell.id ? order : false}
            sx={{ 
              fontWeight: 'bold',
              width: headCell.width,
              backgroundColor: 'grey.50'
            }}
          >
            {headCell.sortable !== false ? (
              <TableSortLabel
                active={orderBy === headCell.id}
                direction={orderBy === headCell.id ? order : 'asc'}
                onClick={createSortHandler(headCell.id)}
              >
                {headCell.label}
                {orderBy === headCell.id ? (
                  <Box component="span" sx={visuallyHidden}>
                    {order === 'desc' ? 'sorted descending' : 'sorted ascending'}
                  </Box>
                ) : null}
              </TableSortLabel>
            ) : (
              headCell.label
            )}
          </TableCell>
        ))}
        {hasActions && (
          <TableCell align="center" sx={{ fontWeight: 'bold', backgroundColor: 'grey.50' }}>
            Actions
          </TableCell>
        )}
      </TableRow>
    </TableHead>
  );
}
function SortableTable<T>({
  data,
  headCells,
  title,
  defaultOrderBy,
  defaultOrder = 'asc',
  onRowClick,
  dense = false,
  stickyHeader = false,
  maxHeight,
  emptyMessage = 'No data available',
  loading = false,
  actions
}: SortableTableProps<T>) {
  const [order, setOrder] = useState<Order>(defaultOrder);
  const [orderBy, setOrderBy] = useState<keyof T>(defaultOrderBy || headCells[0]?.id);
  const handleRequestSort = (property: keyof T) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };
  const sortedData = useMemo(() => {
    if (!data?.length) {return [];}
    return stableSort(data, getComparator(order, orderBy));
  }, [data, order, orderBy]);
  const hasActions = Boolean(actions);
  if (loading) {
    return (
      <Paper sx={{ p: 3, textAlign: 'center' }}>
        <Typography>Loading...</Typography>
      </Paper>
    );
  }
  return (
    <Paper sx={{ width: '100%', mb: 2 }}>
      {title && (
        <Box sx={{ p: 2 }}>
          <Typography variant="h6" component="div">
            {title}
          </Typography>
        </Box>
      )}
      <TableContainer sx={{ maxHeight }}>
        <Table
          stickyHeader={stickyHeader}
          size={dense ? 'small' : 'medium'}
          aria-label="sortable table"
        >
          <SortableTableHead
            headCells={headCells}
            order={order}
            orderBy={orderBy}
            onRequestSort={handleRequestSort}
            hasActions={hasActions}
          />
          <TableBody>
            {sortedData.length === 0 ? (
              <TableRow>
                <TableCell 
                  colSpan={headCells.length + (hasActions ? 1 : 0)} 
                  align="center"
                  sx={{ py: 3 }}
                >
                  <Typography color="textSecondary">
                    {emptyMessage}
                  </Typography>
                </TableCell>
              </TableRow>
            ) : (
              sortedData.map((row, index) => (
                <TableRow
                  hover={Boolean(onRowClick)}
                  onClick={onRowClick ? () => onRowClick(row) : undefined}
                  key={index}
                  sx={{ 
                    cursor: onRowClick ? 'pointer' : 'default',
                    '&:hover': onRowClick ? { backgroundColor: 'action.hover' } : {}
                  }}
                >
                  {headCells.map((headCell) => (
                    <TableCell
                      key={String(headCell.id)}
                      align={headCell.align || (headCell.numeric ? 'right' : 'left')}
                      padding={headCell.disablePadding ? 'none' : 'normal'}
                    >
                      {headCell.render 
                        ? headCell.render(row[headCell.id], row)
                        : String(row[headCell.id] ?? '')
                      }
                    </TableCell>
                  ))}
                  {hasActions && (
                    <TableCell align="center">
                      {actions!(row)}
                    </TableCell>
                  )}
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
}
export default SortableTable;

// frontend/src/components/StickyNotes/StickyNote.tsx
import React, { useState, useEffect } from 'react';
import Draggable from 'react-draggable';
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  TextField,
  IconButton,
  Box,
  Menu,
  MenuItem,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button
} from '@mui/material';
import {
  Edit,
  Delete,
  Save,
  Cancel,
  MoreVert,
  Palette,
  PushPin,
  PushPinOutlined
} from '@mui/icons-material';
interface StickyNoteProps {
  id: number;
  title: string;
  content: string;
  color: string;
  created_at: string;
  position?: { x: number; y: number };
  pinned?: boolean;
  onUpdate: (id: number, data: { title?: string; content?: string; color?: string; position?: { x: number; y: number }; pinned?: boolean }) => Promise<void>;
  onDelete: (id: number) => Promise<void>;
}
const COLORS = [
  { name: 'yellow', color: '#fff59d', border: '#fff176' },
  { name: 'blue', color: '#81d4fa', border: '#4fc3f7' },
  { name: 'green', color: '#a5d6a7', border: '#81c784' },
  { name: 'pink', color: '#f8bbd9', border: '#f48fb1' },
  { name: 'purple', color: '#ce93d8', border: '#ba68c8' },
  { name: 'orange', color: '#ffcc80', border: '#ffb74d' }
];
const StickyNote: React.FC<StickyNoteProps> = ({
  id,
  title,
  content,
  color,
  created_at,
  position = { x: 0, y: 0 },
  pinned = false,
  onUpdate,
  onDelete
}) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(title);
  const [editContent, setEditContent] = useState(content);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const [colorMenuAnchor, setColorMenuAnchor] = useState<null | HTMLElement>(null);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const colorConfig = COLORS.find(c => c.name === color) || COLORS[0];
  const handleEdit = () => {
    setIsEditing(true);
    setAnchorEl(null);
  };
  const handleSave = async () => {
    if (!editTitle.trim() || !editContent.trim()) {
      return;
    }
    setLoading(true);
    try {
      await onUpdate(id, {
        title: editTitle.trim(),
        content: editContent.trim()
      });
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating note:', error);
    } finally {
      setLoading(false);
    }
  };
  const handleCancel = () => {
    setEditTitle(title);
    setEditContent(content);
    setIsEditing(false);
  };
  const handleColorChange = async (newColor: string) => {
    setLoading(true);
    try {
      await onUpdate(id, { color: newColor });
      setColorMenuAnchor(null);
    } catch (error) {
      console.error('Error updating note color:', error);
    } finally {
      setLoading(false);
    }
  };
  const handleTogglePin = async () => {
    setLoading(true);
    try {
      await onUpdate(id, { pinned: !pinned });
    } catch (error) {
      console.error('Error toggling pin:', error);
    } finally {
      setLoading(false);
    }
  };
  const handleDelete = async () => {
    setLoading(true);
    try {
      await onDelete(id);
      setDeleteDialogOpen(false);
    } catch (error) {
      console.error('Error deleting note:', error);
    } finally {
      setLoading(false);
    }
  };
  const handleDragStop = (e: any, data: any) => {
    const newPosition = { x: data.x, y: data.y };
    onUpdate(id, { position: newPosition }).catch(error => {
      console.error('Error saving note position:', error);
    });
  };
  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };
  return (
    <>
      <Draggable
        bounds="body"
        defaultPosition={position}
        onStop={handleDragStop}
        disabled={isEditing || pinned} // Disable drag if editing or pinned
      >
        <Card
          sx={{
            width: pinned ? '2in' : '2.5in',
            minHeight: pinned ? '2in' : '2.5in',
            backgroundColor: colorConfig.color,
            border: `2px solid ${colorConfig.border}`,
            borderRadius: 2,
            boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
            transition: 'opacity var(--transition-normal)',
            opacity: 0.9,
            zIndex: 1400,
            '&:hover': {
              opacity: 1,
              boxShadow: '0 6px 16px rgba(0,0,0,0.15)',
              transform: 'translateY(-2px)'
            }
          }}
        >
          <CardContent sx={{ pb: 1 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 1 }}>
              {isEditing ? (
                <TextField
                  fullWidth
                  value={editTitle}
                  onChange={(e) => setEditTitle(e.target.value)}
                  variant="standard"
                  placeholder="Note title..."
                  InputProps={{
                    disableUnderline: true,
                    sx: {
                      fontSize: '1.1rem',
                      fontWeight: 600,
                      backgroundColor: 'rgba(255,255,255,0.3)',
                      borderRadius: 1,
                      px: 1,
                      py: 0.5
                    }
                  }}
                  autoFocus
                />
              ) : (
                <Typography variant="h6" sx={{ fontWeight: 600, color: 'rgba(0,0,0,0.8)' }}>
                  {title}
                </Typography>
              )}
              {!isEditing && (
                <Box>
                  <IconButton
                    size="small"
                    onClick={handleTogglePin}
                    sx={{ color: 'rgba(0,0,0,0.6)', mr: 1 }}
                  >
                    {pinned ? <PushPin fontSize="small" /> : <PushPinOutlined fontSize="small" />}
                  </IconButton>
                  <IconButton
                    size="small"
                    onClick={(e) => setAnchorEl(e.currentTarget)}
                    sx={{ color: 'rgba(0,0,0,0.6)' }}
                  >
                    <MoreVert fontSize="small" />
                  </IconButton>
                </Box>
              )}
            </Box>
            {isEditing ? (
              <TextField
                fullWidth
                multiline
                rows={4}
                value={editContent}
                onChange={(e) => setEditContent(e.target.value)}
                variant="outlined"
                placeholder="Write your note content here..."
                sx={{
                  '& .MuiOutlinedInput-root': {
                    backgroundColor: 'rgba(255,255,255,0.3)',
                    '& fieldset': { border: 'none' }
                  }
                }}
              />
            ) : (
              <Typography variant="body2" sx={{ color: 'rgba(0,0,0,0.7)', whiteSpace: 'pre-wrap' }}>
                {content}
              </Typography>
            )}
          </CardContent>
          <CardActions sx={{ justifyContent: 'space-between', px: 2, pb: 2 }}>
            <Chip
              label={formatDate(created_at)}
              size="small"
              sx={{
                backgroundColor: 'rgba(255,255,255,0.4)',
                color: 'rgba(0,0,0,0.6)',
                fontSize: '0.7rem'
              }}
            />
            {isEditing && (
              <Box>
                <IconButton
                  size="small"
                  onClick={handleCancel}
                  disabled={loading}
                  sx={{ color: 'rgba(0,0,0,0.6)', mr: 1 }}
                >
                  <Cancel fontSize="small" />
                </IconButton>
                <IconButton
                  size="small"
                  onClick={handleSave}
                  disabled={loading || !editTitle.trim() || !editContent.trim()}
                  sx={{ color: 'green' }}
                >
                  <Save fontSize="small" />
                </IconButton>
              </Box>
            )}
          </CardActions>
        </Card>
      </Draggable>
      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={() => setAnchorEl(null)}
      >
        <MenuItem onClick={handleEdit}>
          <Edit fontSize="small" sx={{ mr: 1 }} />
          Edit
        </MenuItem>
        <MenuItem onClick={(e) => setColorMenuAnchor(e.currentTarget)}>
          <Palette fontSize="small" sx={{ mr: 1 }} />
          Change Color
        </MenuItem>
        <MenuItem onClick={() => { setDeleteDialogOpen(true); setAnchorEl(null); }}>
          <Delete fontSize="small" sx={{ mr: 1 }} />
          Delete
        </MenuItem>
      </Menu>
      <Menu
        anchorEl={colorMenuAnchor}
        open={Boolean(colorMenuAnchor)}
        onClose={() => setColorMenuAnchor(null)}
      >
        {COLORS.map((colorOption) => (
          <MenuItem
            key={colorOption.name}
            onClick={() => handleColorChange(colorOption.name)}
            sx={{ minWidth: 120 }}
          >
            <Box
              sx={{
                width: 20,
                height: 20,
                backgroundColor: colorOption.color,
                border: `2px solid ${colorOption.border}`,
                borderRadius: 1,
                mr: 1
              }}
            />
            {colorOption.name.charAt(0).toUpperCase() + colorOption.name.slice(1)}
          </MenuItem>
        ))}
      </Menu>
      <Dialog open={deleteDialogOpen} onClose={() => setDeleteDialogOpen(false)}>
        <DialogTitle>Delete Note</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete "{title}"? This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleDelete} color="error" disabled={loading}>
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};
export default StickyNote;

// frontend/src/components/StickyNotes/StickyNotesPanel.tsx
import React, { useState, useEffect } from 'react';
import {
declare function fetchNotes(...args: any[]): any;
  Box,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem,
  CircularProgress,
  Alert,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Typography // Added import
} from '@mui/material';
import {PushPin, PushPinOutlined} from '@mui/icons-material';
import StickyNote from './StickyNote';
import { useAuth } from '../../context/AuthContext';
import { stickyNotesService } from '../../services/stickyNotesService';
import { useStickyNotes } from '../../hooks/useStickyNotes';
interface StickyNoteData {
  id: number;
  title: string;
  content: string;
  color: string;
  created_at: string;
  updated_at?: string;
  position?: { x: number; y: number };
  pinned?: boolean;
}
const COLORS = [
  { name: 'yellow', label: 'Yellow' },
  { name: 'blue', label: 'Blue' },
  { name: 'green', label: 'Green' },
  { name: 'pink', label: 'Pink' },
  { name: 'purple', label: 'Purple' },
  { name: 'orange', label: 'Orange' }
];
const StickyNotesPanel: React.FC = () => {
const  = useAuth();
  const { userSettings } = useStickyNotes();
  const [notes, setNotes] = useState<StickyNoteData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [popupOpen, setPopupOpen] = useState(false);
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [newNote, setNewNote] = useState({
    title: '',
    content: '',
    color: 'yellow',
    position: { x: window.innerWidth - 300, y: 100 },
    pinned: false
  });
  const [creating, setCreating] = useState(false);
  useEffect(() => {
    if (userSettings.sticky_notes_enabled) {
      fetchNotes();
    }
  }, [userSettings.sticky_notes_enabled]);
  const fetchNotes = async () => {
    try {
      setLoading(true);
      const data = await stickyNotesService.getNotes();
      setNotes(data);
      setError(null);
    } catch (err) {
      console.error('Error fetching notes:', err);
      setError('Failed to load sticky notes');
    } finally {
      setLoading(false);
    }
  };
  const createNote = async () => {
    if (!newNote.title.trim() || !newNote.content.trim()) {
      return;
    }
    try {
      setCreating(true);
      const createdNote = await stickyNotesService.createNote(newNote);
      setNotes(prev => [createdNote, ...prev]);
      setNewNote({ title: '', content: '', color: 'yellow', position: { x: window.innerWidth - 300, y: 100 }, pinned: false });
      setCreateDialogOpen(false);
      setError(null);
    } catch (err) {
      console.error('Error creating note:', err);
      setError('Failed to create sticky note');
    } finally {
      setCreating(false);
    }
  };
  const updateNote = async (id: number, updateData: { title?: string; content?: string; color?: string; position?: { x: number; y: number }; pinned?: boolean }) => {
    try {
      const updatedNote = await stickyNotesService.updateNote(id, updateData);
      setNotes(prev => prev.map(note => note.id === id ? updatedNote : note));
      setError(null);
    } catch (err) {
      console.error('Error updating note:', err);
      setError('Failed to update sticky note');
      throw err;
    }
  };
  const togglePin = (note: StickyNoteData) => {
    updateNote(note.id, { pinned: !note.pinned });
  };
  const deleteNote = async (id: number) => {
    try {
      await stickyNotesService.deleteNote(id);
      setNotes(prev => prev.filter(note => note.id !== id));
      setError(null);
    } catch (err) {
      console.error('Error deleting note:', err);
      setError('Failed to delete sticky note');
      throw err;
    }
  };
  // Always show icon, regardless of settings, but disable functionality if not enabled
  return (
    <>
      <Box
        className="sticky-notes-icon"
        sx={{
          position: 'fixed',
          bottom: 'var(--space-4)',
          right: 'var(--space-4)',
          width: '1in',
          height: '1in',
          zIndex: 1500,
          opacity: 0.2,
          '&:hover': {
            opacity: 1,
          },
          cursor: 'pointer'
        }}
        onClick={() => {
          if (!userSettings.sticky_notes_enabled) {
            setError('Sticky notes feature is disabled. Please enable in settings.');
            return;
          }
          setPopupOpen(true);
        }}
      >
        <svg width="100%" height="100%" viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
          <rect x="0" y="0" width="100" height="100" fill="yellow" rx="10" ry="10" />
          <polygon points="100,50 100,100 50,100" fill="#4b5563" />
          <polygon points="100,60 100,100 60,100" fill="#b45309" />
        </svg>
      </Box>
      {/* Pinned notes - rendered separately at top right */}
      {notes.filter(note => note.pinned).map((note) => (
        <Box
          key={note.id}
          sx={{
            position: 'fixed',
            top: 'var(--space-4)',
            right: 'var(--space-4)',
            zIndex: 1400,
            opacity: 0.9,
            '&:hover': { opacity: 1 }
          }}
        >
          <StickyNote
            id={note.id}
            title={note.title}
            content={note.content}
            color={note.color}
            created_at={note.created_at}
            position={{ x: 0, y: 0 }} // Fixed position
            pinned={note.pinned}
            onUpdate={updateNote}
            onDelete={deleteNote}
          />
        </Box>
      ))}
      {/* Unpinned notes - draggable as before */}
      {notes.filter(note => !note.pinned).map((note) => (
        <StickyNote
          key={note.id}
          id={note.id}
          title={note.title}
          content={note.content}
          color={note.color}
          created_at={note.created_at}
          position={note.position}
          pinned={note.pinned}
          onUpdate={updateNote}
          onDelete={deleteNote}
        />
      ))}
      {/* Popup dialog for notes list */}
      <Dialog
        open={popupOpen}
        onClose={() => setPopupOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Sticky Notes</DialogTitle>
        <DialogContent>
          {loading ? (
            <CircularProgress />
          ) : error ? (
            <Alert severity="error">{error}</Alert>
          ) : notes.length === 0 ? (
            <Typography>No notes yet. Add one!</Typography>
          ) : (
            <List>
              {notes.map((note) => (
                <ListItem key={note.id}>
                  <ListItemText primary={note.title} secondary={note.content} />
                  <ListItemSecondaryAction>
                    <IconButton onClick={() => togglePin(note)}>
                      {note.pinned ? <PushPin /> : <PushPinOutlined />}
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCreateDialogOpen(true)} variant="contained">Add Note</Button>
          <Button onClick={() => setPopupOpen(false)}>Close</Button>
        </DialogActions>
      </Dialog>
      {/* Create note dialog */}
      <Dialog
        open={createDialogOpen}
        onClose={() => setCreateDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>Create New Sticky Note</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Title"
            value={newNote.title}
            onChange={(e) => setNewNote(prev => ({ ...prev, title: e.target.value }))}
            margin="normal"
            variant="outlined"
          />
          <TextField
            fullWidth
            label="Content"
            value={newNote.content}
            onChange={(e) => setNewNote(prev => ({ ...prev, content: e.target.value }))}
            margin="normal"
            variant="outlined"
            multiline
            rows={4}
          />
          <TextField
            fullWidth
            label="Color"
            value={newNote.color}
            onChange={(e) => setNewNote(prev => ({ ...prev, color: e.target.value }))}
            margin="normal"
            variant="outlined"
            select
          >
            {COLORS.map((color) => (
              <MenuItem key={color.name} value={color.name}>
                {color.label}
              </MenuItem>
            ))}
          </TextField>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCreateDialogOpen(false)}>Cancel</Button>
          <Button
            onClick={createNote}
            variant="contained"
            disabled={creating || !newNote.title.trim() || !newNote.content.trim()}
          >
            {creating ? <CircularProgress size={20} /> : 'Create Note'}
          </Button>
        </DialogActions>
      </Dialog>
      {error && (
        <Alert severity="error" sx={{ position: 'fixed', top: 'var(--space-4)', right: 'var(--space-4)', zIndex: 1600 }}>
          {error}
        </Alert>
      )}
    </>
  );
};
export default StickyNotesPanel;

declare function handleClose(...args: any[]): any;
'use client';
// SuspendDialog component for requirement #5 - Account Suspension & License Pause
import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  TextField,
  Box,
  Alert,
  CircularProgress,
  FormControlLabel,
  Checkbox
} from '@mui/material';
import { Block as BlockIcon, PlayArrow as ActivateIcon } from '@mui/icons-material';
import { toast } from 'react-toastify';
interface SuspendDialogProps {
  open: boolean;
  onClose: () => void;
  organizationId: number;
  organizationName: string;
  currentStatus: string;
  onSuccess?: () => void;
}
const SuspendDialog: React.FC<SuspendDialogProps> = ({
  open,
  onClose,
  organizationId,
  organizationName,
  currentStatus,
  onSuccess
}) => {
  const [reason, setReason] = useState('');
  const [confirmChecked, setConfirmChecked] = useState(false);
  const [loading, setLoading] = useState(false);
  const isCurrentlySuspended = currentStatus === 'suspended';
  const action = isCurrentlySuspended ? 'activate' : 'suspend';
  const handleAction = async () => {
    if (!isCurrentlySuspended && !reason.trim()) {
      toast.error('Please provide a reason for suspension');
      return;
    }
    if (!confirmChecked) {
      toast.error('Please confirm the action');
      return;
    }
    try {
      setLoading(true);
      const endpoint = isCurrentlySuspended
        ? `/api/v1/settings/settings/organization/${organizationId}/activate`
        : `/api/v1/settings/organization/${organizationId}/suspend`;
      const body = isCurrentlySuspended 
        ? {} 
        : { reason: reason.trim() };
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify(body),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.detail || `Failed to ${action} organization`);
      }
      const result = await response.json();
      toast.success(result.message || `Organization ${action}d successfully`);
      if (onSuccess) {
        onSuccess();
      }
      handleClose();
    } catch (error) {
      console.error(`${action} error:`, error);
      toast.error(error instanceof Error ? error.message : `Failed to ${action} organization`);
    } finally {
      setLoading(false);
    }
  };
  const handleClose = () => {
    setReason('');
    setConfirmChecked(false);
    setLoading(false);
    onClose();
  };
  const isValid = isCurrentlySuspended ? confirmChecked : (reason.trim().length > 0 && confirmChecked);
  return (
    <Dialog 
      open={open} 
      onClose={handleClose}
      maxWidth="sm"
      fullWidth
    >
      <DialogTitle sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
        {isCurrentlySuspended ? (
          <>
            <ActivateIcon color="success" />
            Activate Organization
          </>
        ) : (
          <>
            <BlockIcon color="error" />
            Suspend Organization
          </>
        )}
      </DialogTitle>
      <DialogContent>
        <Alert severity={isCurrentlySuspended ? "info" : "warning"} sx={{ mb: 2 }}>
          <Typography variant="subtitle1" fontWeight="bold">
            {isCurrentlySuspended 
              ? 'Activate Organization Account'
              : ' Suspend Organization Account'
            }
          </Typography>
        </Alert>
        <Typography variant="body1" gutterBottom>
          {isCurrentlySuspended 
            ? `Are you sure you want to activate "${organizationName}"? This will restore full access to all users in this organization.`
            : `Are you sure you want to suspend "${organizationName}"? This will immediately block access for all users in this organization.`
          }
        </Typography>
        {!isCurrentlySuspended && (
          <Box sx={{ my: 2 }}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Effects of suspension:
            </Typography>
            <ul>
              <li>All users will be logged out immediately</li>
              <li>Users cannot log in until reactivated</li>
              <li>Data is preserved but not accessible</li>
              <li>All API access is blocked</li>
            </ul>
          </Box>
        )}
        {!isCurrentlySuspended && (
          <Box sx={{ my: 2 }}>
            <TextField
              fullWidth
              label="Reason for Suspension"
              multiline
              rows={3}
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              disabled={loading}
              placeholder="Enter the reason for suspending this organization..."
              required
            />
          </Box>
        )}
        <FormControlLabel
          control={
            <Checkbox
              checked={confirmChecked}
              onChange={(e) => setConfirmChecked(e.target.checked)}
              disabled={loading}
            />
          }
          label={isCurrentlySuspended 
            ? `I confirm that I want to activate "${organizationName}"`
            : `I confirm that I want to suspend "${organizationName}"`
          }
        />
        {!isCurrentlySuspended && (
          <Alert severity="error" sx={{ mt: 2 }}>
            This action takes effect immediately and will affect all users in the organization.
          </Alert>
        )}
      </DialogContent>
      <DialogActions>
        <Button 
          onClick={handleClose} 
          disabled={loading}
        >
          Cancel
        </Button>
        <Button
          onClick={handleAction}
          variant="contained"
          color={isCurrentlySuspended ? "success" : "error"}
          disabled={!isValid || loading}
          startIcon={loading ? <CircularProgress size={16} /> : null}
        >
          {loading 
            ? `${isCurrentlySuspended ? 'Activating' : 'Suspending'}...`
            : `${isCurrentlySuspended ? 'Activate' : 'Suspend'} Organization`
          }
        </Button>
      </DialogActions>
    </Dialog>
  );
};
export default SuspendDialog;

// frontend/src/components/UnifiedLoginForm.tsx
'use client';
import React, { useState } from 'react';
import { 
  Box,
  Card,
  CardContent,
  TextField,
  Button,
  Typography,
  Alert,
  CircularProgress,
  FormControlLabel,
  Checkbox,
  IconButton,
  InputAdornment,
  Stepper,
  Step,
  StepLabel
} from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import { useForm, Controller } from 'react-hook-form';
import { authService } from '../services/authService';
interface UnifiedLoginFormProps {
  onLogin: (token: string, loginResponse?: any) => void;
}
interface LoginFormData {
  email: string;
  password: string;
  phoneNumber: string;
  otp: string;
}
const UnifiedLoginForm: React.FC<UnifiedLoginFormProps> = ({ onLogin }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [useOTP, setUseOTP] = useState(false);
  const [otpStep, setOtpStep] = useState(0); // 0: credentials, 1: OTP entry
  const [otpSent, setOtpSent] = useState(false);
const { control, handleSubmit, formState: { errors }, watch, setValue} = useForm<LoginFormData>({
    defaultValues: {
      email: '',
      password: '',
      phoneNumber: '',
      otp: ''
    }
  });
  const email = watch('email');
  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };
  const handleOTPToggle = (event: React.ChangeEvent<HTMLInputElement>) => {
    setUseOTP(event.target.checked);
    setError('');
    setSuccess('');
    if (!event.target.checked) {
      setOtpStep(0);
      setOtpSent(false);
      setValue('otp', '');
      setValue('phoneNumber', '');
    }
  };
  const requestOTP = async (email: string, phoneNumber: string) => {
    try {
      setLoading(true);
      setError('');
      // Determine delivery method based on phone number
      const deliveryMethod = phoneNumber ? 'auto' : 'email';
      const response = await authService.requestOTP(email, phoneNumber, deliveryMethod);
      setSuccess(response.delivery_method 
        ? `OTP sent via ${response.delivery_method}. Please check your messages.`
        : `OTP sent to ${email}. Please check your email.`);
      setOtpSent(true);
      setOtpStep(1);
    } catch (error: any) {
      setError(error.response?.data?.detail || 'Failed to send OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  const onSubmit = async (data: LoginFormData) => {
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      if (useOTP) {
        if (!otpSent) {
          // Step 1: Request OTP
          await requestOTP(data.email, data.phoneNumber);
          return; // Don't continue to login yet
        } else {
          // Step 2: Verify OTP and login
          const response = await authService.verifyOTP(data.email, data.otp);
          setSuccess('Login successful!');
          // Add flag to indicate OTP login (so password change is not mandatory)
          response.otp_login = true;
          // Call parent callback with token and response
          onLogin(response.access_token, response);
        }
      } else {
        // Standard email/password login
        // Clear any existing invalid token before login attempt
        localStorage.removeItem('token');
        const response = await authService.loginWithEmail(data.email, data.password);
        onLogin(response.access_token, response);
      }
    } catch (error: any) {
      const errorMessage = error.message || error.response?.data?.detail || 'Login failed. Please check your credentials.';
      setError(errorMessage);
      // Clear potentially invalid token on failure
      localStorage.removeItem('token');
    } finally {
      setLoading(false);
    }
  };
  const handleBackToCredentials = () => {
    setOtpStep(0);
    setOtpSent(false);
    setError('');
    setSuccess('');
    setValue('otp', '');
  };
  const handleResendOTP = async () => {
    const phoneNumber = watch('phoneNumber');
    await requestOTP(email, phoneNumber);
  };
  const steps = ['Login Details', 'Verify OTP'];
  return (
    <Card>
      <CardContent sx={{ p: 4 }}>
        <Typography variant="h5" component="h2" gutterBottom align="center">
          {useOTP ? 'OTP Login' : 'Login'}
        </Typography>
        {useOTP && (
          <Stepper activeStep={otpStep} sx={{ mt: 2, mb: 3 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
        )}
        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}
        {success && (
          <Alert severity="success" sx={{ mb: 2 }}>
            {success}
          </Alert>
        )}
        <Box component="form" onSubmit={handleSubmit(onSubmit)}>
          {/* Email field - always visible */}
          <Controller
            name="email"
            control={control}
            rules={{
              required: 'Email is required',
              pattern: {
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                message: 'Invalid email address'
              }
            }}
            render={({ field }) => (
              <TextField
                {...field}
                fullWidth
                label="Email Address"
                type="email"
                variant="outlined"
                slotProps={{
                  inputLabel: {
                    shrink: field.value ? true : undefined
                  }
                }}
                error={!!errors.email}
                helperText={errors.email?.message}
                margin="normal"
                autoComplete="email"
                autoFocus
                disabled={otpSent && useOTP}
              />
            )}
          />
          {/* Password field - hidden when OTP is active and step > 0 */}
          {!useOTP && (
            <Controller
              name="password"
              control={control}
              rules={{
                required: 'Password is required'
              }}
              render={({ field }) => (
                <TextField
                  {...field}
                  fullWidth
                  label="Password"
                  type={showPassword ? 'text' : 'password'}
                  variant="outlined"
                  slotProps={{
                    inputLabel: {
                      shrink: field.value ? true : undefined
                    }
                  }}
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          aria-label="toggle password visibility"
                          onClick={togglePasswordVisibility}
                          edge="end"
                        >
                          {showPassword ? <VisibilityOff /> : <Visibility />}
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                  error={!!errors.password}
                  helperText={errors.password?.message}
                  margin="normal"
                  autoComplete="current-password"
                />
              )}
            />
          )}
          {/* Phone number field - shown when OTP is enabled and not yet sent */}
          {useOTP && !otpSent && (
            <Controller
              name="phoneNumber"
              control={control}
              rules={{
                pattern: {
                  value: /^[\+]?[1-9][\d]{0,15}$/,
                  message: 'Enter a valid phone number with country code (e.g., +91XXXXXXXXXX)'
                }
              }}
              render={({ field }) => (
                <TextField
                  {...field}
                  fullWidth
                  label="Phone Number (Optional for WhatsApp OTP)"
                  type="tel"
                  variant="outlined"
                  placeholder="+91XXXXXXXXXX"
                  slotProps={{
                    inputLabel: {
                      shrink: field.value ? true : undefined
                    }
                  }}
                  error={!!errors.phoneNumber}
                  helperText={errors.phoneNumber?.message || "Include country code for WhatsApp OTP, or leave blank for email OTP"}
                  margin="normal"
                  autoComplete="tel"
                />
              )}
            />
          )}
          {/* OTP field - shown when OTP step is active */}
          {useOTP && otpSent && (
            <Controller
              name="otp"
              control={control}
              rules={{
                required: 'OTP is required',
                pattern: {
                  value: /^\d{6}$/,
                  message: 'OTP must be 6 digits'
                }
              }}
              render={({ field }) => (
                <TextField
                  {...field}
                  fullWidth
                  label="OTP Code"
                  type="text"
                  inputProps={{ maxLength: 6, pattern: '[0-9]*' }}
                  variant="outlined"
                  slotProps={{
                    inputLabel: {
                      shrink: field.value ? true : undefined
                    }
                  }}
                  error={!!errors.otp}
                  helperText={errors.otp?.message}
                  margin="normal"
                  autoFocus
                />
              )}
            />
          )}
          {/* Login with OTP checkbox */}
          <FormControlLabel
            control={
              <Checkbox
                checked={useOTP}
                onChange={handleOTPToggle}
                name="useOTP"
                color="primary"
                disabled={loading}
              />
            }
            label="Login with OTP"
            sx={{ mt: 2, mb: 1 }}
          />
          {/* Action buttons */}
          {!useOTP || !otpSent ? (
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              disabled={loading}
            >
              {loading ? (
                <CircularProgress size={24} />
              ) : (
                useOTP ? 'Send OTP' : 'Login'
              )}
            </Button>
          ) : (
            <Box sx={{ mt: 3, mb: 2, display: 'flex', gap: 2 }}>
              <Button
                variant="outlined"
                onClick={handleBackToCredentials}
                disabled={loading}
                sx={{ flex: 1 }}
              >
                Back
              </Button>
              <Button
                type="submit"
                variant="contained"
                disabled={loading}
                sx={{ flex: 1 }}
              >
                {loading ? <CircularProgress size={24} /> : 'Verify & Login'}
              </Button>
            </Box>
          )}
          {/* Resend OTP button */}
          {useOTP && otpSent && (
            <Button
              variant="text"
              onClick={handleResendOTP}
              disabled={loading}
              fullWidth
              sx={{ mt: 1 }}
            >
              Resend OTP
            </Button>
          )}
        </Box>
        <Typography variant="body2" color="textSecondary" align="center" sx={{ mt: 2 }}>
          {useOTP 
            ? "Enter your email and optional phone number to receive an OTP for secure login."
            : "Use your email and password to login, or try OTP authentication for enhanced security."
          }
        </Typography>
      </CardContent>
    </Card>
  );
};
export default UnifiedLoginForm;

// frontend/src/components/VoucherContextMenu.tsx
import React, { useState } from 'react';
import { Menu, MenuItem, IconButton } from '@mui/material';
import { 
  MoreVert as MoreVertIcon, 
  Visibility, 
  Edit, 
  Delete, 
  Print, 
  SaveAlt, 
  Email, 
  ContentCopy,
  LocalShipping 
} from '@mui/icons-material';
interface VoucherContextMenuProps {
  voucher?: any;
  voucherType: string;
  onView: (id: number) => void;
  onEdit: (id: number) => void;
  onDelete: (id: number) => void;
  onPrint?: (id: number, mode: 'print' | 'download') => void;
  onEmail?: (id: number) => void;
  onDuplicate?: (id: number) => void;
  onCreateDispatch?: (id: number) => void;
  showKebab?: boolean;
  contextMenu?: { mouseX: number; mouseY: number; voucher: any } | null;
  onClose: () => void;
  open?: boolean;
  anchorPosition?: { left: number; top: number };
  anchorReference?: 'anchorPosition' | 'anchorEl' | 'none';
}
const VoucherContextMenu: React.FC<VoucherContextMenuProps> = ({
  voucher,
  voucherType,
  onView,
  onEdit,
  onDelete,
  onPrint,
  onEmail,
  onDuplicate,
  onCreateDispatch,
  showKebab = false,
  contextMenu = null,
  onClose,
  open = false,
  anchorPosition = null,
  anchorReference,
}) => {
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const effectiveVoucher = voucher || (contextMenu ? contextMenu.voucher : null);
  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleMenuClose = () => {
    setAnchorEl(null);
    onClose();
  };
    if (effectiveVoucher) {
      action(effectiveVoucher.id);
    }
    handleMenuClose();
  };
  const handlePrintAction = (mode: 'print' | 'download') => () => {
    if (onPrint && effectiveVoucher) {
      onPrint(effectiveVoucher.id, mode);
    }
    handleMenuClose();
  };
  // Determine email recipient based on voucher type
  const getEmailRecipient = () => {
    if (!effectiveVoucher) {return '';}
    if (!voucherType) {return '';}
    const lowerType = voucherType.toLowerCase();
    if (lowerType.includes('sales')) {
      return effectiveVoucher.customer?.email || '';
    } else if (lowerType.includes('purchase') || lowerType.includes('financial') || lowerType.includes('payment')) {
      return effectiveVoucher.vendor?.email || '';
    } else if (lowerType.includes('receipt')) {
      return effectiveVoucher.customer?.email || '';
    }
    return '';
  };
  const handleEmailClick = () => {
    if (onEmail && effectiveVoucher) {
      const recipient = getEmailRecipient();
      if (recipient) {
        onEmail(effectiveVoucher.id);
      } else {
        alert('No email recipient found for this voucher type.');
      }
    }
    handleMenuClose();
  };
  const menuProps = anchorPosition && anchorReference ? {
    open: open || Boolean(anchorEl),
    onClose: handleMenuClose,
    anchorReference: anchorReference,
    anchorPosition,
  } : {
    open: Boolean(anchorEl),
    anchorEl,
    onClose: handleMenuClose,
  };
  const hasEmail = !!onEmail && !!getEmailRecipient();
  const isDeliveryChallan = voucherType.toLowerCase().includes('delivery challan');
  const isPurchaseOrder = voucherType.toLowerCase().includes('purchase order');
  return (
    <>
      {showKebab && (
        <IconButton onClick={handleClick}>
          <MoreVertIcon />
        </IconButton>
      )}
      <Menu {...menuProps}>
        <MenuItem onClick={handleAction(onView)}>
          <Visibility sx={{ mr: 1 }} /> View {voucherType}
        </MenuItem>
        <MenuItem onClick={handleAction(onEdit)}>
          <Edit sx={{ mr: 1 }} /> Edit {voucherType}
        </MenuItem>
        {onDuplicate && (
          <MenuItem onClick={handleAction(onDuplicate)}>
            <ContentCopy sx={{ mr: 1 }} /> Duplicate {voucherType}
          </MenuItem>
        )}
        <MenuItem onClick={handleAction(onDelete)}>
          <Delete sx={{ mr: 1 }} /> Delete {voucherType}
        </MenuItem>
        {onPrint && (
          <MenuItem onClick={handlePrintAction('print')}>
            <Print sx={{ mr: 1 }} /> Print {voucherType}
          </MenuItem>
        )}
        {onPrint && (
          <MenuItem onClick={handlePrintAction('download')}>
            <SaveAlt sx={{ mr: 1 }} /> Save {voucherType} as PDF
          </MenuItem>
        )}
        {hasEmail && (
          <MenuItem onClick={handleEmailClick}>
            <Email sx={{ mr: 1 }} /> Email {voucherType}
          </MenuItem>
        )}
        {isDeliveryChallan && onCreateDispatch && (
          <MenuItem onClick={handleAction(onCreateDispatch)}>
            <LocalShipping sx={{ mr: 1 }} /> Create Dispatch Order
          </MenuItem>
        )}
      </Menu>
    </>
  );
};
export default VoucherContextMenu;

// frontend/src/components/VoucherHeaderActions.tsx
'use client';
import React from 'react';
import { Box, Button } from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Close as CloseIcon } from '@mui/icons-material';
import { useRouter } from 'next/navigation';
interface VoucherHeaderActionsProps {
  mode: 'create' | 'edit' | 'view';
  voucherType: string; // e.g., 'Purchase Order', 'Sales Voucher', etc.
  voucherRoute: string; // The base route for this voucher type
  currentId?: number; // Current voucher ID (for edit route)
  onEdit?: () => void;
  onCreate?: () => void;
