// src/context/AuthContext.tsx
import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useRouter } from 'next/router';
import { toast } from 'react-toastify';
import { authService } from '../services/authService';
import { User, getDisplayRole } from '../types/user.types';
import { markAuthReady, resetAuthReady } from '../lib/api';
declare function restoreFormData(...args: any[]): any;
declare function handlePostLoginRedirect(...args: any[]): any;
interface AuthContextType {
  user: User | null;
  loading: boolean;
  displayRole: string | null;
  login: (loginResponse: any) => Promise<void>;
  logout: () => void;
  refreshUser: () => Promise<void>;
  updateUser: (updatedData: Partial<User>) => void;
  isOrgContextReady: boolean;
}
const AuthContext = createContext<AuthContextType | undefined>(undefined);
export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const router = useRouter();
  // -- Fetch the current user from API using the token in localStorage --
  // NOTE: This should only be called for:
  // 1. Initial auth state check on app mount
  // 2. Manual user refresh (e.g., after profile updates)  
  // DO NOT call this during login - use the user data from login response instead
  const fetchUser = async (retryCount = 0) => {
    const maxRetries = 2;
    console.log(`[AuthProvider] fetchUser started - attempt ${retryCount + 1}/${maxRetries + 1}`, {
      hasToken: !!localStorage.getItem('token'),
      timestamp: new Date().toISOString()
    });
    try {
      const currentToken = localStorage.getItem('token');
      if (!currentToken) {
        console.log('[AuthProvider] No token found in localStorage');
        throw new Error('No token found');
      }
      console.log('[AuthProvider] Token found, fetching user data from API');
      const userData = await authService.getCurrentUser();
      console.log('[AuthProvider] User data received from API:', {
        userId: userData.id,
        email: userData.email,
        role: userData.role,
        isSuperAdmin: userData.is_super_admin,
        hasOrgId: !!userData.organization_id,
        mustChangePassword: userData.must_change_password
      });
      // Defensive: org ID should never be leaked between users
      const newUser = {
        id: userData.id,
        email: userData.email,
        role: userData.role,
        is_super_admin: userData.is_super_admin,
        organization_id: userData.organization_id,
        must_change_password: userData.must_change_password,
      };
      setUser(newUser);
      console.log('[AuthProvider] User state updated successfully');
      // Check org context for non-super-admins
      if (!userData.is_super_admin && !userData.organization_id) {
        console.error('[AuthProvider] Organization context missing for regular user');
        throw new Error('User account is not properly configured with organization context');
      }
      markAuthReady();
      console.log('[AuthProvider] Auth context marked as ready');
      // If on login page after successful fetch, redirect to dashboard
      if (router.pathname === '/login') {
        handlePostLoginRedirect();
      }
    } catch (error: any) {
      console.error(`[AuthProvider] fetchUser error on attempt ${retryCount + 1}:`, {
        error: error.message,
        status: error?.status,
        willRetry: retryCount < maxRetries && (error?.status !== 401 && error?.status !== 403)
      });
      // Only retry on non-auth errors
      if (retryCount < maxRetries && (error?.status !== 401 && error?.status !== 403)) {
        const retryDelay = Math.pow(2, retryCount) * 1000;
        console.log(`[AuthProvider] Retrying fetchUser in ${retryDelay}ms`);
        setTimeout(() => fetchUser(retryCount + 1), retryDelay);
        return;
      }
      // On error, clear sensitive data and force re-auth
      console.log('[AuthProvider] Auth error - clearing data');
      localStorage.removeItem('token');
      localStorage.removeItem('user_role');
      localStorage.removeItem('is_super_admin');
      setUser(null);
      resetAuthReady();
      if (error?.userMessage) {
        toast.error(`Authentication failed: ${error.userMessage}`, { position: "top-right", autoClose: 5000 });
      } else {
        toast.error('Failed to establish secure session. Please log in again.', { position: "top-right", autoClose: 5000 });
      }
      // Only redirect if not already on login page to prevent loop
      if (router.pathname !== '/login') {
        console.log('[AuthProvider] Redirecting to login');
        router.push('/login');
      } else {
        console.log('[AuthProvider] Already on login - no redirect needed');
      }
    }
  };
  // -- On mount, check for token and initialize user session --
  useEffect(() => {
    console.log('[AuthProvider] Component mounted, initializing auth state');
    const token = localStorage.getItem('token');
    console.log('[AuthProvider] Token check result:', {
      hasToken: !!token,
      pathname: router.pathname,
      timestamp: new Date().toISOString()
    });
    if (token) {
      console.log('[AuthProvider] Token found - starting user fetch');
      // Critical fix: Don't set loading to false until we have a definitive result
      fetchUser().finally(() => {
        console.log('[AuthProvider] User fetch completed - setting loading to false');
        setLoading(false);
      });
    } else {
      console.log('[AuthProvider] No token found - marking auth ready and stopping loading');
      markAuthReady();
      setLoading(false);
    }
  }, [router.pathname]);  // Add pathname dependency to re-run on route changes
  // -- Handle post-login redirect with state preservation --
  const handlePostLoginRedirect = () => {
    try {
      // Check for return URL
      const returnUrl = sessionStorage.getItem('returnUrlAfterLogin');
      if (returnUrl) {
        console.log('[AuthProvider] Redirecting to saved URL:', returnUrl);
        sessionStorage.removeItem('returnUrlAfterLogin');
        // Use router.replace to avoid adding to history
        router.replace(returnUrl);
        // Attempt to restore form data after a short delay
        setTimeout(() => {
          restoreFormData();
        }, 500);
        return;
      }
      // Default redirect to dashboard
      console.log('[AuthProvider] No return URL found, redirecting to dashboard');
      router.push('/dashboard');
    } catch (error) {
      console.error('[AuthProvider] Error during post-login redirect:', error);
      // Fallback to dashboard
      router.push('/dashboard');
    }
  };
  // -- Attempt to restore form data after login --
  const restoreFormData = () => {
    try {
      const savedFormData = sessionStorage.getItem('formDataBeforeExpiry');
      if (savedFormData) {
        const formData = JSON.parse(savedFormData);
        console.log('[AuthProvider] Attempting to restore form data:', formData);
        // Attempt to restore form values
        Object.entries(formData).forEach(([formKey, formValues]: [string, any]) => {
          if (formValues && typeof formValues === 'object') {
            Object.entries(formValues).forEach(([fieldName, fieldValue]) => {
              const field = document.querySelector(`[name="${fieldName}"]`) as HTMLInputElement;
              if (field && typeof fieldValue === 'string') {
                field.value = fieldValue;
                // Trigger input event to update React state
                field.dispatchEvent(new Event('input', { bubbles: true }));
              }
            });
          }
        });
        // Clean up saved form data
        sessionStorage.removeItem('formDataBeforeExpiry');
        // Show notification to user
        toast.info('Form data has been restored from before session expiry.', {
          position: "top-right",
          autoClose: 5000,
        });
      }
    } catch (error) {
      console.warn('[AuthProvider] Could not restore form data:', error);
    }
  };
  // -- Force password reset if required --
  useEffect(() => {
    if (user && user.must_change_password && router.pathname !== '/password-reset') {
      router.push('/password-reset');
    }
  }, [user, router]);
  // -- Login: store token, hydrate user, and mark ready --
  const login = async (loginResponse: any) => {
    console.log('[AuthProvider] Login process started:', {
      hasToken: !!loginResponse.access_token,
      userRole: loginResponse.user_role,
      isSuperAdmin: loginResponse.user?.is_super_admin,
      hasOrgId: !!loginResponse.organization_id,
      mustChangePassword: loginResponse.must_change_password,
      timestamp: new Date().toISOString()
    });
    localStorage.setItem('token', loginResponse.access_token);
    console.log('[AuthProvider] Token stored in localStorage');
    if (loginResponse.user_role) {
      localStorage.setItem('user_role', loginResponse.user_role);
      console.log('[AuthProvider] User role stored:', loginResponse.user_role);
    }
    localStorage.setItem('is_super_admin', loginResponse.user?.is_super_admin ? 'true' : 'false');
    console.log('[AuthProvider] Super admin flag stored:', loginResponse.user?.is_super_admin);
    // Defensive: never store org_id in localStorage
    const userData = loginResponse.user;
    // Validate org context for regular users
    if (!userData.is_super_admin && !loginResponse.organization_id) {
      console.error('[AuthProvider] Organization context validation failed for regular user');
      throw new Error('Login failed: User account is not properly configured with organization context');
    }
    const newUser = {
      id: userData.id,
      email: userData.email,
      role: userData.role,
      is_super_admin: userData.is_super_admin,
      organization_id: loginResponse.organization_id,
      must_change_password: loginResponse.must_change_password,
    };
    setUser(newUser);
    console.log('[AuthProvider] User state set from login response');
    resetAuthReady();
    markAuthReady();
    console.log('[AuthProvider] Auth ready state reset and marked');
    // Handle post-login redirect and form state restoration
    handlePostLoginRedirect();
    // NOTE: No need to call fetchUser() here as we already have fresh user data from login response
    // This prevents duplicate /users/me API calls that can cause session instability
    console.log('[AuthProvider] Login process completed successfully - user context established from login response');
  };
  // -- Logout: clear all sensitive data and redirect --
  const logout = () => {
    console.log('[AuthProvider] Logout initiated');
    localStorage.removeItem('token');
    localStorage.removeItem('user_role');
    localStorage.removeItem('is_super_admin');
    setUser(null);
    resetAuthReady();
    console.log('[AuthProvider] Auth data cleared');
    // Only redirect if not already on login
    if (router.pathname !== '/login') {
      console.log('[AuthProvider] Redirecting to login');
      router.push('/login');
    } else {
      console.log('[AuthProvider] Already on login - no redirect needed');
    }
  };
  // -- Manual refresh of user (e.g., after profile update) --
  const refreshUser = async () => {
    await fetchUser();
  };
  // -- Update the user object in memory only --
  const updateUser = (updatedData: Partial<User>) => {
    setUser(prev => prev ? { ...prev, ...updatedData } : null);
  };
  // -- Only ready if user is super admin or has org context --
  const isOrgContextReady = !user || user.is_super_admin || !!user.organization_id;
  console.log('[AuthProvider] Render phase:', {
    loading,
    hasUser: !!user,
    userEmail: user?.email,
    isOrgContextReady,
    willRenderChildren: !loading,
    timestamp: new Date().toISOString()
  });
  // Show loading spinner while auth state is being determined
  if (loading) {
    console.log('[AuthProvider] Rendering loading state');
    // CSS for enhanced spinner animation with app branding
    const spinnerStyles = `
      @keyframes authSpinner {
        0% { transform: rotate(0deg); }
        100% { transform: rotate(360deg); }
      }
      .auth-spinner {
        width: 40px;
        height: 40px;
        border: 4px solid #f3f3f3;
        border-top: 4px solid #3498db;
        border-radius: 50%;
        animation: authSpinner 2s linear infinite;
        margin-bottom: 15px;
      }
      @keyframes pulse {
        0% { opacity: 0.6; }
        50% { opacity: 1; }
        100% { opacity: 0.6; }
      }
      .auth-pulse {
        animation: pulse 2s ease-in-out infinite;
      }
    `;
    return (
      <>
        <style dangerouslySetInnerHTML={{ __html: spinnerStyles }} />
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh',
          flexDirection: 'column',
          backgroundColor: '#f8fafc',
          backgroundImage: 'linear-gradient(to bottom right, #f8fafc, #e2e8f0)'
        }}>
          <div style={{ 
            fontSize: '24px', 
            fontWeight: 600,
            marginBottom: '10px',
            color: '#1e293b',
            textAlign: 'center'
          }}>
            TRITIQ BOS
          </div>
          <div style={{ 
            fontSize: '14px', 
            marginBottom: '30px',
            color: '#64748b',
            textAlign: 'center'
          }}>
            Business Management System
          </div>
          <div className="auth-spinner"></div>
          <div style={{ 
            marginTop: '20px', 
            fontSize: '14px', 
            color: '#475569',
            fontWeight: 500,
            textAlign: 'center'
          }} className="auth-pulse">
            Loading your workspace...
          </div>
          <div style={{ 
            marginTop: '5px', 
            fontSize: '12px', 
            color: '#94a3b8',
            textAlign: 'center'
          }}>
            Establishing secure connection
          </div>
        </div>
      </>
    );
  }
  return (
    <AuthContext.Provider value={{
      user,
      loading,
      displayRole: user ? getDisplayRole(user.role, user.is_super_admin) : null,
      login,
      logout,
      refreshUser,
      updateUser,
      isOrgContextReady
    }}>
      {children}
    </AuthContext.Provider>
  );
}
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};
export const useAuthWithOrgContext = () => {
  const auth = useAuth();
  return {
    ...auth,
    isReady: !auth.loading && auth.isOrgContextReady
  };
};

// frontend/src/context/CompanyContext.tsx
import React, { createContext, useContext } from 'react';
import { useQuery } from '@tanstack/react-query';
import { companyService } from '../services/authService';
import { useAuth } from './AuthContext';
import { useRouter } from 'next/router';
interface CompanyContextType {
  isCompanySetupNeeded: boolean;
  company: any;  // Replace with proper type
  isLoading: boolean;
  error: any;
  refetch: () => void;
}
const CompanyContext = createContext<CompanyContextType | undefined>(undefined);
export const CompanyProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const router = useRouter();
  const { user, loading } = useAuth();
  const enabled = !loading && !!user && !!localStorage.getItem('token') && router.pathname !== '/login';
  const { data: company, isLoading, error, refetch } = useQuery({
    queryKey: ['currentCompany'],
    queryFn: companyService.getCurrentCompany,
    enabled: enabled,  // Only fetch when authenticated and not on login
    retry: false,  // Don't retry on failure
    onError: (err: any) => {
      if (err.message === 'No authentication token available' || err.status === 401) {
        // Silent handling for no token
        console.log('[CompanyContext] No auth token - skipping setup check silently');
      } else if (err.status === 404 || err.isCompanySetupRequired) {
        console.log('[CompanyContext] Company setup needed due to 404/missing company');
      } else {
        console.error('[CompanyContext] Unexpected error fetching company:', err);
        toast.error(`Error fetching company details: ${err.message}`);
      }
    },
  });
  const isCompanySetupNeeded = enabled && !isLoading && company === null && !error;
  return (
    <CompanyContext.Provider value={{ isCompanySetupNeeded, company, isLoading, error, refetch }}>
      {children}
    </CompanyContext.Provider>
  );
};
export const useCompany = () => {
  const context = useContext(CompanyContext);
  if (undefined === context) {
    throw new Error('useCompany must be used within a CompanyProvider');
  }
  return context;
};

import React, { createContext, useContext, useState, ReactNode } from 'react';
import { Alert, Snackbar } from '@mui/material';
declare function hideError(...args: any[]): any;
interface ErrorMessage {
  id: string;
  message: string;
  type: 'error' | 'warning' | 'info' | 'success';
}
interface ErrorContextType {
  showError: (message: string, type?: 'error' | 'warning' | 'info' | 'success') => void;
  hideError: (id: string) => void;
}
const ErrorContext = createContext<ErrorContextType | undefined>(undefined);
export const useError = () => {
  const context = useContext(ErrorContext);
  if (!context) {
    throw new Error('useError must be used within an ErrorProvider');
  }
  return context;
};
interface ErrorProviderProps {
  children: ReactNode;
}
export const ErrorProvider: React.FC<ErrorProviderProps> = ({ children }) => {
  const [errors, setErrors] = useState<ErrorMessage[]>([]);
  const showError = (message: string, type: 'error' | 'warning' | 'info' | 'success' = 'error') => {
    const id = Date.now().toString();
    const newError: ErrorMessage = { id, message, type };
    setErrors(prev => [...prev, newError]);
    // Auto-hide after 6 seconds
    setTimeout(() => {
      hideError(id);
    }, 6000);
  };
  const hideError = (id: string) => {
    setErrors(prev => prev.filter(error => error.id !== id));
  };
  return (
    <ErrorContext.Provider value={{ showError, hideError }}>
      {children}
      {/* Render error snackbars */}
      {errors.map((error, index) => (
        <Snackbar
          key={error.id}
          open={true}
          autoHideDuration={6000}
          onClose={() => hideError(error.id)}
          anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
          sx={{ mt: index * 7 }} // Stack multiple errors
        >
          <Alert 
            onClose={() => hideError(error.id)} 
            severity={error.type}
            sx={{ width: '100%' }}
          >
            {error.message}
          </Alert>
        </Snackbar>
      ))}
    </ErrorContext.Provider>
  );
};

// frontend/src/context/VoucherContext.tsx
import React, { createContext, useState, ReactNode, useContext } from 'react';
interface VoucherContextType {
  vouchers: any[];
  setVouchers: React.Dispatch<React.SetStateAction<any[]>>;
  addVoucher: (voucher: any) => void;
  updateVoucher: (id: number, updatedVoucher: any) => void;
}
const VoucherContext = createContext<VoucherContextType | undefined>(undefined);
export const VoucherProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [vouchers, setVouchers] = useState<any[]>([]);
  const addVoucher = (voucher: any) => {
    setVouchers((prev) => [...prev, voucher]);
  };
  const updateVoucher = (id: number, updatedVoucher: any) => {
    setVouchers((prev) =>
      prev.map((v) => (v.id === id ? { ...v, ...updatedVoucher } : v))
    );
  };
  const value = {
    vouchers,
    setVouchers,
    addVoucher,
    updateVoucher
  };
  return (
    <VoucherContext.Provider value={value}>
      {children}
    </VoucherContext.Provider>
  );
};
export const useVoucherContext = (): VoucherContextType => {
  const context = useContext(VoucherContext);
  if (!context) {
    throw new Error('useVoucherContext must be used within a VoucherProvider');
  }
  return context;
};

/**
 * Test file for AuthProvider race condition fix
 */
import React from 'react';
import { render, screen, waitFor, act } from '@testing-library/react';
import '@testing-library/jest-dom';
import { AuthProvider, useAuth } from '../AuthContext';
import { authService } from '../../services/authService';
// Mock dependencies
jest.mock('../../services/authService');
jest.mock('next/router', () => ({
  useRouter: () => ({
    push: jest.fn(),
    pathname: '/dashboard'
  })
}));
jest.mock('../../lib/api', () => ({
  markAuthReady: jest.fn(),
  resetAuthReady: jest.fn()
}));
jest.mock('react-toastify', () => ({
  toast: {
    error: jest.fn()
  }
}));
const mockedAuthService = authService as jest.Mocked<typeof authService>;
// Test component to access auth context
const TestComponent = () => {
  const { user, loading } = useAuth();
  if (loading) {
    return <div data-testid="loading">Loading...</div>;
  }
  if (user) {
    return <div data-testid="user-info">{user.email}</div>;
  }
  return <div data-testid="no-user">No user</div>;
};
describe('AuthProvider Race Condition Fix', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    // Clear localStorage
    Object.defineProperty(window, 'localStorage', {
      value: {
        getItem: jest.fn(),
        setItem: jest.fn(),
        removeItem: jest.fn(),
        clear: jest.fn(),
      },
      writable: true,
    });
  });
  it('should show loading spinner while fetching user data', async () => {
    // Mock localStorage to have a token
    (window.localStorage.getItem as jest.Mock).mockReturnValue('fake-token');
    // Mock authService to return user data after a delay
    mockedAuthService.getCurrentUser.mockImplementation(
      () => new Promise(resolve => {
        setTimeout(() => resolve({
          id: 1,
          email: 'test@example.com',
          role: 'user',
          is_super_admin: false,
          organization_id: 1,
          must_change_password: false
        }), 100);
      })
    );
    render(
      <AuthProvider>
        <TestComponent />
      </AuthProvider>
    );
    // Should show loading initially
    expect(screen.getByTestId('loading')).toBeInTheDocument();
    expect(screen.getByText('Loading...')).toBeInTheDocument();
    // Should show user info after loading completes
    await waitFor(() => {
      expect(screen.getByTestId('user-info')).toBeInTheDocument();
      expect(screen.getByText('test@example.com')).toBeInTheDocument();
    }, { timeout: 2000 });
    // Loading should be gone
    expect(screen.queryByTestId('loading')).not.toBeInTheDocument();
  });
  it('should show proper loading state when no token is present', async () => {
    // Mock localStorage to have no token
    (window.localStorage.getItem as jest.Mock).mockReturnValue(null);
    render(
      <AuthProvider>
        <TestComponent />
      </AuthProvider>
    );
    // Should show loading initially
    expect(screen.getByTestId('loading')).toBeInTheDocument();
    // Should show no user after loading completes
    await waitFor(() => {
      expect(screen.getByTestId('no-user')).toBeInTheDocument();
    });
    // Loading should be gone
    expect(screen.queryByTestId('loading')).not.toBeInTheDocument();
  });
  it('should handle auth service errors gracefully', async () => {
    // Mock localStorage to have a token
    (window.localStorage.getItem as jest.Mock).mockReturnValue('fake-token');
    // Mock authService to throw an error
    mockedAuthService.getCurrentUser.mockRejectedValue(new Error('Auth failed'));
    render(
      <AuthProvider>
        <TestComponent />
      </AuthProvider>
    );
    // Should show loading initially
    expect(screen.getByTestId('loading')).toBeInTheDocument();
    // Should handle error and show no user
    await waitFor(() => {
      expect(screen.getByTestId('no-user')).toBeInTheDocument();
    }, { timeout: 2000 });
    // Token should be removed from localStorage on error
    expect(window.localStorage.removeItem).toHaveBeenCalledWith('token');
  });
  it('should show loading fallback UI with proper styling', () => {
    // Mock localStorage to have a token
    (window.localStorage.getItem as jest.Mock).mockReturnValue('fake-token');
    // Mock authService to never resolve (simulate loading state)
    mockedAuthService.getCurrentUser.mockImplementation(() => new Promise(() => {}));
    render(
      <AuthProvider>
        <TestComponent />
      </AuthProvider>
    );
    // Check that the loading UI elements are present
    expect(screen.getByText('Loading authentication...')).toBeInTheDocument();
    expect(screen.getByText('Establishing secure session...')).toBeInTheDocument();
    // Check for the spinner element (should have the auth-spinner class)
    const spinner = document.querySelector('.auth-spinner');
    expect(spinner).toBeInTheDocument();
  });
  it('should not render children until loading is complete', async () => {
