});

// Test for enhanced usePincodeLookup hook with retry logic and caching
import { renderHook, act } from '@testing-library/react';
import axios from 'axios';
import { usePincodeLookup } from '../../hooks/usePincodeLookup';
// Mock axios
jest.mock('axios');
const mockedAxios = axios as jest.Mocked<typeof axios>;
describe('usePincodeLookup', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    // Clear the cache before each test
    (global as any).pincodeCache?.clear();
  });
  it('should successfully lookup and cache pincode data', async () => {
    const mockResponse = {
      data: {
        city: 'Mumbai',
        state: 'Maharashtra',
        state_code: '27'
      }
    };
    mockedAxios.get.mockResolvedValueOnce(mockResponse);
    const { result } = renderHook(() => usePincodeLookup());
    await act(async () => {
      await result.current.lookupPincode('400001');
    });
    expect(result.current.pincodeData).toEqual(mockResponse.data);
    expect(result.current.loading).toBe(false);
    expect(result.current.error).toBe(null);
    expect(mockedAxios.get).toHaveBeenCalledWith('/api/v1/pincode/lookup/400001');
  });
  it('should return cached data on subsequent requests', async () => {
    const mockResponse = {
      data: {
        city: 'Mumbai',
        state: 'Maharashtra',
        state_code: '27'
      }
    };
    mockedAxios.get.mockResolvedValueOnce(mockResponse);
    const { result } = renderHook(() => usePincodeLookup());
    // First call - should make API request
    await act(async () => {
      await result.current.lookupPincode('400001');
    });
    expect(mockedAxios.get).toHaveBeenCalledTimes(1);
    // Second call - should use cache
    await act(async () => {
      await result.current.lookupPincode('400001');
    });
    expect(mockedAxios.get).toHaveBeenCalledTimes(1); // No additional API call
    expect(result.current.pincodeData).toEqual(mockResponse.data);
  });
  it('should handle validation errors for invalid pincode format', async () => {
    const { result } = renderHook(() => usePincodeLookup());
    await act(async () => {
      await result.current.lookupPincode('invalid');
    });
    expect(result.current.error).toBe('Please enter a valid 6-digit PIN code');
    expect(result.current.pincodeData).toBe(null);
    expect(mockedAxios.get).not.toHaveBeenCalled();
  });
  it('should handle 404 errors gracefully', async () => {
    mockedAxios.get.mockRejectedValueOnce({
      response: { status: 404 }
    });
    const { result } = renderHook(() => usePincodeLookup());
    await act(async () => {
      await result.current.lookupPincode('999999');
    });
    expect(result.current.error).toBe('PIN code not found. Please enter city and state manually.');
    expect(result.current.pincodeData).toBe(null);
    expect(result.current.loading).toBe(false);
  });
  it('should retry on server errors with exponential backoff', async () => {
    // Mock multiple failures followed by success
    mockedAxios.get
      .mockRejectedValueOnce({ response: { status: 503 } })
      .mockRejectedValueOnce({ response: { status: 503 } })
      .mockResolvedValueOnce({
        data: {
          city: 'Delhi',
          state: 'Delhi',
          state_code: '07'
        }
      });
    const { result } = renderHook(() => usePincodeLookup());
    await act(async () => {
      await result.current.lookupPincode('110001');
    });
    expect(mockedAxios.get).toHaveBeenCalledTimes(3); // Initial + 2 retries
    expect(result.current.pincodeData).toEqual({
      city: 'Delhi',
      state: 'Delhi',
      state_code: '07'
    });
    expect(result.current.error).toBe(null);
  });
  it('should not retry on client errors (4xx)', async () => {
    mockedAxios.get.mockRejectedValueOnce({
      response: { status: 400 }
    });
    const { result } = renderHook(() => usePincodeLookup());
    await act(async () => {
      await result.current.lookupPincode('400001');
    });
    expect(mockedAxios.get).toHaveBeenCalledTimes(1); // No retries for client errors
    expect(result.current.error).toBe('Failed to lookup PIN code. Please enter details manually.');
  });
  it('should clear data correctly', async () => {
    const mockResponse = {
      data: {
        city: 'Mumbai',
        state: 'Maharashtra',
        state_code: '27'
      }
    };
    mockedAxios.get.mockResolvedValueOnce(mockResponse);
    const { result } = renderHook(() => usePincodeLookup());
    await act(async () => {
      await result.current.lookupPincode('400001');
    });
    expect(result.current.pincodeData).toEqual(mockResponse.data);
    act(() => {
      result.current.clearData();
    });
    expect(result.current.pincodeData).toBe(null);
    expect(result.current.error).toBe(null);
    expect(result.current.loading).toBe(false);
  });
});

// src/hooks/useEntity.ts
// React hooks for Entity abstraction system
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useState } from 'react';
import {
  getAllEntities,
  getEntitiesByType,
  searchEntities,
  getEntityById,
  createEntity,
  updateEntity,
  deleteEntity,
  getEntityBalance,
  entitiesToOptions
} from '../services/entityService';
import { Entity, EntityType, EntityOption } from '../types/entity.types';
/**
 * Hook to get all entities with unified interface
 */
export const useEntities = (entityTypes: EntityType[] = ['Customer', 'Vendor']) => {
  return useQuery({
    queryKey: ['entities', entityTypes],
    queryFn: ({ signal }) => {
      if (entityTypes.length === 1) {
        return getEntitiesByType(entityTypes[0], { signal });
      }
      return getAllEntities({ signal });
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
  });
};
/**
 * Hook to get entity options for form dropdowns
 */
export const useEntityOptions = (entityTypes: EntityType[] = ['Customer', 'Vendor']) => {
  const { data: entities, ...queryProps } = useEntities(entityTypes);
  const options: EntityOption[] = entities ? entitiesToOptions(entities) : [];
  return {
    options,
    entities,
    ...queryProps
  };
};
/**
 * Hook for entity search with debouncing
 */
export const useEntitySearch = (
  searchTerm: string,
  entityTypes: EntityType[] = ['Customer', 'Vendor'],
  enabled: boolean = true
) => {
  return useQuery({
    queryKey: ['entitySearch', searchTerm, entityTypes],
    queryFn: ({ signal }) => searchEntities(searchTerm, entityTypes, { signal }),
    enabled: enabled && searchTerm.length >= 2,
    staleTime: 2 * 60 * 1000, // 2 minutes
  });
};
/**
 * Hook to get specific entity by ID and type
 */
export const useEntity = (id: number | null, entityType: EntityType | null) => {
  return useQuery({
    queryKey: ['entity', id, entityType],
    queryFn: ({ signal }) => {
      if (!id || !entityType) {return null;}
      return getEntityById(id, entityType, { signal });
    },
    enabled: !!id && !!entityType,
  });
};
/**
 * Hook for entity balance/outstanding amount
 */
export const useEntityBalance = (id: number | null, entityType: EntityType | null) => {
  return useQuery({
    queryKey: ['entityBalance', id, entityType],
    queryFn: ({ signal }) => {
      if (!id || !entityType) {return null;}
      return getEntityBalance(id, entityType, { signal });
    },
    enabled: !!id && !!entityType && ['Customer', 'Vendor'].includes(entityType),
    staleTime: 1 * 60 * 1000, // 1 minute
  });
};
/**
 * Hook for entity mutations (create, update, delete)
 */
export const useEntityMutations = () => {
  const queryClient = useQueryClient();
  const createMutation = useMutation({
    mutationFn: ({ entityType, data }: { entityType: EntityType; data: Partial<Entity> }) =>
      createEntity(entityType, data),
    onSuccess: (_, { entityType }) => {
      queryClient.invalidateQueries({ queryKey: ['entities'] });
      queryClient.invalidateQueries({ queryKey: ['entities', [entityType]] });
    },
  });
  const updateMutation = useMutation({
    mutationFn: ({ id, entityType, data }: { id: number; entityType: EntityType; data: Partial<Entity> }) =>
      updateEntity(id, entityType, data),
    onSuccess: (_, { entityType }) => {
      queryClient.invalidateQueries({ queryKey: ['entities'] });
      queryClient.invalidateQueries({ queryKey: ['entities', [entityType]] });
    },
  });
  const deleteMutation = useMutation({
    mutationFn: ({ id, entityType }: { id: number; entityType: EntityType }) =>
      deleteEntity(id, entityType),
    onSuccess: (_, { entityType }) => {
      queryClient.invalidateQueries({ queryKey: ['entities'] });
      queryClient.invalidateQueries({ queryKey: ['entities', [entityType]] });
    },
  });
  return {
    createEntity: createMutation,
    updateEntity: updateMutation,
    deleteEntity: deleteMutation,
  };
};
/**
 * Hook for entity form state management
 */
export const useEntityForm = (initialEntityType: EntityType = 'Customer') => {
  const [selectedEntityType, setSelectedEntityType] = useState<EntityType>(initialEntityType);
  const [selectedEntityId, setSelectedEntityId] = useState<number | null>(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const { options, isLoading } = useEntityOptions([selectedEntityType]);
  const { data: searchResults, isLoading: searchLoading } = useEntitySearch(
    searchTerm,
    [selectedEntityType],
    searchTerm.length >= 2
  );
  const displayOptions = searchTerm.length >= 2 ? (searchResults || []) : options;
  const handleEntitySelect = (entityOption: EntityOption | null) => {
    if (entityOption) {
      setSelectedEntityId(entityOption.id);
      setSelectedEntityType(entityOption.type);
    } else {
      setSelectedEntityId(null);
    }
  };
  const handleEntityTypeChange = (newType: EntityType) => {
    setSelectedEntityType(newType);
    setSelectedEntityId(null); // Reset selection when type changes
    setSearchTerm(''); // Reset search
  };
  return {
    // State
    selectedEntityType,
    selectedEntityId,
    showCreateModal,
    searchTerm,
    // Options
    options: displayOptions,
    isLoading: isLoading || searchLoading,
    // Handlers
    setSelectedEntityType: handleEntityTypeChange,
    setSelectedEntityId,
    setShowCreateModal,
    setSearchTerm,
    handleEntitySelect,
  };
};
/**
 * Legacy compatibility hooks
 */
export const useVendors = () => useEntities(['Vendor']);
export const useCustomers = () => useEntities(['Customer']);

import {useState} from 'react';
import axios from 'axios';
interface PincodeData {
  city: string;
  state: string;
  state_code: string;
}
interface UsePincodeLookupReturn {
  lookupPincode: (pincode: string) => Promise<void>;
  pincodeData: PincodeData | null;
  loading: boolean;
  error: string | null;
  clearData: () => void;
}
// Session-based cache for pincode lookups
const pincodeCache = new Map<string, PincodeData>();
// Retry utility with exponential backoff
const sleep = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));
const retryWithBackoff = async <T>(
  fn: () => Promise<T>,
  maxRetries: number = 3,
  baseDelay: number = 1000
): Promise<T> => {
  let lastError: Error;
  for (let attempt = 0; attempt <= maxRetries; attempt++) {
    try {
      return await fn();
    } catch (error: any) {
      lastError = error;
      // Don't retry for client errors (4xx) or if it's the last attempt
      if (error.response?.status >= 400 && error.response?.status < 500) {
        throw error;
      }
      if (attempt === maxRetries) {
        throw error;
      }
      // Exponential backoff: 1s, 2s, 4s
      const delay = baseDelay * Math.pow(2, attempt);
      await sleep(delay);
    }
  }
  throw lastError!;
};
export const usePincodeLookup = (): UsePincodeLookupReturn => {
  const [pincodeData, setPincodeData] = useState<PincodeData | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const lookupPincode = async (pincode: string): Promise<void> => {
    // Validate pincode format
    if (!pincode || !/^\d{6}$/.test(pincode)) {
      setError('Please enter a valid 6-digit PIN code');
      return;
    }
    // Check cache first
    const cachedData = pincodeCache.get(pincode);
    if (cachedData) {
      setPincodeData(cachedData);
      setError(null);
      return;
    }
    setLoading(true);
    setError(null);
    try {
      // Use retry logic for better reliability
      const response = await retryWithBackoff(
        () => axios.get(`/api/v1/pincode/lookup/${pincode}`),
        3, // 3 retries
        1000 // 1 second base delay
      );
      const data = response.data;
      setPincodeData(data);
      // Cache successful lookup for the session
      pincodeCache.set(pincode, data);
    } catch (err: any) {
      if (err.response?.status === 404) {
        setError('PIN code not found. Please enter city and state manually.');
      } else if (err.response?.status === 503) {
        setError('PIN code lookup service is currently unavailable. Please try again later or enter details manually.');
      } else {
        setError('Failed to lookup PIN code. Please enter details manually.');
      }
      setPincodeData(null);
    } finally {
      setLoading(false);
    }
  };
  const clearData = () => {
    setPincodeData(null);
    setError(null);
    setLoading(false);
  };
  return {
    lookupPincode,
    pincodeData,
    loading,
    error,
    clearData
  };
};

// frontend/src/hooks/useStickyNotes.ts
import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { stickyNotesService, StickyNote, UserSettings } from '../services/stickyNotesService';
export const useStickyNotes = () => {
  const { user } = useAuth();
  const [notes, setNotes] = useState<StickyNote[]>([]);
  const [userSettings, setUserSettings] = useState<UserSettings>({ sticky_notes_enabled: false });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const fetchUserSettings = async () => {
    try {
      const settings = await stickyNotesService.getUserSettings();
      setUserSettings(settings);
    } catch (err) {
      console.error('Error fetching user settings:', err);
      // Use disabled settings if fetch fails to prevent unauthorized fetches
      setUserSettings({ sticky_notes_enabled: false });
    }
  };
  const fetchNotes = async () => {
    try {
      setLoading(true);
      const notesData = await stickyNotesService.getNotes();
      setNotes(notesData);
      setError(null);
    } catch (err) {
      console.error('Error fetching notes:', err);
      setError('Failed to load sticky notes');
    } finally {
      setLoading(false);
    }
  };
  const toggleStickyNotes = async (enabled: boolean) => {
    try {
      const updatedSettings = await stickyNotesService.updateUserSettings({
        sticky_notes_enabled: enabled
      });
      setUserSettings(updatedSettings);
      return updatedSettings;
    } catch (err) {
      console.error('Error updating user settings:', err);
      throw err;
    }
  };
  useEffect(() => {
    if (user) {
      fetchUserSettings();
    } else {
      setUserSettings({ sticky_notes_enabled: false });
    }
  }, [user]);
  useEffect(() => {
    if (userSettings.sticky_notes_enabled && user) {
      fetchNotes();
    }
  }, [userSettings.sticky_notes_enabled, user]);
  return {
    notes,
    userSettings,
    loading,
    error,
    fetchNotes,
    toggleStickyNotes,
    refreshSettings: fetchUserSettings
  };
};
export default useStickyNotes;

import {useState} from 'react';
import uiConfig from '../config/ui-config.json';
interface UIConfig {
  tooltips: Record<string, any>;
  help_text: Record<string, any>;
  error_messages: Record<string, any>;
  success_messages: Record<string, any>;
}
interface UseUIConfigReturn {
  getTooltip: (path: string) => string;
  getHelpText: (path: string) => any;
  getErrorMessage: (path: string) => string;
  getSuccessMessage: (path: string) => string;
  config: UIConfig;
}
/**
 * Hook to access centralized UI configuration including tooltips, help text, and messages
 * 
 * @example
 * const { getTooltip, getHelpText } = useUIConfig();
 * const tooltip = getTooltip('masters.products.name');
 * const helpText = getHelpText('vouchers.pdf_extraction');
 */
export const useUIConfig = (): UseUIConfigReturn => {
  const [config] = useState<UIConfig>(uiConfig);
  const getNestedValue = (obj: any, path: string): any => {
    return path.split('.').reduce((current, key) => {
      return current && current[key] !== undefined ? current[key] : null;
    }, obj);
  };
  const getTooltip = (path: string): string => {
    const tooltip = getNestedValue(config.tooltips, path);
    return tooltip || '';
  };
  const getHelpText = (path: string): any => {
    const helpText = getNestedValue(config.help_text, path);
    return helpText || null;
  };
  const getErrorMessage = (path: string): string => {
    const errorMsg = getNestedValue(config.error_messages, path);
    return errorMsg || 'An error occurred';
  };
  const getSuccessMessage = (path: string): string => {
    const successMsg = getNestedValue(config.success_messages, path);
    return successMsg || 'Operation completed successfully';
  };
  return {
    getTooltip,
    getHelpText,
    getErrorMessage,
    getSuccessMessage,
    config
  };
};
export default useUIConfig;

// Voucher numbering utilities and hooks
import { useQuery } from '@tanstack/react-query';
import { useEffect } from 'react';
import api from '../lib/api';
interface UseVoucherNumberingProps {
  apiEndpoint: string;
  mode: 'create' | 'edit' | 'view';
  setValue: (name: string, value: any) => void;
  voucherData?: any;
}
export const useVoucherNumbering = ({ 
  apiEndpoint, 
  mode, 
  setValue, 
  voucherData 
}: UseVoucherNumberingProps) => {
  // Fetch next voucher number for create mode
  const { data: nextVoucherNumber, refetch: refetchNextNumber } = useQuery({
    queryKey: [`next${apiEndpoint}Number`],
    queryFn: () => api.get(`/${apiEndpoint}/next-number`).then(res => res.data),
    enabled: mode === 'create',
  });
  // Auto-set voucher number based on mode
  useEffect(() => {
    if (mode === 'create' && nextVoucherNumber) {
      setValue('voucher_number', nextVoucherNumber);
    } else if (voucherData && voucherData.voucher_number) {
      setValue('voucher_number', voucherData.voucher_number);
    }
  }, [mode, nextVoucherNumber, voucherData, setValue]);
  // Return refetch function for post-save increment
  const refreshVoucherNumber = async () => {
    if (mode === 'create') {
      const { data: newNextNumber } = await refetchNextNumber();
      setValue('voucher_number', newNextNumber);
      return newNextNumber;
    }
  };
  return {
    nextVoucherNumber,
    refreshVoucherNumber
  };
};
// Get API endpoint from voucher config
export const getVoucherApiEndpoint = (voucherType: string): string => {
  const endpointMap: Record<string, string> = {
    'quotation': 'quotations',
    'proforma-invoice': 'proforma-invoices',
    'sales-order': 'sales-orders',
    'sales-voucher': 'sales-vouchers',
    'delivery-challan': 'delivery-challans',
    'sales-return': 'sales-returns',
    'purchase-order': 'purchase-orders',
    'grn': 'goods-receipt-notes',
    'purchase-voucher': 'purchase-vouchers',
    'purchase-return': 'purchase-returns',
    'payment-voucher': 'payment-vouchers',
    'receipt-voucher': 'receipt-vouchers',
    'journal-voucher': 'journal-vouchers',
    'contra-voucher': 'contra-vouchers',
    'credit-note': 'credit-notes',
    'debit-note': 'debit-notes',
    'non-sales-credit-note': 'non-sales-credit-notes'
  };
  return endpointMap[voucherType] || voucherType;
};

