  cost_centers: CostCenter[];
  recent_kpis: KPI[];
}
interface TabPanelProps {
  children?: React.ReactNode;
  index: number;
  value: number;
}
function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`finance-tabpanel-${index}`}
      aria-labelledby={`finance-tab-${index}`}
      {...other}
    >
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}
const FinanceDashboard: React.FC = () => {
  const [data, setData] = useState<DashboardData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState(0);
  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/v1/finance/analytics/dashboard', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setData(response.data);
      setError(null);
    } catch (err: any) {
      setError(err.response?.data?.detail || 'Failed to fetch dashboard data');
    } finally {
      setLoading(false);
    }
  };
  useEffect(() => {
    fetchDashboardData();
  }, []);
  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setActiveTab(newValue);
  };
  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };
  const formatPercentage = (value: number) => {
    return `${value.toFixed(2)}%`;
  };
  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }
  if (error) {
    return (
      <Alert severity="error" action={
        <Button color="inherit" size="small" onClick={fetchDashboardData}>
          Retry
        </Button>
      }>
        {error}
      </Alert>
    );
  }
  if (!data) {return null;}
  // Chart data for cash flow
  const cashFlowChartData = {
    labels: ['Inflow', 'Outflow'],
    datasets: [
      {
        data: [data.cash_flow.inflow, data.cash_flow.outflow],
        backgroundColor: ['#4caf50', '#f44336'],
        borderWidth: 1
      }
    ]
  };
  // Chart data for cost center performance
  const costCenterChartData = {
    labels: data.cost_centers.map(cc => cc.name),
    datasets: [
      {
        label: 'Budget',
        data: data.cost_centers.map(cc => cc.budget),
        backgroundColor: '#2196f3',
        borderWidth: 1
      },
      {
        label: 'Actual',
        data: data.cost_centers.map(cc => cc.actual),
        backgroundColor: '#ff9800',
        borderWidth: 1
      }
    ]
  };
  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Box display="flex" justifyContent="between" alignItems="center" mb={3}>
        <Typography variant="h4" component="h1">
          Finance Dashboard
