          <IconButton onClick={fetchDashboardData} color="primary">
            <Refresh />
          </IconButton>
          <Button startIcon={<Download />} variant="outlined" sx={{ ml: 1 }}>
            Export
          </Button>
        </Box>
      </Box>
      {/* Key Metrics Cards */}
      <Grid container spacing={3} mb={3}>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Box display="flex" alignItems="center">
                <AccountBalance color="primary" sx={{ mr: 2 }} />
                <Box>
                  <Typography color="textSecondary" gutterBottom>
                    Total Assets
                  </Typography>
                  <Typography variant="h6">
                    {formatCurrency(data.financial_ratios.total_assets)}
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Box display="flex" alignItems="center">
                <Payment color="warning" sx={{ mr: 2 }} />
                <Box>
                  <Typography color="textSecondary" gutterBottom>
                    Total Liabilities
                  </Typography>
                  <Typography variant="h6">
                    {formatCurrency(data.financial_ratios.total_liabilities)}
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Box display="flex" alignItems="center">
                <TrendingUp color="success" sx={{ mr: 2 }} />
                <Box>
                  <Typography color="textSecondary" gutterBottom>
                    Working Capital
                  </Typography>
                  <Typography variant="h6">
                    {formatCurrency(data.financial_ratios.working_capital)}
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Box display="flex" alignItems="center">
                <Analytics color="info" sx={{ mr: 2 }} />
                <Box>
                  <Typography color="textSecondary" gutterBottom>
                    Current Ratio
                  </Typography>
                  <Typography variant="h6">
                    {data.financial_ratios.current_ratio.toFixed(2)}
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      {/* Tabs for detailed views */}
      <Paper sx={{ width: '100%' }}>
        <Tabs value={activeTab} onChange={handleTabChange} aria-label="finance dashboard tabs">
          <Tab label="Cash Flow" />
          <Tab label="Cost Centers" />
          <Tab label="KPIs" />
          <Tab label="Aging" />
        </Tabs>
        {/* Cash Flow Tab */}
        <TabPanel value={activeTab} index={0}>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    Cash Flow Overview
                  </Typography>
                  <Box sx={{ height: 300 }}>
                    <Doughnut data={cashFlowChartData} />
                  </Box>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
