                    </Box>
                    <Box display="flex" justifyContent="space-between" mb={1}>
                      <Typography>Outflow:</Typography>
                      <Typography color="error.main">
                        {formatCurrency(data.cash_flow.outflow)}
                      </Typography>
                    </Box>
                    <Box display="flex" justifyContent="space-between" mb={1}>
                      <Typography variant="h6">Net Flow:</Typography>
                      <Typography 
                        variant="h6" 
                        color={data.cash_flow.net_flow >= 0 ? 'success.main' : 'error.main'}
                      >
                        {formatCurrency(data.cash_flow.net_flow)}
                      </Typography>
                    </Box>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </TabPanel>
        {/* Cost Centers Tab */}
        <TabPanel value={activeTab} index={1}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Cost Center Performance
              </Typography>
              <Box sx={{ height: 400, mb: 3 }}>
                <Bar data={costCenterChartData} />
              </Box>
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Cost Center</TableCell>
                      <TableCell align="right">Budget</TableCell>
