"use client";
import React, { useState, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Grid,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Button,
  CircularProgress,
  Alert,
  Chip,
  Divider,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper
} from '@mui/material';
import {
  TrendingUp,
  TrendingDown,
  Analytics,
  AccountBalance,
  People,
  Inventory,
  Assessment,
  Download,
  Refresh
} from '@mui/icons-material';
import { useQuery } from '@tanstack/react-query';
import axios from 'axios';
interface DashboardData {
  period: string;
  date_range: {
    start_date: string;
    end_date: string;
  };
  revenue_metrics: {
    total_revenue: number;
    sales_count: number;
    average_sale_value: number;
  };
  cost_metrics: {
    total_costs: number;
    purchase_count: number;
    average_purchase_value: number;
  };
  profitability: {
    gross_profit: number;
    profit_margin: number;
  };
  customer_metrics: {
    total_active_customers: number;
    new_customers: number;
    customer_growth_rate: number;
  };
  inventory_metrics: {
    total_products: number;
    low_stock_items: number;
    stock_health_percentage: number;
  };
  cash_flow: {
    pending_receivables: number;
    pending_payables: number;
    net_outstanding: number;
  };
}
const ManagementDashboard: React.FC = () => {
  const [period, setPeriod] = useState<string>('month');
  const [refreshKey, setRefreshKey] = useState<number>(0);
  // Fetch dashboard data
  const { data: dashboardData, isLoading, error, refetch } = useQuery<DashboardData>({
    queryKey: ['management-dashboard', period, refreshKey],
    queryFn: async () => {
      const response = await axios.get(
        `/api/v1/management-reports/executive-dashboard?period=${period}`,
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      return response.data;
    },
    staleTime: 5 * 60 * 1000, // 5 minutes
  });
  const handlePeriodChange = (event: any) => {
    setPeriod(event.target.value);
  };
  const handleRefresh = () => {
    setRefreshKey(prev => prev + 1);
    refetch();
  };
  const handleExportExcel = async () => {
    try {
      const response = await axios.get(
        `/api/v1/management-reports/export/executive-dashboard?format=excel&period=${period}`,
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
          responseType: 'blob',
        }
      );
      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `executive_dashboard_${period}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Export failed:', error);
    }
  };
  const formatCurrency = (value: number): string => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(value);
  };
  const formatPercentage = (value: number): string => {
    return `${value.toFixed(1)}%`;
  };
  const MetricCard: React.FC<{
    title: string;
    value: string | number;
    icon: React.ReactNode;
    color: 'primary' | 'secondary' | 'success' | 'error' | 'warning' | 'info';
    subtitle?: string;
  }> = ({ title, value, icon, color, subtitle }) => (
    <Card sx={{ height: '100%' }}>
      <CardContent>
        <Box display="flex" alignItems="center" justifyContent="space-between">
          <Box>
            <Typography color="textSecondary" gutterBottom variant="body2">
              {title}
            </Typography>
            <Typography variant="h5" component="div">
              {value}
            </Typography>
            {subtitle && (
              <Typography variant="body2" color="textSecondary">
                {subtitle}
              </Typography>
            )}
          </Box>
          <Box color={`${color}.main`}>
            {icon}
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
  if (isLoading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }
  if (error) {
    return (
      <Alert severity="error">
        Failed to load management dashboard. Please check your permissions and try again.
      </Alert>
    );
  }
  if (!dashboardData) {
    return (
      <Alert severity="info">
        No dashboard data available for the selected period.
      </Alert>
    );
  }
  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      {/* Header */}
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
