                    </TableCell>
                    <TableCell>
                      <Box sx={{ display: 'flex', alignItems: 'center' }}>
                        <AccountTree sx={{ mr: 2, color: 'primary.main' }} />
                        <Typography variant="body1">
                          {account.account_name}
