                    </TableCell>
                    <TableCell>
                      <Typography variant="body1" fontWeight="medium">
                        {account.balance.toLocaleString()}
                      </Typography>
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={account.is_active ? 'Active' : 'Inactive'}
                        color={account.is_active ? 'success' : 'default'}
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      <IconButton size="small" color="primary" onClick={() => handleEditClick(account)}>
                        <Edit />
                      </IconButton>
                      <IconButton size="small" color="error" onClick={() => handleDeleteClick(account)}>
                        <Delete />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </TableContainer>
        {/* Add/Edit Account Dialog */}
        <Dialog 
          open={addDialog || editDialog} 
          onClose={() => { setAddDialog(false); setEditDialog(false); }}
          maxWidth="sm" 
          fullWidth
        >
          <DialogTitle>
            {selectedAccount ? 'Edit Account' : 'Add New Account'}
          </DialogTitle>
          <DialogContent>
            <Grid container spacing={2} sx={{ mt: 1 }}>
              <Grid item xs={12} sm={6}>
                <TextField
                  fullWidth
                  label="Account Code *"
                  value={formData.account_code}
                  onChange={(e) => setFormData(prev => ({ ...prev, account_code: e.target.value }))}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <InputLabel>Account Type</InputLabel>
                  <Select
                    value={formData.account_type}
                    label="Account Type"
                    onChange={(e) => setFormData(prev => ({ ...prev, account_type: e.target.value }))}
                  >
                    {accountTypes.map((type) => (
                      <MenuItem key={type.value} value={type.value}>
                        {type.label}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Account Name *"
                  value={formData.account_name}
                  onChange={(e) => setFormData(prev => ({ ...prev, account_name: e.target.value }))}
                />
              </Grid>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel>Parent Account</InputLabel>
                  <Select
                    value={formData.parent_account}
                    label="Parent Account"
                    onChange={(e) => setFormData(prev => ({ ...prev, parent_account: e.target.value }))}
                  >
                    <MenuItem value="">
                      <em>None (Top Level Account)</em>
                    </MenuItem>
                    {accounts
                      .filter(acc => acc.account_type === formData.account_type)
                      .map((account) => (
                        <MenuItem key={account.id} value={account.id}>
                          {account.account_code} - {account.account_name}
                        </MenuItem>
                      ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Description"
                  multiline
                  rows={3}
                  value={formData.description}
                  onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                />
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => { setAddDialog(false); setEditDialog(false); }}>
              Cancel
            </Button>
            <Button onClick={handleSubmit} variant="contained">
              {selectedAccount ? 'Update' : 'Create'}
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
};
export default ChartOfAccountsPage;

