              />
              <TextField
                fullWidth
                label="Reorder Level"
                type="number"
                value={formData.reorder_level}
                onChange={(e) => setFormData(prev => ({ ...prev, reorder_level: parseInt(e.target.value) }))}
                sx={{ mb: 2 }}
              />
              <TextField
                fullWidth
                label="Description"
