    queryFn: getProducts
  });
  // Fetch specific voucher
const { data: voucherData} = useQuery({
    queryKey: ['job-card-voucher', selectedId],
    queryFn: () => api.get(`/job-card-vouchers/${selectedId}`).then(res => res.data),
    enabled: !!selectedId
  });
