                                />
                              </TableCell>
                              <TableCell>
                                <Typography variant="body2">
                                  {((watch(`received_outputs.${index}.quantity_received`) || 0) * (watch(`received_outputs.${index}.unit_rate`) || 0)).toFixed(2)}
                                </Typography>
                              </TableCell>
                              <TableCell>
                                <Select
                                  size="small"
                                  value={watch(`received_outputs.${index}.quality_status`) || ''}
                                  onChange={(e) => setValue(`received_outputs.${index}.quality_status`, e.target.value)}
                                  disabled={mode === 'view'}
                                  sx={{ width: 100 }}
                                >
                                  {qualityStatusOptions.map((option) => (
                                    <MenuItem key={option.value} value={option.value}>
                                      {option.label}
                                    </MenuItem>
                                  ))}
                                </Select>
                              </TableCell>
                              <TableCell>
                                <TextField
                                  type="date"
                                  size="small"
                                  value={watch(`received_outputs.${index}.receipt_date`)}
                                  onChange={(e) => setValue(`received_outputs.${index}.receipt_date`, e.target.value)}
                                  disabled={mode === 'view'}
                                  sx={{ width: 120 }}
                                />
                              </TableCell>
                              {mode !== 'view' && (
                                <TableCell>
                                  <IconButton
                                    onClick={() => removeOutput(index)}
                                    size="small"
                                    color="error"
                                  >
                                    <Remove />
                                  </IconButton>
                                </TableCell>
                              )}
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  </TabPanel>
                </Box>
                {/* Notes  */}
                <Grid container spacing={2} mt={2}>
                  <Grid size={12}>
                    <TextField
                      label="Notes"
                      {...control.register('notes')}
                      fullWidth
                      multiline
                      rows={3}
                      disabled={mode === 'view'}
                    />
                  </Grid>
                </Grid>
                {/* Total Amount  */}
                <Box display="flex" justifyContent="flex-end" mt={2}>
                  <Typography variant="h6">
                    Net Job Work Value: {(watch('total_amount') || 0).toFixed(2)}
                  </Typography>
                </Box>
                {/* Action Buttons  */}
                {mode !== 'view' && (
                  <Box mt={3} display="flex" gap={2}>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      disabled={createMutation.isPending || updateMutation.isPending}
                      startIcon={<Save />}
                    >
                      {mode === 'edit' ? 'Update' : 'Create'} Voucher
                    </Button>
                    <Button
                      variant="outlined"
                      onClick={handleCancel}
                      startIcon={<Cancel />}
                    >
                      Cancel
                    </Button>
                  </Box>
                )}
              </form>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
}

