import api from '../../../lib/api';
import { getProducts } from '../../../services/masterService';
interface ManufacturingJournalFinishedProduct {
  product_id: number;
  quantity: number;
  unit: string;
  unit_cost: number;
  quality_grade?: string;
  batch_number?: string;
  lot_number?: string;
}
interface ManufacturingJournalMaterial {
  product_id: number;
  quantity_consumed: number;
