  unit: string;
  unit_value: number;
  batch_number?: string;
  condition?: string;
}
interface ManufacturingJournalVoucher {
  id?: number;
  voucher_number: string;
  date: string;
  manufacturing_order_id: number;
  bom_id: number;
  date_of_manufacture: string;
  shift?: string;
  operator?: string;
  supervisor?: string;
  machine_used?: string;
