  scrap_quantity: number;
  rework_quantity: number;
  byproduct_quantity: number;
  material_cost: number;
  labor_cost: number;
  overhead_cost: number;
  quality_grade?: string;
  quality_remarks?: string;
  narration?: string;
  notes?: string;
  status: string;
