// Dispatch Details Voucher Page
import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  Grid,
  IconButton,
  CircularProgress,
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Chip,
  Card,
  CardContent,
  CardActions,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  Tooltip,
  Divider
} from '@mui/material';
import {
  LocalShipping,
  Visibility,
  Edit,
  Search,
  FilterList,
  Track,
  Assignment,
  Person,
  Phone,
  LocationOn
} from '@mui/icons-material';
import { useQuery } from '@tanstack/react-query';
import VoucherLayout from '../../../components/VoucherLayout';
import { dispatchService } from '../../../services/dispatchService';
const DispatchDetailsPage: React.FC = () => {
  const [selectedDispatch, setSelectedDispatch] = useState<any>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  // Fetch dispatch orders
  const { data: dispatches = [], isLoading, error } = useQuery({
    queryKey: ['dispatch-orders', searchTerm, statusFilter],
    queryFn: () => dispatchService.getDispatchOrders({
      search: searchTerm || undefined,
      filter: statusFilter ? { status: statusFilter } : undefined
    })
  });
  const handleView = (dispatch: any) => {
    setSelectedDispatch(dispatch);
    setIsDialogOpen(true);
  };
  const handleDialogClose = () => {
    setIsDialogOpen(false);
    setSelectedDispatch(null);
  };
  const getStatusColor = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'pending': return 'warning';
      case 'in_transit': return 'info';
      case 'delivered': return 'success';
      case 'cancelled': return 'error';
      default: return 'default';
    }
  };
  const formatDate = (dateString: string) => {
    if (!dateString) {return '-';}
    return new Date(dateString).toLocaleDateString();
  };
  const formatDateTime = (dateString: string) => {
    if (!dateString) {return '-';}
    return new Date(dateString).toLocaleString();
  };
  return (
    <VoucherLayout
      title="Dispatch Details"
      description="View and track dispatch order details and delivery status"
    >
      <Container maxWidth="xl">
        {/* Header Actions */}
        <Box sx={{ mb: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
            <TextField
              size="small"
              placeholder="Search dispatch orders..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              InputProps={{
                startAdornment: <Search sx={{ mr: 1, color: 'action.active' }} />
              }}
            />
            <FormControl size="small" sx={{ minWidth: 150 }}>
              <InputLabel>Status</InputLabel>
              <Select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                label="Status"
              >
                <MenuItem value="">All</MenuItem>
                <MenuItem value="pending">Pending</MenuItem>
                <MenuItem value="in_transit">In Transit</MenuItem>
                <MenuItem value="delivered">Delivered</MenuItem>
                <MenuItem value="cancelled">Cancelled</MenuItem>
              </Select>
            </FormControl>
          </Box>
        </Box>
        {/* Error Alert */}
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            Failed to load dispatch orders
          </Alert>
