// Request for Quotation (RFQ) Page
import React, { useState, useEffect } from 'react';
import {
declare function handleDialogClose(...args: any[]): any;
  Box,
  Button,
  TextField,
  Typography,
  Grid,
  IconButton,
  CircularProgress,
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Autocomplete,
  Chip,
  Card,
  CardContent,
  CardActions,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  Fab,
  Tooltip
} from '@mui/material';
import {
  Add,
  Remove,
  Visibility,
  Edit,
  Delete,
  Send,
  Assignment,
  Search,
  FilterList,
  Download,
  Upload
} from '@mui/icons-material';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { toast } from 'react-toastify';
import VoucherLayout from '../../../components/VoucherLayout';
import { procurementService } from '../../../services/procurementService';
interface RFQItem {
  item_code: string;
  item_name: string;
  item_description: string;
  quantity: number;
  unit: string;
  specifications: any;
  expected_price: number;
}
interface RFQFormData {
  rfq_title: string;
  rfq_description: string;
  issue_date: string;
  submission_deadline: string;
  validity_period: number;
  terms_and_conditions: string;
  delivery_requirements: string;
  payment_terms: string;
  is_public: boolean;
  requires_samples: boolean;
  allow_partial_quotes: boolean;
  rfq_items: RFQItem[];
}
const RFQPage: React.FC = () => {
  const [mode, setMode] = useState<'create' | 'view' | 'edit'>('view');
  const [selectedRFQ, setSelectedRFQ] = useState<any>(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [formData, setFormData] = useState<RFQFormData>({
    rfq_title: '',
    rfq_description: '',
    issue_date: new Date().toISOString().split('T')[0],
    submission_deadline: '',
    validity_period: 30,
    terms_and_conditions: '',
    delivery_requirements: '',
    payment_terms: '',
    is_public: false,
    requires_samples: false,
    allow_partial_quotes: true,
    rfq_items: []
  });
  const queryClient = useQueryClient();
  // Fetch RFQs
  const { data: rfqs = [], isLoading, error } = useQuery({
    queryKey: ['rfqs', searchTerm, statusFilter],
    queryFn: () => procurementService.getRFQs({
      search: searchTerm || undefined,
      status: statusFilter || undefined
    })
  });
  // Create RFQ mutation
  const createRFQMutation = useMutation({
    mutationFn: procurementService.createRFQ,
    onSuccess: () => {
      toast.success('RFQ created successfully');
      queryClient.invalidateQueries({ queryKey: ['rfqs'] });
      handleDialogClose();
    },
    onError: (error: any) => {
      toast.error(error.message || 'Failed to create RFQ');
    }
  });
  // Update RFQ mutation
  const updateRFQMutation = useMutation({
    mutationFn: ({ id, data }: { id: number; data: any }) =>
      procurementService.updateRFQ(id, data),
    onSuccess: () => {
      toast.success('RFQ updated successfully');
      queryClient.invalidateQueries({ queryKey: ['rfqs'] });
      handleDialogClose();
    },
    onError: (error: any) => {
      toast.error(error.message || 'Failed to update RFQ');
    }
  });
  const handleDialogClose = () => {
    setIsDialogOpen(false);
    setSelectedRFQ(null);
    setMode('view');
    setFormData({
      rfq_title: '',
      rfq_description: '',
      issue_date: new Date().toISOString().split('T')[0],
      submission_deadline: '',
      validity_period: 30,
      terms_and_conditions: '',
      delivery_requirements: '',
      payment_terms: '',
      is_public: false,
      requires_samples: false,
      allow_partial_quotes: true,
      rfq_items: []
    });
  };
  const handleCreateNew = () => {
    setMode('create');
    setSelectedRFQ(null);
    setIsDialogOpen(true);
  };
  const handleEdit = (rfq: any) => {
    setMode('edit');
    setSelectedRFQ(rfq);
    setFormData({
