      rfq_items: rfq.rfq_items || []
    });
    setIsDialogOpen(true);
  };
  const handleSubmit = () => {
    if (mode === 'create') {
      createRFQMutation.mutate(formData);
    } else if (mode === 'edit' && selectedRFQ) {
      updateRFQMutation.mutate({ id: selectedRFQ.id, data: formData });
    }
  };
  const addRFQItem = () => {
