                          value={watch(`items.${index}.gst_rate`) || 18}
                          onChange={(_, value) => setValue(`items.${index}.gst_rate`, value || 18)}
                          renderInput={(params) => (
                            <TextField {...params} size="small" sx={{ width: 60 }} />
