                    <>
                      <Grid size={6}>
                        <Typography variant="body2" sx={{ textAlign: 'right', fontSize: 14 }}>
                          CGST:
                        </Typography>
                      </Grid>
                      <Grid size={6}>
                        <Typography variant="body2" sx={{ textAlign: 'right', fontSize: 14, fontWeight: 'bold' }}>
                          {(totalGst / 2).toLocaleString()}
                        </Typography>
                      </Grid>
                      <Grid size={6}>
                        <Typography variant="body2" sx={{ textAlign: 'right', fontSize: 14 }}>
                          SGST:
                        </Typography>
                      </Grid>
                      <Grid size={6}>
                        <Typography variant="body2" sx={{ textAlign: 'right', fontSize: 14, fontWeight: 'bold' }}>
                          {(totalGst / 2).toLocaleString()}
                        </Typography>
                      </Grid>
                    </>
                  ) : (
                    <>
                      <Grid size={6}>
                        <Typography variant="body2" sx={{ textAlign: 'right', fontSize: 14 }}>
                          IGST:
                        </Typography>
                      </Grid>
                      <Grid size={6}>
                        <Typography variant="body2" sx={{ textAlign: 'right', fontSize: 14, fontWeight: 'bold' }}>
                          {totalGst.toLocaleString()}
                        </Typography>
                      </Grid>
                    </>
                  )}
                  <Grid size={6}>
                    <Typography variant="h6" sx={{ textAlign: 'right', fontSize: 16, fontWeight: 'bold' }}>
                      Total:
                    </Typography>
                  </Grid>
                  <Grid size={6}>
                    <Typography variant="h6" sx={{ textAlign: 'right', fontSize: 16, fontWeight: 'bold' }}>
                      {totalAmount.toLocaleString()}
                    </Typography>
                  </Grid>
                </Grid>
              </Box>
            </Box>
          </Grid>
          {/* Amount in Words */}
          <Grid size={12}>
            <TextField
              fullWidth
              label="Amount in Words"
              value={getAmountInWords(totalAmount)}
              disabled
              InputLabelProps={{ shrink: true, style: { fontSize: 12 } }}
              inputProps={{ style: { fontSize: 14 } }}
              size="small"
            />
          </Grid>
        </Grid>
      </form>
    </Box>
  );
  if (isLoading) {
    return (
      <Container>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress />
        </Box>
      </Container>
    );
  }
  return (
    <>
      <VoucherLayout
        voucherType={config.voucherTitle}
        voucherTitle={config.voucherTitle}
        indexContent={indexContent}
        formContent={formContent}
        onShowAll={() => setShowVoucherListModal(true)}
        // pagination={paginationData ? {
        //   currentPage: currentPage,
        //   totalPages: paginationData.totalPages,
        //   onPageChange: handlePageChange,
        //   totalItems: paginationData.totalItems
        // } : undefined)
        centerAligned={true}
        modalContent={
          <VoucherListModal
            open={showVoucherListModal}
            onClose={() => setShowVoucherListModal(false)}
            voucherType="Purchase Orders"
            vouchers={sortedVouchers || []}
            onVoucherClick={handleVoucherClick}
            onEdit={handleEditWithData}
            onView={handleViewWithData}
            onDelete={handleDelete}
            onGeneratePDF={handleGeneratePDF}
            vendorList={vendorList}
          />
        }
      />
      {/* Modals */}
      <AddVendorModal 
        open={showAddVendorModal}
        onClose={() => setShowAddVendorModal(false)}
        onAdd={(newVendor) => {
          setValue('vendor_id', newVendor.id);
          refreshMasterData();
        }}
        loading={addVendorLoading}
        setLoading={setAddVendorLoading}
      />
      <AddProductModal 
        open={showAddProductModal}
        onClose={() => setShowAddProductModal(false)}
        onAdd={(newProduct) => {
          setValue(`items.${addingItemIndex}.product_id`, newProduct.id);
          setValue(`items.${addingItemIndex}.product_name`, newProduct.product_name);
          setValue(`items.${addingItemIndex}.unit_price`, newProduct.unit_price || 0);
          setValue(`items.${addingItemIndex}.original_unit_price`, newProduct.unit_price || 0);
          setValue(`items.${addingItemIndex}.gst_rate`, newProduct.gst_rate || 18);
          setValue(`items.${addingItemIndex}.unit`, newProduct.unit || '');
          setValue(`items.${addingItemIndex}.reorder_level`, newProduct.reorder_level || 0);
          refreshMasterData();
        }}
        loading={addProductLoading}
        setLoading={setAddProductLoading}
      />
      <AddShippingAddressModal 
        open={showShippingModal}
        onClose={() => setShowShippingModal(false)}
        loading={addShippingLoading}
        setLoading={setAddShippingLoading}
      />
      <VoucherContextMenu
        contextMenu={contextMenu}
        voucherType="Purchase Order"
        onClose={handleCloseContextMenu}
        onEdit={handleEditWithData}
        onView={handleViewWithData}
        onDelete={handleDelete}
        onPrint={handleGeneratePDF}
        onDuplicate={(id: number) => handleDuplicate(id)}
      />
    </>
  );
};
export default PurchaseOrderPage;

