                        </TableCell>
                        <TableCell sx={{ p: 1, textAlign: 'right' }}>
                          <TextField
                            type="number"
                            {...control.register(`items.${index}.unit_price`, { valueAsNumber: true })}
                            disabled={mode === 'view'}
                            size="small"
                            sx={{ width: 80 }}
                          />
                        </TableCell>
                        <TableCell sx={{ p: 1 }}>
                          <TextField
                            type="number"
                            {...control.register(`items.${index}.discount_percentage`, { valueAsNumber: true })}
                            disabled={mode === 'view'}
