                          ) : watch(`items.${index}.product_id`) ? (
                            <Typography variant="caption" color={getStockColor(watch(`items.${index}.current_stock`), watch(`items.${index}.reorder_level`))}>
                              {watch(`items.${index}.current_stock`)} {watch(`items.${index}.unit`)}
