    return {
      canView: true,
      canEdit: true,
      canDelete: voucher.status !== 'approved' && voucher.status !== 'confirmed',
      canPrint: true,
      canEmail: Boolean(recipient?.email),
      emailRecipient: recipient,
    };
  },
};

// frontend/src/types/rbac.types.ts
/**
 * Service CRM RBAC Types
 * 
 * TypeScript type definitions for Role-Based Access Control
 * in the Service CRM module.
 */
// Enums matching backend
export enum ServiceRoleType {
  ADMIN = 'admin',
  MANAGER = 'manager',
  SUPPORT = 'support',
  VIEWER = 'viewer'
}
export enum ServiceModule {
  SERVICE = 'service',
  TECHNICIAN = 'technician',
  APPOINTMENT = 'appointment',
  CUSTOMER_SERVICE = 'customer_service',
  WORK_ORDER = 'work_order',
  SERVICE_REPORTS = 'service_reports',
  CRM_ADMIN = 'crm_admin'
}
export enum ServiceAction {
  CREATE = 'create',
  READ = 'read',
  UPDATE = 'update',
  DELETE = 'delete',
  EXPORT = 'export',
  ADMIN = 'admin'
}
// Service Permission Types
export interface ServicePermission {
  id: number;
  name: string;
  display_name: string;
  description?: string;
  module: ServiceModule;
  action: ServiceAction;
  is_active: boolean;
  created_at: string;
  updated_at?: string;
}
export interface ServicePermissionCreate {
  name: string;
  display_name: string;
  description?: string;
  module: ServiceModule;
  action: ServiceAction;
  is_active?: boolean;
}
export interface ServicePermissionUpdate {
  display_name?: string;
  description?: string;
  is_active?: boolean;
}
// Service Role Types
export interface ServiceRole {
  id: number;
  organization_id: number;
  name: ServiceRoleType;
  display_name: string;
  description?: string;
  is_active: boolean;
  created_at: string;
  updated_at?: string;
}
export interface ServiceRoleWithPermissions extends ServiceRole {
  permissions: ServicePermission[];
}
export interface ServiceRoleCreate {
  name: ServiceRoleType;
  display_name: string;
  description?: string;
  organization_id: number;
  permission_ids?: number[];
  is_active?: boolean;
}
export interface ServiceRoleUpdate {
  display_name?: string;
  description?: string;
  is_active?: boolean;
  permission_ids?: number[];
}
// User Role Assignment Types
export interface UserServiceRole {
  id: number;
  user_id: number;
  role_id: number;
  assigned_by_id?: number;
  is_active: boolean;
  created_at: string;
  updated_at?: string;
}
export interface UserServiceRoleCreate {
  user_id: number;
  role_id: number;
  assigned_by_id?: number;
  is_active?: boolean;
}
export interface UserWithServiceRoles {
  id: number;
  email: string;
  full_name?: string;
  role: string; // Regular user role
  is_active: boolean;
  service_roles: ServiceRole[];
}
// Role Assignment Request/Response Types
export interface RoleAssignmentRequest {
  user_id: number;
