// Revised: v1/frontend/src/utils/apiUtils.ts
import axios from 'axios';
const api = axios.create({
  baseURL: process.env.NEXT_PUBLIC_API_URL || 'http://localhost:8000/api/v1',
  headers: {
    'Content-Type': 'application/json',
  },
});
// Add token interceptor
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});
// Add error handling interceptor
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Handle unauthorized - redirect to login
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);
export const handleApiError = (error: any): string => {
  if (error.response) {
    return error.response.data?.message || error.response.data?.detail || 'An error occurred';
  } else if (error.request) {
    return 'No response received from server';
  } else {
    return error.message || 'Unknown error';
  }
};
export const getApiParams = (params: any): URLSearchParams => {
  const searchParams = new URLSearchParams();
  Object.entries(params).forEach(([key, value]) => {
    if (value !== undefined && value !== null) {
      searchParams.append(key, String(value));
    }
  });
  return searchParams;
};
export const uploadStockBulk = async (file: File): Promise<any> => {
  try {
    const formData = new FormData();
    formData.append('file', file); // Ensure field name matches backend expectation ('file')
    const response = await api.post('/stock/bulk', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  } catch (error) {
    throw new Error(handleApiError(error));
  }
};
export default api;

import { toast } from 'react-toastify';
interface ApiError {
  status?: number;
  userMessage?: string;
  response?: {
    data?: {
      detail?: string;
      message?: string;
    };
  };
}
export const handleApiError = (error: ApiError, defaultMessage?: string) => {
  let message = defaultMessage || 'An error occurred';
  // Check for user-friendly message first
  if (error.userMessage) {
    message = error.userMessage;
  } else if (error.response?.data?.detail) {
    message = error.response.data.detail;
  } else if (error.response?.data?.message) {
    message = error.response.data.message;
  }
  // Handle specific status codes with enhanced messaging
  if (error.status === 412) {
    // Precondition failed - usually company setup required
    const companySetupMessage = message.includes('company') || message.includes('Company') 
      ? message 
      : 'Company setup required before performing this operation.';
    toast.error(companySetupMessage, {
      autoClose: 8000,
      toastId: 'company-setup-required'
    });
    return;
  }
  if (error.status === 404) {
    const notFoundMessage = message.includes('company') || message.includes('Company')
      ? message
      : message || 'Resource not found';
    toast.error(notFoundMessage, {
      autoClose: 5000,
    });
    return;
  }
  if (error.status === 403) {
    toast.error(message || 'Access denied', {
      autoClose: 5000,
    });
    return;
  }
  if (error.status === 400) {
    const badRequestMessage = message.includes('organization') || message.includes('company')
      ? message
      : message || 'Invalid request';
    toast.error(badRequestMessage, {
      autoClose: 6000,
    });
    return;
  }
  // Generic error
  toast.error(message, {
    autoClose: 5000,
  });
};
export const showCompanySetupRequiredMessage = () => {
  toast.warning('Please complete your company setup before accessing this feature.', {
    autoClose: 8000,
    toastId: 'company-setup-required-action'
  });
};

// src/utils/nameRefUtils.ts
import { useQuery } from '@tanstack/react-query';
import api from '../lib/api';
import { useEntityOptions } from '../hooks/useEntity';
import { EntityType } from '../types/entity.types';
/**
 * Legacy hook for name options - now uses Entity abstraction
 * @deprecated Use useEntityOptions from useEntity hook instead
 */
export const useNameOptions = () => {
  const { options } = useEntityOptions(['Vendor', 'Customer']);
  // Convert to legacy format for backward compatibility
  return options.map(option => ({
    ...option.originalData,
    type: option.type
  }));
};
/**
 * Enhanced reference options with Entity support
 */
export const useReferenceOptions = (
  selectedEntityId: number | null, 
  selectedEntityType: EntityType | null
) => {
  const { data: unpaidVouchers } = useQuery({
    queryKey: ['unpaid-vouchers', selectedEntityId, selectedEntityType],
    queryFn: () => {
      if (!selectedEntityId || !selectedEntityType) {return [];}
      // Map entity types to voucher endpoints
      const endpoint = selectedEntityType === 'Vendor' ? '/purchase-vouchers' : 
                     selectedEntityType === 'Customer' ? '/sales-vouchers' : null;
      if (!endpoint) {return [];}
      const params = selectedEntityType === 'Vendor' 
        ? { vendor_id: selectedEntityId }
        : { customer_id: selectedEntityId };
      return api.get(endpoint, { params }).then(res => res.data);
    },
    enabled: !!selectedEntityId && !!selectedEntityType,
  });
  return [
    ...(unpaidVouchers || []).map(v => v.voucher_number),
    'Advance',
    'On Account'
  ];
};

